INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (1, N' Introduction to Media Design and Production I', N'1120', N'CMAF', N'An introduction to fundamental concepts, methods and strategies used to create specific meaning, emotional impact and consumer behaviour through both the analysis and creation of messages. In-class workshops and experiential learning exercises provide students with foundational skills in story development, media treatment, scriptwriting, and production budgets. Students will research various media/delivery channels and potential demographics. (Also offered as CNMA-1120)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (2, N' Introduction to Media Design and Production II', N'1130', N'CMAF', N'Building on skills learned in CMAF-1120, this course offers an introduction to the fundamentals of film and video production. In-class workshops and experiential learning exercises provide students with skills in production planning and audio-visual design including cinematography, lighting, sound recording, editing and digital content deliverables. Combining both studio and field based learning, students will scriptwrite, shoot and edit basic projects. (2 lecture hours and 1 laboratory hour per week) (Prerequisite: CNMA-1120/CMAF-1120). (Also offered as CNMA-1130)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (3, N' Introduction to Film Studies', N'1400', N'CMAF', N'Cinematic appreciation is studied through one or more of the following methods: an examination of great films, specific actors, auteurs, film genres or movements. Films may be critically studied within their cultural, historical, political and socio-economic context. (2 lecture hours and 2 screening hours per week).', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (4, N' New Media Studies', N'2010', N'CMAF', N'This course introduces students to theories of new media, explores the historical emergence of digital media forms and examines their social, cultural, political and economic implications. Topics may include: “old” and “new” media, convergence, political economy of new media, the digital divide, social networking, participatory cultures and Web 2.0 (e.g. Facebook, Twitter, YouTube) (Prerequisite: CMAF-1010)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (5, N' New Media and Social Movements', N'2030', N'CMAF', N'This course charts the history and contemporary manifestations of the role of new media technologies in the formation of social movements. A variety of activists, advocacy groups and social movements have used new/social media technologies and platforms to coordinate and organize their activities, to intervene in the public sphere and to document, share and shape their own narratives. Topics may include: mainstream media frames and social movements, social control and mass-mediated social movements, social media and political organizing, user-generated content, and Web 2.0. (Prerequisite: CMAF-1010).', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (6, N' Fundamentals of Sound Technology', N'2090', N'CMAF', N'This course introduces students to the principles of sound technology and message design appropriate for a variety of contexts. Students will apply relevant design, vocal and sound theory in conceptualizing and producing effective sound messages using contemporary technologies and software. (Prerequisites: CMAF-1010 and CNMA-1130/CMAF-1130) (2 lecture hours and 1 laboratory hour per week) (Also offered as CNMA-2090)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (7, N' Speech Communication to Inform', N'2100', N'CMAF', N'A beginning course designed to help the student to develop poise and confidence in communicating information. ( 2 lecture hours and one 1 laboratory hour per week.) (Not available on an Audit basis.) (Prerequisite: CMAF-1010) (Also offered as DRAM-2100)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (8, N' Podcasting and Internet Media', N'2130', N'CMAF', N'This course examines experimental processes in film, video and sound and provides a technical and critical foundation in each medium. Focus will be given to the basic events and artists central to the historical development of this genre as well as its contemporary practitioners. Based on screenings and various exercises, students will craft projects that explore the potential of these artistic forms in challenging the norms and conventions of mainstream cultural production. (Prerequisites: CMAF-1010 and CNMA-1130/CMAF-1130) (Also offered as CNMA-2150) (2 lecture hours and 1 laboratory hour per week)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (9, N'CMAF-2200 Screenwriting Fundamentals', N'2200', N'CMAF', N'This course introduces students to the craft of screenwriting with an emphasis on core concepts such as structure, character development, plot, theme, tone and dialogue. Through a variety of writing exercises and assignments students will develop the skills necessary to translate visual and story ideas into written format. (Prerequisites: CMAF-1010 and CNMA-1130/CMAF-1130) (Also offered as CNMA-2200) (2 lecture hours and 1 laboratory hour per week).', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (10, N' The Art of Photo-blogging', N'2210', N'CMAF', N'In this course students will develop and design photography-based blog projects that combine effective research and writing skills with the creative use of image capture as a tool for visual communication. Students will learn photographic techniques and image editing software as well as communication strategies, applicable Canadian copyright laws, web analytics and search engine optimization. (Prerequisites: CMAF-1010 and CNMA-1130/CMAF-1130) (2 lecture hours and 1 laboratory hour per week)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (11, N' Media Literacy', N'2250', N'CMAF', N'A critical exploration of how the media contribute to the social construction of reality. Students will develop the skills and conceptual frameworks necessary to interpret and investigate the contemporary media environment with a particular focus on examples derived from Canadian informational/news sources and popular culture. Topics may include: media coverage of social and political issues, political economy of media/culture industries, media and democracy, media representation and stereotypes. (Prerequisite: CMAF-1010)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (12, N' Film Production', N'2310', N'CMAF', N'In this project-based course, students pitch project ideas that are then selected based on merit and work in teams to research, write, shoot and edit productions targeted to specific external screening venues. A variety of techniques are explored appropriate for fiction, non-fiction, experimental, etc., genres. (Prerequisites: CMAF-1010 and CNMA-1130/CMAF-1130.)(2 lecture hours and 1 laboratory hour per week) (Also offered as CNMA-2310) (Credit may not be obtained for both CNMA-2310/CMAF-2310 and CMAF-2190)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (13, N' Documentary', N'2320', N'CMAF', N'In this project-based course, students will work in groups to write proposals, scripts and storyboards while also familiarizing themselves with the terminology, aesthetics, mechanics and equipment associated with producing, lighting, shooting and editing for studio and location-based documentary projects. (Prerequisites: CMAF-1010 and CNMA-1130/CMAF-1130.)(1.5 lecture hours and 1.5 laboratory hours per week) (Also offered as CNMA-2320) (Credit may not be obtained for both CMAF-2180 and CNMA-2320/CMAF-2320.)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (14, N' Research Methods in Communication', N'2340', N'CMAF', N'An introductory overview of research approaches, methods, and designs in communication studies. Students will learn about the theoretical grounding of quantitative, qualitative, and interpretive methods, and practice various methods to explore communication issues. (Prerequisites: CMAF-1010) (2 lecture hours and 1 tutorial hour per week.)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (15, N' Cinema History I (Pre-War)', N'2400', N'CMAF', N'The course charts the early history of the cinema from its inception to World War II: film shorts at the turn of the century, the silent film era, the introduction of sound, and the decline of the studio system. Films are examined as technical, industrial, commercial, artistic, and, most importantly, as historical artifacts. Industry, audience, and the development of cinematic language are viewed within an international framework and their local cultural context. (Prerequisite: CMAF-1010).', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (16, N' Cinema History II (Post-War)', N'2410', N'CMAF', N'The course examines films from the post-War period to the present: the heyday of the classical Hollywood narrative and challenges to its dominance from European neo-realism and the avant-garde film movement are considered. Films are viewed as influenced by and reflective of social upheaval of the sixties, as well as their consolidation within distinct but mutually influencing categories of mainstream and alternative cinema. An important consideration is how films can either paper over or expose social fractures along the lines of gender, race, sexuality, and nationalism.(Prerequisite: CMAF-1010)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (17, N' Media Aesthetics', N'2430', N'CMAF', N'The course provides a basic set of principles and tools to understand the formal qualities of visual signification and the broader contours of visual culture. Students learn aesthetic and technical terms, rules, conventions, and social assumptions used to construct meaning through sound, images, or graphics in stills, film, television and the web. The course offers a grounding useful for both producers and consumers of visual images.(Prerequisite: CMAF-1010).', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (18, N' Introductory Photography', N'2530', N'CMAF', N'This introductory course in film and chemical photographic processes provides an opportunity for students to explore techniques and concepts within the medium of photography. Students will learn the basic technical skills of operating cameras, processing film, and making black and white prints, through a series of concerns specific to photography. (Prerequisite: CMAF-1010 and Communication, Media and Film Major; students must have a 35mm adjustable film camera to complete this course.) (Also offered as Visual Arts VSAR-2530.) (Lab fees may apply.)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (19, N' Communication and Cultural Policy in Canada', N'2450', N'CMAF', N'This course surveys the historical development of communication and cultural policy in the Canadian context. Students will investigate particular culture industries (e.g. music, film, television) and key themes (nation-state, public sphere, globalization, media convergence) that have informed policy debates as well as the structure, performance and regulation of culture/media industries. Topics may include: the role of the State in cultural production, national culture, citizenship, identity and multiculturalism, representations of ‘Canada’ in the popular imagination. (Prerequisite: CMAF-1010.)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (20, N' Public Relations, Media and Society', N'2610', N'CMAF', N'This course examines the historical and contemporary role and influence of the public relations industry on media discourses and the shaping of public opinion. Students will critically explore the emergence of the PR industry; the art of "spin" and the engineering of consent; corporate and government PR; and the symbiotic relationship between PR and informational media. (Prerequisites: CMAF-1010)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (21, N' Speaking Truth to Power: Voice and Activism', N'2700', N'CMAF', N'An examination of contemporary struggles for social change with a particular focus on anti-consumerist and environmental justice campaigns. Students learn to create persuasive social justice messages. (Prerequisite: Semester 3 or above standing.) (Also offered as DRAM-2700, SJST-2700, and WORK-2700)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (22, N' Theories of Communication and Media', N'2750', N'CMAF', N'This course introduces students to various theorists and schools of thought that have shaped the discipline of communication/media studies within the Canadian context, traces the development of theoretical approaches to communication forms and processes and explores a variety of underlying philosophical perspectives and assumptions in communication and media theory. (Prerequisite: CMAF-1010.) (2 lecture hours and 1 tutorial hour per week.)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (23, N' Advertising in Historical and Cultural Context', N'2820', N'CMAF', N'This course contextualizes advertising and branding within the history of capitalism and contemporary consumer culture. The course draws upon approaches from Marxism, sociology, feminism, and other critical perspectives. Topics may include: the historical rise of consumer culture, advertising’s reciprocal and structural relationship with media, controversial advertising categories, issues of representation and meaning and the ideological dimensions of advertising and branding. (Prerequisites: CMAF-1010).', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (24, N' Digital Technologies and Everyday Life', N'3010', N'CMAF', N'This course surveys critical theories of technology with a focus on how evolving and emerging communication/digital technologies are received and adapted and how they shape practices in various institutional contexts and in everyday life. Topics may include: representations of technology, technologies and the organization/perception of space and time, privacy/surveillance, gender, labour, the environment and technology. (Prerequisite: CMAF-2750.)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (25, N' Popular Culture', N'3020', N'CMAF', N'Examines the relationship between popular culture and questions of economics and social and cultural politics, through an exploration of struggles over knowledge, power and authority manifest in popular cultural artifacts and processes. Intended to provide students with tools for critical evaluation of contemporary popular culture, including the constitution of social ideologies, values and representations through cultural artifacts. (Prerequisite: CMAF-2750.)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (26, N' Privacy, Surveillance and Security in the Digital Age', N'3040', N'CMAF', N'This course provides an historical examination of the conceptual apparatuses that have traditionally framed understandings of the right to privacy, critically assesses the capacities of the State and corporate entities to monitor digital activities and explores the social, political and economic implications of surveillance practices. Topics may include: user-generated surveillance, mobile technologies, cloud computing, geo-locating technologies, tracking software, and data mining in social media contexts. (Prerequisite: CMAF-2010)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (27, N' Field and Studio Sound Recording', N'3090', N'CMAF', N'An overview of the theoretical and practical aspects of audio including frequency range, khz, bits and file formats in accordance with industry standards. Students will learn advanced techniques and principles of sound mixing for various production contexts. (Prerequisite: CNMA-2090/CMAF-2090 and CNMA/CMAF-2320) (Also offered as CNMA-3090) (2 lecture hours and 1 laboratory hour per week) (Credit may not be obtained for both CNMA-3090/CMAF-3090 and CMAF-3180)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (28, N' Cinematography', N'3100', N'CMAF', N'This course focuses on cinematic visuals using various cameras as tools. Students will explore the theory and craft of motion picture cinematography. This course will provide students with the opportunity to shoot several film assignments, engage in critical discussion, conduct peer reviews of each other’s work, and screen their productions. (Prerequisites: CNMA-1010 and CNMA-2320/CMAF-2320) (Also offered as CNMA-3100).', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (29, N' Studio and Location Lighting for Film and Video', N'3140', N'CMAF', N'This course examines the aesthetics, techniques and technology of lighting and its role in storytelling and meaning-making processes. Through hands-on exercises with lighting instruments and systems, students will develop skills in professional and creative applications of lighting in both location and studio environments. (Prerequisites: CNMA-2320/CMAF-2320, CNMA-2320/CMAF-2320) (Also offered as CNMA-3140) (2 lecture hours and 1 laboratory hour per week)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (30, N' Intermediate Screenwriting', N'3200', N'CMAF', N'This course builds on skills acquired in CNMA-2200. Students will write scripts that will be work shopped throughout the semester in order to develop and refine writing and storytelling skills. Writing assignments, discussion, peer review, pitch presentations and analysis of texts via screenings and readings will inform the students’ work and understanding of the form. (Prerequisite: CNMA/CMAF-2320 and CNMA-2200/CMAF-2200) (Also offered as CNMA-3200).', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (31, N' Production Planning and Development', N'3230', N'CMAF', N'This course provides a foundation in pre-production and planning processes including the development of location agreements, production schedules and budgets. Students will also learn how to assemble representative portfolios of their creative work, advance their pitching skills, write effective grant proposals and identify funding sources for independent film/video projects in the Canadian context. (Prerequisite: CNMA-2320/CMAF-2320) (Also offered as CNMA-3230).', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (32, N' Digital Video Editing and Post-Production', N'3270', N'CMAF', N'This course provides an examination of the historical, aesthetic and theoretical aspects of editing and post-production. Through screenings, workshops, praxis-based pedagogical approaches and group assignments, students will explore various editing styles and acquire advanced editing skills in accordance with industry standard software. (Prerequisite: CNMA/CMAF-2320) (2 lecture hours and 1 laboratory hour per week) (Also offered as CNMA-3270)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (33, N' Motion Picture Technologies', N'3300', N'CMAF', N'An in-depth study of techniques for creating 3D film production, special effects, aerial cinematography and robotic cinema imagery. Students will expand their creative visual approach and technical practice in field and film studios. (Prerequisite: CNMA/CMAF-2320) (2 lecture hours and 1 laboratory hour pera week). (Also offered as CNMA-3300).', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (34, N' Intermediate Film Production', N'3310', N'CMAF', N'This course continues the creative approach to and technical practice of independent film production initiated in CNMA-2310/CMAF-2310. Students will expand their experience and knowledge of theoretical and practical filmmaking in both field and studio settings. (Prerequisite: CNMA/CMAF-23200 (Also offered as CNMA-3310) (2 lecture hours and 1 laboratory hour per week)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (35, N' Intermediate Documentary', N'3320', N'CMAF', N'This course examines theories, techniques and styles of modern documentary as well as other non-fiction forms such as the photo-essay. Students may investigate different sub-genres of the documentary approach and create projects that explore specific personal, social and/or community/campus related issues. Readings and in-class screenings will focus on the theory and practice of documentary for social change. (Also offered as CNMA-3320) (1.5 lecture hours and 1.5 laboratory hours per week) (Credit may not be obtained for both CMAF-3320 and 40-319).', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (36, N' Methods of Mass Media Criticism', N'3340', N'CMAF', N'In this course, students examine mass/popular cultural texts (e.g. news, TV shows, films, music videos, advertisements, social media, etc.,) and explore representational politics using a variety of contemporary methodological approaches. These may include: content analysis, cultural studies, discourse/textual analysis, semiotics, genre study, feminist criticism, audience research, on-line ethnography, web-based inquiry. (Prerequisites: CMAF-2340 or CMAF-2750)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (37, N' Cinema and/in Culture', N'3430', N'CMAF', N'This intermediate course on cinema focuses on shifting topics that underscore contemporary approaches to studying the culture and politics of cinema as a medium. Topics may include: film auteurs, film practice modes, genres, movements, national cinemas, representational politics of race, gender and sexuality; spectatorship/reception, star system, transnational productions/flows. (Prerequisites: CMAF-2400 or CMAF-2410)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (38, N' Public Relations Today: Issues and Practices', N'3610', N'CMAF', N'This course explores the contemporary world of public relations. Through case studies and in-class exercises, students will assess a variety of strategies and techniques and apply them in the planning and execution of public relations campaigns deliverable across a variety of platforms. Topics may include: PR in the social media age; media relations, journalism and PR; PR and ethical considerations. (Prerequisite: CMAF-2610)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (39, N' Media, Technology and the Environment', N'3640', N'CMAF', N'This course explores the relationship between media practices, representations, communication technologies and the environment. Topics may include: media constructions of the environment; mainstream and alternative media coverage of environmental movements and issues; environmental impact of communication practices, technological advancements and consumer culture; environmental themes in advertisements, corporate greenwashing. (Prerequisite: CMAF-1010 and CMAF-2750)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (40, N' Alternative Media and Digital Activism', N'3700', N'CMAF', N'This course examines existing theory and scholMACSip on alternative media and media activism and explores the ways in which activists and citizen groups use/have used “old” as well as new media and emerging technologies to challenge mainstream media narratives and express alternative views on a range of social and political issues. Special emphasis will be placed on the Canadian context. Topics may include: mainstream versus alternative media framing; historical roots of Canadian alternative media; media reform movements; participatory journalism; culture jamming; the tactics, strategies, aesthetics and goals of alternative/activist media. (Prerequisite: One of CMAF-2010, CMAF-2250, CMAF-2700, DRAM-2700 (Dramatic Art) or WORK-2700 (Labour Studies).)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (41, N' Critical Approaches to Media and Culture', N'3750', N'CMAF', N'This course explores contemporary theories and methods related to the critical study of media and culture including Marxian and neo-Marxian political economy, the Frankfurt School, Gramscian hegemony theory, structuralism, semiotics, cultural studies, social constructionism, postmodernism, poststructuralism, and feminism. Topics may include: political economy of media and the culture industries; the production, consumption, and circulation of cultural texts and artefacts; the materialities of communication and the politics and practices of representation. (Prerequisite: One of CMAF-2000, CMAF-2010 or CMAF-2750.)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (42, N' Advertising in the New Media Era: Contemporary Issues and Practices', N'3820', N'CMAF', N'This course considers how “new media” formations including Internet, streaming, mobile, social, and user-generated media have altered the advertising industry and advertising practices. Topics may include: advertising’s structural influence over new media technologies and platforms, new media audiences/users/influencers, guerrilla marketing, advertising strategy, regulation and policy. Students will learn advertising skills and techniques required to propose, plan, and execute campaigns in the new/social media environment. (Pre-requisite: CMAF-2820).', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (43, N'CMAF-3830 Children and the Mediated Marketplace', N'3830', N'CMAF', N'This course offers a critical approach to the study of children’s media, advertising, and consumer culture. Topics may include: issues of ideology and representation in children’s media and consumer culture, the social construction of childhood by market forces, and the impact of internet and mobile technology on the evolving nature of childhood. (Prerequisite: CMAF-1010 and CMAF-2250)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (44, N'CMAF-3900 Special Topics in Media and Society', N'3900', N'CMAF', N'This Special Topics course will explore contemporary issues relevant to understanding the links between media institutions, cultural texts/forms and social practices. The specific focus will vary based on faculty expertise (Prerequisite: CMAF-2750 and at least semester 4 standing). (May be repeated for credit if the topics differ.) (Prerequisites: CMAF-2750 and at least Semester 4 standing.)(May be repeated for credit if the topics differ.)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (45, N' Advanced Topics in New Media and Digital Culture', N'4010', N'CMAF', N'This seminar provides an in-depth exploration of the diverse social, economic, political, cultural and artistic practices that constitute the contemporary new media landscape. Through an examination of web 2.0 technologies/digital platforms and their formations, structures, limits and possibilities, students will be engaged as both content consumers and producers. Topics may include: social networking as immaterial labour, art in the age of digital reproduction, cyber-identity/community, new media and public policy, digitally-mediated activism/social movements. (Prerequisite: CMAF-3010 or CMAF-3040)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (46, N'CMAF-4150 Advanced Studio and Location Lighting for Film and Video', N'4150', N'CMAF', N'This course builds on skills acquired in CMAF-3140 and will deepen student''s technical, conceptual and aesthetic knowledge while working with light in creative projects. Students will explore a variety of strategies to evoke specific moods/themes and plan and execute aesthetically strong and technically advanced lighting schemes for controlled studio environments and diverse interior and exterior location sites. (Prerequisite: CNMA-3140/CMAF-3140 or permission of instructor) (Also offered as CNMA-4150) (2 lecture hours and 1 laboratory hour per week)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (47, N' Advanced Television Production', N'4210', N'CMAF', N'This course integrates advanced principles and practices of television studio production. Students will research, plan, write scripts, shoot and edit segments about the campus community and the region for broadcast on local cable television. This course may be repeated once for credit with permission of the instructor and the Undergraduate Advisor/Department Head. (2 lecture and 1 laboratory hour per week) (Prerequisite as of Fall 2020: One of CNMA-2320/CMAF-2320, CNMA-3320/CMAF-3320, or CNMA-3270/CMAF-3270, or permission of instructor) (2 lecture and 1 laboratory hour per week)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (48, N' Advertising/Marketing Campaign Production', N'4230', N'CMAF', N'This advanced course integrates knowledge and skills acquired in previous theory and production courses. Working in groups, students will obtain hands-on experience in the production of advertising/marketing campaigns for various delivery formats, including social media. The class will focus on graphic design programs, script writing, the digital editing of video, stills and audio, audience research and ethical standards in advertising. (Prerequisites: CMAF-3310 and one of: CMAF-3230 or CMAF-3320 or CMAF-3820) (2 lecture hours and 1 laboratory hours per week.)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (49, N' Advanced Non-Fiction Media Production', N'4240', N'CMAF', N'This course integrates theories and practices of digital media with an emphasis on professional standards and skills. Students will plan, write, shoot, edit and compress news, documentary, and other short form non-fiction projects. This course may be repeated once for credit with permission of the instructor. (Prerequisite: One of 40-219, CNMA-2310/CMAF-2310, CNMA-3270/CMAF-3270, CNMA-3310/CMAF-3310 or CNMA-3310/CMAF-3320 or permission of instructor; (2 lecture hours and 1 laboratory hour per week.)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (50, N' Advanced Studies in the Sociology of News Media', N'4250', N'CMAF', N'This seminar course explores and investigates the role played by mass media in power relations and the social construction of reality from a critical political economy perspective. Topics may include: the political economy of mainstream media, including issues of media ownership and control; the intersections of media, corporate and governmental power; mainstream media coverage/representation of domestic and foreign affairs. (Prerequisite: CMAF-2250 or permission of the instructor)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (51, N' Advanced Editing and Post-Production', N'4270', N'CMAF', N'This course explores various technical, aesthetic and ethical issues involved in editing, color grading, 16mm and 35mm HD Telecine, special effects and post production workflow. Through readings, screenings, hands-on exercises and projects, students will gain in-depth knowledge about the impact of post-production aural/visual effects and manipulation in editing processes. (Prerequisites: CMAF-3270 or CNMA-3310/CMAF-3310.) (2 lecture hours and 1 laboratory hour per week) (Also offered as CNMA-4270)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (52, N' Senior Project', N'4280', N'CMAF', N'In this course, students may produce content across a range of new media platforms. Emphasis will be on demonstrating professional capabilities in the areas of critical thinking, proposal writing, project development, creation and distribution. The course synthesizes both theoretical and practical learning acquired throughout the program and offers students the opportunity to further advance their production portfolio. This course may be repeated once for credit with permission of the instructor. (Prerequisites: CNMA-3100/CMAF-3100, CNMA-3270/CMAF-3270, CNMA-3310/CMAF-3310 or CNMA-3320/CMAF-3320 or permission of instructor).', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (53, N' Advanced Documentary', N'4320', N'CMAF', N'A continuation of CNMA-3320/CMAF-3320, this course will further explore issues and concepts that apply to documentary film. Students will analyze the form and create work within a context of artistic experimentation and theoretical consideration. Students will further develop their abilities to reflect upon, critique, discuss and express the creative strategies and theoretical meanings that motivate their own work on topics of local interest that engage and impact local communities, particularly on campus. (Prerequisite: CNMA-3320/CMAF-3320). (Also offered as CNMA-4320).', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (54, N' Film Theory and Criticism', N'4430', N'CMAF', N'This seminar course examines the changing theoretical and critical approaches to film, including issues in the production and reception of film, such as realism, adaptation, convention, signification, and culture. (Prerequisites: CMAF-2400 and CMAF-2410)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (55, N' Border Culture', N'4500', N'CMAF', N'This course addresses the role of borders in contemporary global culture as both physical boundaries and affective conditions. In the context of the Windsor-Detroit border, students from the University of Windsor will exchange viewpoints based upon the experience of living in a border culture. Seminars and field trips will take up the topic of borders from a number of perspectives and contexts. Students will look at historical and contemporary ideas about borders that have been articulated in various disciplines: from political theory and cultural geography, to urban planning, art, literature, architectural and spatial theory. (Open to majors and non-majors.) (Prerequisites: CMAF-1010 or MACS-1500 and semester 4 standing.) (Also offered as MACS-4500)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (56, N' Communication Perspectives and Aboriginal People, Race and Ethnicity', N'4620', N'CMAF', N'Explores theoretical and practical communication issues of race and ethnicity and links these issues to the practice of social justice. Topics include: historical and critical implications of identity politics, media (mis-) representation, cultural policy, First Nations, multicultural and multiracial media production. (Prerequisites: CMAF-2250 or CMAF-2450, and Semester 5 standing.) (Sociology majors: SACR-3330 and two courses in Communication, Media, and Film.)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (57, N' Gender and Technology', N'4630', N'CMAF', N'This advanced seminar addresses issues related to gendered experiences with technology in the digital age through an examination of various theoretical debates and case studies. Topics may include: the historical gendering of technological skills; the social construction of technology and masculinity; impact of technology on the environment; critiques of techno-science; gendered representation of, and participation in, video games; gendered experiences of mobile phone and social media use. (Prerequisite CMAF-3010 or CMAF-3640)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (58, N' Canadian Communication Thought', N'4760', N'CMAF', N'The course examines a range of thinkers and artists who have contributed to the various branches of communication and media studies in Canada, including media history and criticism, political economy, cultural studies, philosophies of technology, and media and digital arts. Commonalities and differences between first and second-generation Canadian theorists and artists will be discussed in relationship to theories and practices emanating from the United States and overseas. (Prerequisite: at least Semester 6 standing.)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (59, N' Selected Topics in Media Production', N'4890', N'CMAF', N'An advanced exploration of selected topics related to production processes across a variety of media platforms. For individual projects (available only to four-year Honours students in Communication, Media and Film or four-year Honours students in Combined Programs with Communication, Media and Film), proposals must be submitted on appropriate forms and approved, prior to registration, by the Undergraduate Advisor and Head in Communication, Media and Film. (Prerequisites: CMAF-2310 and CMAF-2320 and Semester 5 standing ) (Normally, CMAF-4890 or CMAF-4950 may be taken no more than a total of two times combined.)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (60, N' Selected Topics in Communication/Media Studies', N'4900', N'CMAF', N'An advanced seminar that explores selected topics in the field of Communication/Media Studies. Topics and prerequisites may vary depending on the focus of the course. (Prerequisites: One of CMAF-2750, CMAF-3020, CMAF-3340, CMAF-3750 or permission of instructor.) (May be repeated for credit if the topics are different). (May be repeated for credit if the topics are different).', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (61, N' Directed Reading', N'4950', N'CMAF', N'Intended for students with special interest in areas not covered in sufficient depth by other courses. (To be taken only with permission of instructor and Department Head or delegate in Communication, Media and Film) (Normally, CMAF-4890 or CMAF-4950 may be taken no more than a total of two times combined.)', 3, 1, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (62, N' The Nature of Theatre I', N'1000', N'DRAM', N'Introduction to the Process of Theatre. Two of the following topics will be covered: the analysis of the play script; the mechanics of performance; the principles of direction; and the theories of design/technical theatre. Nature of Theatre is a two-part sequence, required for majors in all School of Dramatic Art programs. A laboratory assignment supporting the production schedule of University Players is required for either DRAM-1000 or DRAM-2000. Three critical writing assignments are required for the term in which a laboratory is not taken. This course must be successfully completed in the first year of the program. (Laboratory hours by arrangement).', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (63, N' Theatre in Contemporary Culture', N'1110', N'DRAM', N'An examination of factors and principles involved in an audience''s appreciation of theatre. (No prerequisite.) (Open to non-Dramatic Art majors.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (64, N' Drawing for the Theatre', N'1170', N'DRAM', N'Introductory course confronting challenges in drawing for the theatre. Areas covered will include common and innovative materials, elementary drafting, perspective, contour drawing and shading, and their computer enhancement. (Restricted to Dramatic Art majors.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (65, N' Rendering for the Theatre', N'1180', N'DRAM', N'Introductory course confronting challenges in creating colourized renderings for the theatre. Areas covered will include common and innovative materials, colour theory and psychology, script and character analysis, textural creation, sheen, reflections, and their computer enhancement. (Pre-requisite: DRAM-1170) (Restricted to Dramatic Art majors.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (66, N' Voice for the Actor I', N'1200', N'DRAM', N'An introduction to the study and practice of voice and speech for the theatre. .(co-requisites: DRAM-1260, DRAM-1280.) (Restricted to BFA Acting students only.) (Laboratory hours by arrangement.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (67, N' Voice for the Actor II', N'1210', N'DRAM', N'Continuation of DRAM-1200. The study and practice of voice and speech for the theatre (Restricted to BFA Acting students only.) (Prerequisite: DRAM-1200.).(co-requisites: DRAM-1270, DRAM-1290.) (Laboratory hours by arrangement.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (68, N' Movement for the Actor I', N'1260', N'DRAM', N'An introduction to the study and practice of movement for the actor. (Co-requisites: DRAM-1200, DRAM-1280.) (Restricted to BFA Acting students only.) (Laboratory hours by arrangement.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (69, N' Movement for the Actor II', N'1270', N'DRAM', N'Continuation of DRAM-1260. The study and practice of movement for the actor. (Prerequisite: DRAM-1260.) (Co-requisites: DRAM-1200, DRAM-1280.) (Restricted to BFA Acting students only.) (Laboratory hours by arrangement.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (70, N' Improvisation and Introduction to Acting for the Theatre I', N'1280', N'DRAM', N'An introduction to the study and practice of acting with an emphasis on the basic elements of improvisation. (Co-requisites: DRAM-1200, DRAM-1260.) (Restricted to BFA Acting students only.) (Laboratory hours by arrangement.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (71, N'Improvisation and Introduction to Acting for the Theatre II', N'1290', N'DRAM', N'Continuation of DRAM-1280. The study and practice of basic elements of acting and improvisation (Prerequisite: DRAM-1280.) (Co-requisites: DRAM-1200, DRAM-1260.) (Restricted to BFA Acting students only.) (Laboratory hours by arrangement.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (72, N'Theatre History I', N'1300', N'DRAM', N'Critical approaches to the main elements of Greek and Roman theatre, medieval western and Asian theatre, Italian, Spanish, and English Renaissance Theatre. (Open to non-Dramatic Art majors.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (73, N'Introduction to Drama in Education and Community I', N'1600', N'DRAM', N'An introduction to the principles, theories and applications of Drama in Education and Community with an emphasis on creativity, storytelling, and the developmental aspects of play. (Restricted to Drama in Education and Community Majors, or consent of instructor.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (74, N'Introduction to Drama in Education and Community II', N'1610', N'DRAM', N'A continuing study of Drama in Education and Community. (Prerequisite: DRAM-1600 or consent of instructor.) (Restricted to Drama in Education and Community Majors, or consent of instructor.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (75, N' The Nature of Theatre II', N'2000', N'DRAM', N'Continuation of DRAM-1000. Two topics not covered in DRAM-1000 will be covered in DRAM-2000. Nature of Theatre is a two-part sequence, required for majors in all School of Dramatic Art programs. A laboratory assignment supporting the production schedule of University Players is required for either DRAM-1000 or DRAM-2000. Three critical writing assignments are required for the term in which a laboratory is not taken. This course must be successfully completed in the first year of the program. (Laboratory hours by arrangement).', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (76, N' Speech Communication to Inform', N'2100', N'DRAM', N'A beginning course designed to help the student to develop poise and confidence in communicating information. (Two lecture and one lab hours per week.) (Not available on an Audit basis.) (Also offered as CMAF-2100).', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (77, N'Scenic Design I', N'2110', N'DRAM', N'Introduction to the technical aspects of design for the stage; communicating the design idea through basic drafting techniques. (Prerequisite: DRAM-1180, or consent of instructor.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (78, N'Scene Painting for the Theatre', N'2130', N'DRAM', N'Laboratory and demonstration course with an emphasis on materials, texturing techniques, and the creation of three-dimensional effects.', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (79, N' Computer Technology for the Theatre', N'2140', N'DRAM', N'This course is an introduction to the use of computers for the theatre. Content and approaches will vary according to the nature of software programs. This course may be taken more than once for credit provided the content is significantly varied. (Open to Non-Drama Majors with consent of the instructor.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (80, N'Lighting Design I', N'2150', N'DRAM', N'The introduction and application of basic principles, including electricity, instruments, and design theory. (Laboratory hours by arrangement.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (81, N' Costume Design', N'2170', N'DRAM', N'Introduction to the principles of costume design for the stage; character analysis; and costume rendering. (Prerequisite: DRAM-1170 and DRAM-1180 or consent of instructor).', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (82, N' Gender, Movement and Performance', N'2190', N'DRAM', N'This course explores how people come to know themselves and others through their bodies. Students investigate movement as a cultural message and explore how stereotypes connected to gender, race, sexuality, and ability are created and contested through the body. Prerequisite: WGST-1000 or DRAM-1000 or with consent of the instructor. (Cross-listed as a Social Science course in Women and Gender Studies, WGST-2190)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (83, N'Voice for the Actor III', N'2200', N'DRAM', N'Continuation of DRAM-1210. The study and practice of voice and speech for the theatre. (Prerequisite: DRAM-1210.) (Co-requisites: DRAM-2230, DRAM-2260, DRAM-2280.) (Restricted to BFA Acting students only.) (Laboratory hours by arrangement.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (84, N'Voice for the Actor IV', N'2210', N'DRAM', N'Continuation of DRAM-2200. The study and practice of voice and speech for the theatre. (Prerequisite: DRAM-2200.)(Co-requisites: DRAM-2230, DRAM-2260, DRAM-2280.) (Restricted to BFA Acting students only.) (Laboratory hours by arrangement.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (85, N'Acting for the Theatre I', N'2230', N'DRAM', N'Continuation of DRAM-1290. The emphasis will be on acting exercises, script analysis and scene work. (Prerequisite: DRAM-1290.) (Co-requisites: DRAM-2200, DRAM-2260, DRAM-2280.) (Restricted to BFA Acting students only.) (May be taken twice with the consent of instructor if assignments or their treatments are significantly varied.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (86, N' Acting for the Theatre II', N'2240', N'DRAM', N'Continuation of DRAM-2230. The emphasis will be on the analysis and performance of scenes from plays. (Prerequisite: DRAM-2230.)(Co-requisites: DRAM-2200, DRAM-2260, DRAM-2280.) (Restricted to BFA Acting students only.) (May be taken twice with the consent of instructor if assignments or their treatments are significantly varied.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (87, N' Introductory Acting I', N'2250', N'DRAM', N'A practical study of the fundamentals of acting experienced through acting exercises. (Not open to BFA Acting students.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (88, N'Movement for the Actor III', N'2260', N'DRAM', N'Continuation of DRAM-1270. The study and practice of movement for the actor. (Prerequisite: DRAM-1270.) (Co-requisites: DRAM-2200, DRAM-2230, DRAM-2280.) (Restricted to BFA Acting students only.) (Laboratory hours by arrangement.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (89, N'Movement for the Actor IV', N'2270', N'DRAM', N'Continuation of DRAM-2260. The study and practice of movement for the actor. (Prerequisite: DRAM-2260.)(Co-requisites: DRAM-2200, DRAM-2230, DRAM-2280.) (Restricted to BFA Acting students only.) (Laboratory hours by arrangement.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (90, N'Psychophysical Acting Techniques', N'2280', N'DRAM', N'Exercises in improvisation designed to support the rehearsal process for scripted works (Prerequisite: DRAM-1290). (Co-requisites: DRAM-2200, DRAM-2230, DRAM-2260.).) (Restricted to BFA Acting majors only.) (Laboratory hours by arrangement.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (91, N' Dance for the Theatre', N'2290', N'DRAM', N'A practical course in a variety of dance styles for the theatre.(Not open to BFA Acting Majors except by consent of instructor.) (Laboratory hours by arrangement.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (92, N' Theatre History II', N'2300', N'DRAM', N'Critical approaches to the main elements of theatre of the seventeenth, eighteenth, and nineteenth centuries. (Open to non-Dramatic Art majors.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (93, N' Introductory Acting II', N'2350', N'DRAM', N'Emphasis is on acting exercises based on script analysis. (Prerequisite: DRAM-2250, or consent of instructor.) (Not open to BFA Acting students.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (94, N' Directing I', N'2440', N'DRAM', N'An examination of the principles of play direction, including the role of the director, choosing plays, casting, analysis, picturization and composition, rehearsal techniques, and scene presentation. (Prerequisite: DRAM-2250, or consent of instructor.) (Laboratory hours by arrangement.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (95, N' Stage Makeup', N'2500', N'DRAM', N'An introduction to the theory and practice of theatrical make-up application. Topics covered will include basic stage make-up, corrective make-up and aging. (Restricted to Drama majors only.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (96, N' Drama in the Classroom: Principles and Theories', N'2600', N'DRAM', N'A course in the use of drama as it relates to the school curriculum and the community. (Prerequisite: DRAM-1610.) (Restricted to Drama in Education and Community students only.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (97, N' Drama in the Classroom: Applications I', N'2610', N'DRAM', N'Applications of drama as they relate to the curriculum for Grades K through Six. Completion of a classroom contact is required. (Field contacts are with the consent of Drama in Education and Community instructors and may be outside class time.) (Prerequisite: DRAM-2600 or consent of instructor.) (Restricted to Drama in Education and Community students only.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (98, N' Drama and Community: Principles and Theories', N'2670', N'DRAM', N'A practical introduction to principles, theories, and applications of drama and theatre techniques to promote and maintain health and well-being for individuals and communities. Focus will be on developing skills in leadership and facilitation in a wide range of community settings. (Prerequisite: DRAM-1610 or consent of instructor.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (99, N' Speaking Truth to Power: Voice and Activism', N'2700', N'DRAM', N'An examination of contemporary struggles for social change with a particular focus on anti-consumerist and environmental justice campaigns. Students learn to create persuasive social justice messages. (Prerequisite: Semester 3 or above standing.) (Also offered as WORK-2700, SJST-2700, and CMAF-2700)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (100, N' The Creative Process', N'2720', N'DRAM', N'An introduction to the creative process through an exploration of various theories, principles, and techniques of selected theorists and innovative thinkers. Students may explore such topics as: Creative Problem Solving, Idea Generation, and Innovation. Through academic study and practical work, students will explore ways of developing their own creative potential and applying it to their daily lives. (Not open to 1st year students.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (101, N' Principles and Practices of Arts Management I', N'2750', N'DRAM', N'Structures and functions of arts organizations. Examination of core competencies as applied to arts organizations: marketing and publicity, financial management, various forms of fund-raising, board and volunteer management, and interaction with Unions and Associations.', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (102, N' Principles and Practices of Arts Management II', N'2760', N'DRAM', N'A continuation of the study of Arts Management I. An exploration of the concepts and practices of organizational design, human resources, leadership, legal concerns, strategic planning, Board governance, policy making and advocacy relating to management of the not for profit and non-profit Arts sectors in Canada today. (Prerequisite: DRAM-2750)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (103, N' Improvisation and Self Development', N'2770', N'DRAM', N'Involves the development of performance skills through a practical application of theatre games and basic elements of improvisation with emphasis on self-development. (Prerequisite: DRAM-1000 and DRAM-2000; or DRAM-1610 or consent of instructor.) (Individual course sections may be restricted to Drama in Education and Community students only.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (104, N' Improvisation: Working with Text', N'2780', N'DRAM', N'A continuation of the study of improvisation. Involves practical applications of improvisation to improvised scenes and written texts. (Prerequisite: DRAM-2770 or consent of instructor.) (Individual course sections may be restricted to Drama in Education and Community students only.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (105, N' Creative Movement and Voice I', N'2840', N'DRAM', N'The study and practice of movement and voice as they relate to the individual, theatre, and the classroom. Emphasis may be on one or both disciplines as necessary. (Prerequisite: DRAM-1000 or consent of instructor.) (Laboratory hours by arrangement.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (106, N' Play Creation', N'3100', N'DRAM', N'A study of the principles and techniques involved in play creation, culminating in a final staged reading or performance. Modules may include playwriting, devising, adaptation, dramaturgy, and translation. (Prerequisite:DRAM-1000 and DRAM-2000 or by permission of the instructor.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (107, N' Scenic Design II', N'3110', N'DRAM', N'Problems related to the designing of a multi-set production; communicating the design idea through the use of drafting, models, and colour renderings. (Prerequisite: DRAM-2110, or consent of instructor.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (108, N' Stage Management', N'3150', N'DRAM', N'A study of the role of the theatrical stage manager. Particular emphasis will be given to rehearsal organization and communication, cue notation, crew supervision, safety issues, Canadian Actors'' Equity guidelines, and managing the production while in technical/dress rehearsals and performance. (Prerequisite: DRAM-2000, or consent of the instructor.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (109, N' Costume Design II', N'3170', N'DRAM', N'Continuation of DRAM-2170, involving theoretical and practical design projects; formal presentation of costume designs. (Prerequisite: DRAM-2170, or consent of the instructor.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (110, N' Studies in Design', N'3190', N'DRAM', N'Designed for the advanced student who wishes to explore further studies in scenic, lighting, or costume design. (Prerequisite: consent of a faculty advisor.) (May be repeated for credit if assignments or their treatments are significantly varied.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (111, N' Acting-Work in Progress I', N'3210', N'DRAM', N'Rehearsal project in a performance style selected by Dramatic Art. The play or scenes from plays become the catalyst for the discovery of the style related to its period. (Prerequisite: DRAM-2240.)(Co-requisites: DRAM-2200, DRAM-2230, DRAM-2260.) (Restricted to BFA Acting students only.) (May be repeated for credit if assignments or their treatments are significantly varied.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (112, N' Acting-Work in Progress II', N'3220', N'DRAM', N'Rehearsal project in a performance style selected by Dramatic Art. The play or scenes from plays become the catalyst for the discovery of the style related to its period. (Prerequisite: DRAM-2240.)(Co-requisites: DRAM-3260 or DRAM-3270, if offered.) (Restricted to BFA Acting students only.) (May be repeated for credit if assignments or their treatments are significantly varied.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (113, N' Acting-Work in Progress III', N'3230', N'DRAM', N'Rehearsal project in a performance style selected by Dramatic Art. The play or scenes from plays become the catalyst for the discovery of the style related to its period. (Prerequisite: DRAM-2240.) (Co-requisites: DRAM-3260 or DRAM-3270, if offered.) (Restricted to BFA Acting students only.) (May be repeated for credit if assignments or their treatments are significantly varied.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (114, N' Performing Shakespeare', N'3240', N'DRAM', N'A course in the acquisition of the skills required to interpret and perform Shakespearean text through the rehearsal and public performance of a play or scenes from a Shakespearean play or plays. (Prerequisite: DRAM-2240) (Corequisite: DRAM-3260 or DRAM-3270, if offered) (Restricted to BFA. students only.) (May be repeated for credit if assignments or their treatments are significantly varied.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (115, N' Intermediate Acting III', N'3250', N'DRAM', N'Emphasis is on the development of situation and characterization. The period to be studied may vary from year to year. (Prerequisite: DRAM-2350 or consent of instructor.) (Not open to BFA Acting students.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (116, N' Voice and Movement for the Actor I', N'3260', N'DRAM', N'Application of voice and movement techniques as they relate to performance assignments. Emphasis may be on one or both disciplines as necessary. (Prerequisite: DRAM-2270.) (Co-requisites: DRAM-3210 and DRAM-3220 or DRAM-3230 and DRAM-3240.) (May be taken twice for credit.) (Restricted to BFAActing students only.) (Laboratory hours by arrangement.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (117, N' Voice and Movement for the Actor II', N'3270', N'DRAM', N'Advanced application of voice and movement techniques as they relate to performance assignments. Emphasis may be on one or both disciplines as necessary. (Prerequisite: DRAM-2210.) (Co-requisites: DRAM-3210 and DRAM-3220 or DRAM-3230 and DRAM-3240.) (Restricted to BFA Acting students only.) (Laboratory hours by arrangement.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (118, N' Canadian Theatre History', N'3330', N'DRAM', N'A study of the evolution of theatre in Canada. (Open to non-Dramatic Art majors.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (119, N' Theatre from the Twentieth Century to the Present Day', N'3350', N'DRAM', N'Critical approaches to major theatrical movements and experiments in theatre during the twentieth and twenty-first centuries. (Open to non-Dramatic Art majors.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (120, N' Directing II', N'3440', N'DRAM', N'A practical course involving rehearsal techniques and the presentation of scenes or one-act plays. (Prerequisite: DRAM-2230 and DRAM-2240; or DRAM-2440 or consent of instructor.) (Laboratory hours by arrangement.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (121, N' Production Problems', N'3510', N'DRAM', N'Advanced explorations of current trends, materials, or practices in performance or non-performance, with an emphasis on problem solving techniques. The student will research and develop solutions to one or more production problems and present his/her findings in a seminar or performance situation. (Prerequisite: A minimum of two courses and/or equivalent experience in the subject area(s) explored and, consent of a faculty advisor and Director of the School.) (Hours by arrangement.) (May be repeated for credit if assignments or their treatments are significantly varied.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (122, N' Production Problems', N'3520', N'DRAM', N'Advanced explorations of current trends, materials, or practices in performance or non-performance, with an emphasis on problem solving techniques. The student will research and develop solutions to one or more production problems and present his/her findings in a seminar or performance situation. (Prerequisite: A minimum of two courses and/or equivalent experience in the subject area(s) explored and, consent of a faculty advisor and the Director of the School.) (May be repeated for credit if assignments or their treatments are significantly varied.) (Hours by arrangement.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (123, N' Production Problems: Stage Management', N'3530', N'DRAM', N'A theoretical and practical course examining the duties and responsibilities of the theatrical stage manager, including rehearsal procedures, protocol and etiquette, assembling the production script, cuing methods and notations, and security/safety regulations. (Prerequisite: DRAM-3150 plus previous experience as an assistant stage manager and consent of a faculty advisor.) (May be repeated for credit if assignments or their treatments are significantly varied.) (Laboratory hours by arrangement.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (124, N' Drama in the Classroom: Applications II', N'3600', N'DRAM', N'Applications of drama as they relate to the curriculum for Grades Seven through Twelve. Completion of a classroom contact is required. (Field contacts are with the consent of Drama in Education and Community instructors and may be outside class time.) (Prerequisite: DRAM-2610 or consent of instructor.) (Restricted to Drama in Education and Community students only.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (125, N' Literacy in Action', N'3710', N'DRAM', N'A course in the use of drama as it relates to the development of literacy in classroom and community settings. (Prerequisite: DRAM-2610.) (Restricted to Drama in Education and Community students only.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (126, N' Theatre for Social Action', N'3780', N'DRAM', N'A study of the theory, practice, and development of popular, community, and collective theatre. Students will examine various approaches to theatre for social action through a combination of academic study and practical exercises in specific popular theatre techniques, including Theatre of the Oppressed and Playback Theatre. (Open only to 3rd and 4th year Drama in Education and Community students or by consent of Instructor.) (May be repeated for credit twice if assignments or approaches are significantly different.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (127, N' Creative Movement and Voice II', N'3840', N'DRAM', N'Continuation of DRAM-2840. The study and practice of movement and voice as they relate to the individual, theatre, and the classroom. Emphasis may be on one or both disciplines as necessary. (Prerequisite: DRAM-2840 or consent of instructor.) (Laboratory hours by arrangement.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (128, N' Internship I: Arts Management Certificate', N'3980', N'DRAM', N'Completion of field placement in an arts organization. (Prerequisitie: DRAM-2750 with a minimum grade of 70%.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (129, N' Internship II: Arts Management Certificate', N'3990', N'DRAM', N'Completion of second field placement in an arts organization involving in-depth analysis of the organization.(Prerequisitie: DRAM-2750 and DRAM-3980 with a minimum grade of 70%.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (130, N' Style in Theatre', N'4000', N'DRAM', N'An exploration of selected theatrical styles. Topics and materials may vary from year to year. (Students may use this course number to register for the Stratford Summer Campus. Contact Dramatic Art for further information.) (May be repeated for credit twice if assignments or approaches are significantly varied.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (131, N' Graduation Recital', N'4200', N'DRAM', N'The selection, preparation, and presentation of suitable audition material in a recital situation. An introduction to the business of being a professional actor will be addressed. (Prerequisite: All required first, second, and third year BFA performance courses; restricted to fourth-year BFA Acting students only.) (Laboratory hours by arrangement.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (132, N' Television Performance', N'4210', N'DRAM', N'Introduction to basic television performance techniques. Each student performs behind and in front of the camera although the emphasis is on performance. (Prerequisite: DRAM-2250 or DRAM-2350, or DRAM-3230 and DRAM-3240. or by consent of instructor. Restricted to third- and fourth-year Dramatic Art and Communication Studies students in the Combined Dramatic Arts and Communication Studies program, and third and four year Dramatic Art students.) (May be taken for credit twice.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (133, N' Character Study', N'4290', N'DRAM', N'Independent work in researching, developing, and presenting character projects based on life studies. (Restricted to Fourth year BFA Acting students only.) (May be taken for credit twice with consent of instructor if assignments or their treatments are significantly varied.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (134, N' Directed Studies in History, Theory, or Theatre Administration', N'4390', N'DRAM', N'Designed for the advanced student who wishes to explore a special area of history, theory, or theatre administration with a faculty advisor. (Prerequisite: consent of a faculty advisor.) (May be taken for credit twice if assignments or their treatments are significantly varied.) (Hours by arrangement.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (135, N' Directed Studies in Direction', N'4490', N'DRAM', N'Designed for the advanced student who wishes to explore a special area of direction with a faculty advisor. (Prerequisite: consent of the faculty advisor.) (May be taken for credit twice.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (136, N' Advanced Shakespeare Performance', N'4510', N'DRAM', N'An extension and intensification of 24-324 Acting Work in Progress IV Performing Shakespeare. An advanced course in the acquisition of the skills required to interpret and perform Shakespearean text through the rehearsal and public performance of a play or scenes from a Shakespearean play or plays.(Co-requisites: 24-420 and 24-429, if offered)(Restricted to B.F.A. acting students only.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (137, N' Directed Studies in Production', N'4520', N'DRAM', N'A series of theatrical play production situations in which the advanced student will research, develop, and execute a major technical/design assignment under the supervision of a faculty advisor. (May be repeated for credit if assignments or approaches are significantly varied.) (Prerequisite: consent of a faculty advisor.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (138, N' Directed Studies in Performance', N'4530', N'DRAM', N'The emphasis in these courses will be a series of plays selected and produced by Dramatic Art or by a theatre company outside of the University as approved by Dramatic Art. Students will research, develop, and execute a role in the performance situation under the supervision of a faculty advisor-director. (May be taken for credit twice.) (Prerequisites: All required first, second, and third year BFA performance courses: DRAM-1200, DRAM-1210, DRAM-1260, DRAM-1270, DRAM-1280, DRAM-1290, DRAM-2200, DRAM-2210, DRAM-2230, DRAM-2240, DRAM-2260, DRAM-2270, DRAM-2280, DRAM-3210, DRAM-3220, DRAM-3230, DRAM-3240, DRAM-3260, and DRAM-3270.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (139, N' Directed Studies in Cross-Cultural Theatre', N'4690', N'DRAM', N'Designed for the advanced student who wishes to explore a special area of theatre of a unique culture with a faculty advisor. (Prerequisite: previous experience/courses in the subject area(s) explored and consent of a faculty advisor.) (Restricted to students in Semester 5 and above.) (May be taken for credit twice.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (140, N' Theatre for Young Audiences', N'4700', N'DRAM', N'Research and practical work in the history, principles, and techniques of theatrical performance by, with, and for young audiences. Students may explore international perspectives on topics such as: Theatre in Education, Children''s Theatre, Theatre for Youth, Collective Creation, and Devised Theatre. (Prerequisites: DRAM-2770, DRAM-2840, DRAM-2250.) (May be repeated for credit twice if assignments or approaches are significantly varied.)', 3, 2, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (141, N' Introduction to Economics I', N'1100', N'ECON', N'An introduction to microeconomics intended to provide students with the tools necessary to begin to understand and evaluate how resources are allocated in a market economy. Specific topics include how markets function, theories of the business firm, of consumer behaviour and of income distribution. The economic roles of labour unions and government are also covered. The theories are applied to contemporary Canadian economic problems.', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (142, N' Introduction to Economics II', N'1110', N'ECON', N'This course is an introduction to macroeconomics. The emphasis is upon measuring and explaining what determines economic aggregates such as the total national product (GDP) and the level of prices and employment. The role of money and financial institutions, the impact of international trade and the policy options available to governments for coping with inflation and unemployment are discussed in detail.', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (143, N' Life Choices and Economics', N'2000', N'ECON', N'The course is designed for Arts and Social Sciences students. It will introduce them to key concepts and methods in Microeconomics. The application and understanding of economic analysis as applied to individual decision-making and public policy will be emphasized. The course provides a non-technical and intuitive way for students to master an understanding of real world problems. (May not be taken for credit in any program within the School of Business, or Faculty of Engineering. Science students may take the course only as a Social Sciences option.) (Antirequisite: ECON-1100.)', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (144, N' Life Choices and Economics II', N'2010', N'ECON', N'The course is designed for Arts and Social Sciences students. It will introduce them to key concepts and methods in Macroeconomics. Key Macroeconomic concepts, such as unemployment, inflation, international trade, and investment will be examined. The course will be a non-technical look at the Canadian and world economies. (May not be taken for credit in any program within the School of Business, or Faculty of Engineering. Science students may take the course only as a Social Sciences option.) (Antirequisite: ECON-1110.)', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (145, N' Games and Behaviour', N'2100', N'ECON', N'The course is designed for Arts, Social Science and Business students. It is intended to introduce them to key concepts and methods in game theory. The application and understanding of behavioral analysis as applied to individual decision making and public policy will be emphasized. The course provides a non-technical and intuitive way for students to master an understanding of real world problems and decision making. Students will learn about strategies for conflict resolutions, co-operation, social interaction, voting strategies, individual and business behaviour. (May not be taken for credit in Economics or joint programs with Economics, Science or the Faculty of Engineering.)', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (146, N' Intermediate Statistical Methods', N'2120', N'ECON', N'An application of statistical methods to economic theory. (Prerequisite: one of SOSC-2500, STAT-2920, STAT-2910, or MSCI-2020.) (Credit will not be given for more than one of ECON-2120, STAT-2950, or any equivalent intermediate statistics course from another area.)', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (147, N' Intermediate Microeconomics I', N'2210', N'ECON', N'The theory of markets, the theory of consumer behaviour and demand; the firm, production, cost, and supply. (Prerequisite: ECON-1100.)', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (148, N' Intermediate Microeconomics II', N'2220', N'ECON', N'Extensions of the theory of consumer and firm behaviour; pricing under different market structures; distribution; general equilibrium and economic welfare. (Prerequisite: ECON-2210.)', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (149, N' Intermediate Macroeconomics I', N'2310', N'ECON', N'A theoretical and policy oriented treatment of the determination of employment, output, interest rates, and the price level; stabilization policies and their effectiveness. (Prerequisites: ECON-1110.)', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (150, N' Intermediate Macroeconomics II', N'2320', N'ECON', N'Effectiveness of stabilization policies in open economies; causes and cures of inflation; simple growth models. (Prerequisite: ECON-2310.)', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (151, N' Microeconomics for the Real World', N'2410', N'ECON', N'This course will focus on the application of techniques of economics to the analysis of practical problems in a variety of fields (public health, natural resources, political science, industrial relations, business administration, and others). The course will emphasize more applied, as opposed to theoretical, aspects of microeconomics. (May not be taken for credit in Economics programs or Combined Major Programs with Economics.) (Prerequisites: ECON-1100 or ECON-2000) (Antirequisite: ECON-2210)', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (152, N' Macroeconomics for the Real World', N'2510', N'ECON', N'This course will focus on the application of techniques of economics to the analysis of practical problems in areas of current interest like globalization, inequality, protectionism and government spending and taxes. The course will emphasize more policy, as opposed to theoretical, aspects of macroeconomics. (May not be taken for credit in Economics or combined major programs with Economics). (Prerequisite ECON-1110 or ECON-2010) (Anti-requisite: ECON-2310).', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (153, N' Selected Issues in Economics', N'2660', N'ECON', N'(Prerequisites: ECON-1100 and ECON-1110.)', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (154, N' Health Economics', N'2900', N'ECON', N'This course will explore the unique economic features of health care with emphasis on international models of delivery, determinants of the demand and supply of health services, and public versus private health care systems. The Canadian experience will be considered with a focus on demographic patterns and legislation. (Pre-requisites: ECON-1100, ECON-1110; plus any university-level course in statistics.)', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (155, N' Mathematical Economics I', N'3060', N'ECON', N'Economic applications of differential calculus and linear algebra, with an emphasis on problem solving and employing software used widely by economists. Topics include input-output analysis, optimization of linear microeconomic models, computer methods for comparative static exercises to analyze closed- and open-economy macroeconomic models, and unconstrained and constrained optimization of non-linear microeconomic models.(Prerequisites: MATH-1720 (or MATH-1760), MATH-1250 (or MATH-1260 or MATH-1270), ECON-2210,and ECON-2310.)', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (156, N' Environmental and Resource Economics', N'3100', N'ECON', N'An examination of economic behaviour in renewable and non-renewable resource markets and an economic treatment of policy issues related to environmental quality and common property resources. (Prerequisite: ECON-2210, or the combination of ECON-1100 and one of MATH-1300, MATH-1760 or MATH-1720.)', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (157, N' Introduction to Econometric Methods I', N'3130', N'ECON', N'Development of the classical regression model and problems associated with this model such as multicollinearity, heteroscedasticity, and autocorrelation. (Some familiarity with linear algebra and calculus will be beneficial.) (Prerequisites: (a) ECON-2120 or STAT-2950; (b) one of MATH-1300, MATH-1760 or MATH-1720; and, (c) one of MATH-1250, MATH-1260 or MATH-1270.)', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (158, N' The Economics of Legal Procedures, Crime, and Punishment', N'3310', N'ECON', N'The application of microeconomic principles in the analysis of legal procedures, crime, and punishment. Economic models of filing suit, bargaining, and going to trial will be discussed. Traditional and economic models of criminal activity will be compared and contrasted, along with the economics of civil and criminal punishment. Additional topics from other areas of law may be included. (Prerequisite: ECON-2210.)', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (159, N' Money and Banking', N'3350', N'ECON', N'The banking system and other financial institutions; money demand and money supply; money and the level of economic activity; money and inflation; issues in monetary policy. (Prerequisite: ECON-2310.)', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (160, N' Economic Growth and Development Theory', N'3410', N'ECON', N'Modern theories of growth and development with emphasis upon less developed countries. (Prerequisites: ECON-2210 and ECON-2310, or consent of instructor.)', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (161, N' Labour Theory', N'3500', N'ECON', N'Wage theories, wage structure, unemployment, labour supply and related topics. (Prerequisite: ECON-2210.)', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (162, N' Labour Institutions', N'3530', N'ECON', N'Canadian and American labour movements, collective bargaining, union philosophy and labour legislation in Canada and the United States. (Prerequisite: ECON-2210 or consent of instructor.)', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (163, N' International Economics: Trade Theory and Policy', N'3730', N'ECON', N'A survey of traditional and contemporary theories of international trade and trade policy. (Prerequisite: ECON-2210.)', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (164, N' International Economics: Exchange Rates and Balance of Payments', N'3740', N'ECON', N'Theory of exchange rate determination and balance of payments adjustment; macroeconomic policy in an open economy; current problems of the international monetary system. (Prerequisite: ECON-2310.)', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (165, N' Game Theory', N'3800', N'ECON', N'The study of strategic interactions among decision makers. Equilibrium concepts, such as Nash equilibrium, sub-game perfect equilibrium, etc, will be introduced and their applications to economic, political and biological decision making will be discussed. (Prerequisite: ECON-2210 or the combination of ECON-1100 and one of MATH-1760 or MATH-1720.)', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (166, N' Public Sector Economics: Expenditure', N'3850', N'ECON', N'Theory of the role of government in the economy; public expenditure theories and practice; public choice and government decision-making; government grants. (Prerequisites: ECON-2210.)', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (167, N' Mathematical Economics II', N'4060', N'ECON', N'Topics will include general equilibrium theory, dynamic analysis, oligopoly, behaviour under uncertainty, and growth theory. (Prerequisites: ECON-2220, ECON-2320, ECON-3060, and MATH-1730.)', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (168, N' Senior Research Workshop', N'4070', N'ECON', N'This course is intended to develop independent research and presentation skills. Students will be assigned a topic (or topics). Students will be expected to conduct a literature survey, collect data, present data in a descriptive format, formulate and carry out formal econometric tests. Students will be assessed on the basis of classroom presentations classroom discussion and written assignments. (Prerequisites: ECON-3130, ECON-4230 and ECON-4330.) (This course is open to students who are enrolled in an Honours Economics program.)', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (169, N' Introduction to Econometric Methods II', N'4140', N'ECON', N'A continuation of ECON-3130. (Prerequisite: ECON-3130.)', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (170, N' Urban and Regional Economics', N'4160', N'ECON', N'Economics of cities and urban problems; effects on production and trade; urban problems such as poverty, congestion, pollution and crime.', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (171, N' Advanced Microeconomics I', N'4230', N'ECON', N'The use of mathematical techniques and economic analysis with special emphasis on consumer theory, producer theory, and theory of markets. (Prerequisites: ECON-2210, ECON-2220, ECON-3060, or consent of instructor.)', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (172, N' Advanced Microeconomics II', N'4240', N'ECON', N'Selected topics in microeconomic theory. (Prerequisite: ECON-4230.)', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (173, N' Economics Analysis of Law', N'4300', N'ECON', N'The application of microeconomic principles to the study of property, contract, and tort law. The economic principles underlying property rights, torts, and contracts are surveyed. Selected problems in property, tort, and contract law are considered. Additional topics from other areas of law may be included. (Prerequisite: ECON-2210.)', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (174, N' Advanced Macroeconomics I', N'4330', N'ECON', N'Modern interpretations of macroeconomics, including inflation, unemployment, and policy implications. (Prerequisites: ECON-2320 and ECON-3060)', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (175, N' Advanced Macroeconomics II', N'4340', N'ECON', N'Selected topics in macroeconomic theory. (Prerequisite: ECON-4330.)', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (176, N' Cost-Benefit Analysis', N'4600', N'ECON', N'The techniques and application of cost-benefit analysis to public sector policies and projects. Topics include the welfare foundations or cost-benefit analysis, investment decision rules, the choice of a social discount rate, risk and uncertainty, shadow pricing of inputs and outputs, public sector pricing and the assessment of the value of intangibles such as time, life and noise. (Prerequisite: ECON-2210, or the combination of ECON-1100 and one of MATH-1300 or MATH-1720.)', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (177, N' Public Sector Economics: Finance', N'4860', N'ECON', N'Government taxation, user charges, borrowing, and the public debt in theory and practice; use of taxation as fiscal policy; and intergovernmental tax relations. (Prerequisites: ECON-2220.)', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (178, N' Economics Internship', N'4950', N'ECON', N'The economics internship course provides an opportunity for Economics students to combine their university education with career-related experience in their field of study. The course will increase the student’s skills and knowledge while working on practical tasks and projects. Students are placed with companies that provide training, a structured work environment and professional supervision of their work. The students are expected to accept and complete a minimum of 96 hours working on assignments at the host organization during one full semester. The students will attend an orientation class at the beginning and a class for course evaluation at the end of the semester.', 3, 3, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (179, N' Composition', N'1001', N'ENGL', N'An exploration of the fundamentals of effective writing, including attention to rhetorical concepts of audience, purpose, and context; planning, logical development, and organization; and format and style. (Because of the large number of written assignments and the need for individual instruction, enrollment in ENGL-1001 is limited.) (Not open to students majoring in English.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (180, N' Writing Creatively', N'1006', N'ENGL', N'An introduction to the fundamentals of writing creatively in various genres with emphases on reading and writing skills, discussions of published texts, and in-class workshops and writing exercises. (No portfolio submission required for admission.) (Does not count for credit as one of the five required creative writing courses of the English Literature and Creative Writing program.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (181, N' Writing about Literature', N'1002', N'ENGL', N'An introduction to the basic tools for analyzing and writing about literature. Students will be trained in practical criticism of the major genres of literature (poetry, drama, and narrative) and will write a number of critical essays. (Not available on an Audit basis.) (Restricted to majors in English and BAS only.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (182, N' Drama of the Western World: The Tragic Vision', N'1229', N'ENGL', N'An introduction to tragedy from antiquity to the present, from literary and theatrical perspectives.', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (183, N' Drama of the Western World: The Comic Vision', N'1239', N'ENGL', N'An introduction to comedy from antiquity to the present, from literary and theatrical perspectives.', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (184, N' Women and Literature', N'1289', N'ENGL', N'An introduction to the ways in which women have been represented and constructed in English literature of various periods.', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (185, N' Topics in Literature', N'1409', N'ENGL', N'An introduction to a topic in literature. Topics may include Canadian Aboriginal literature; literature pertaining to topics such as the Bible, the environment, disability studies, film, or music; comparative literatures; or world literatures in English. (May be repeated for credit if the topics are different.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (186, NULL, NULL, NULL, NULL, 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (187, N' Topics in World Literatures', N'2310', N'ENGL', N'This course features world literature in English or in translation drawn from Western and non-Western sources. Historical settings, cultural backgrounds and critical commentaries complement views on cultural diversity and inter-disciplinarity. This course focuses on any of the major genres, including non-fiction and a variety of literary traditions including women''s, minority, and ethnic literatures. Topics could include Orientalism, Diaspora writing, as well as African, Asian, Chinese, European, or Middle-Eastern literatures either in English or in translation. (May be repeated for credit if the topics are different.) (Prerequisite: Semester 3 standing and one 1000-level English literature course.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (188, N' Topics in Culture and Text', N'2029', N'ENGL', N'This course will feature studies in English literature with changing emphasis on particular themes, genres, or authors. Topics might include genre studies such as Science Fiction, Children’s literature, Detective Fiction, Visual Narrative (i.e.; Comics), Environmental Criticism, Disability and Literature, and the Semiotics of media, among others. (May be repeated for credit if the topics are different). (Prerequisite: Semester 3 standing and one 100 level English literature course) (Prerequisite: Semester 3 standing and one 100 level English literature course).', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (189, N' Creative Writing I', N'2710', N'ENGL', N'An intensive workshop in various genres. Previous formal creative writing experience is expected. (Portfolio approval is required for admission.) (Not available on an Audit basis.) (A 6.0-credit, two-term course.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (190, N' Children’s Literature', N'2059', N'ENGL', N'A critical study of selected works of literature written for children, including nursery rhymes, fairy tales, and book-length classics. (Prerequisite: Semester 3 standing and one English-literature course.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (191, N' Early British Literature', N'2109', N'ENGL', N'A critical study of selected works of major writers of the Medieval, Renaissance, Restoration, and early eighteenth-century periods (from 450 to 1760) (Restricted to English majors only.) (Students may not receive credit for both ENGL-2109 and 26-110).', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (192, N' Later British Literature', N'2119', N'ENGL', N'A critical study of selected works of major writers of the Romantic, Victorian, and Modern periods (from 1760 to the present). (Restricted to English majors only.) (Students may not receive credit for both ENGL-2119 and 26-111).', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (193, N' Canadian Literature', N'2609', N'ENGL', N'A critical study of selected works of Canadian literature across various genres, regions, and communities. (Prerequisite: Semester 3 standing and one English-literature course.) (Restricted to English majors.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (194, N' US Literature', N'2709', N'ENGL', N'A critical study of selected works of US literature across various genres, regions, and communities. (Prerequisite: Semester 3 standing and one English-literature course.)(Restricted to English majors.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (195, N' Contemporary Literary Theory', N'2809', N'ENGL', N'A survey of contemporary literary theory, which may include new criticism, structuralism, poststructuralism, hermeneutics, psychoanalysis, Marxism, new historicism, or gender studies. Explores fundamental critical concepts, with an emphasis on the ways in which notions of reading, textuality, authorship, and subjectivity have developed in Anglo-American and European thought. (Prerequisite: Semester 3 standing and one 1000-level literature course in English.) (Restricted to English Majors)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (196, N' History of Literary Criticism', N'2859', N'ENGL', N'Major Works, movements, and ideas in literary criticism from the Greek Classics to the mid-Twentieth century will be covered. This course will help students develop skills in the application of theory as well as the criticism of theory itself. Key schools of thought or movements covered could include Classical, Neo-Classic, Feminist, Humanist, Romantic, Nihilist, Existential, Marxist, and Psychoanalytic, among others. (Prerequisite: Semester 3 standing and one 1000-level English literature course.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (197, N' Introduction to Rhetoric', N'2410', N'ENGL', N'A survey of historical and theoretical aspects of rhetoric from the fifth century BCE to the present, including an examination of the relationship between rhetoric, epistemology, ethics, and politics. (Prerequisite: Semester 3 standing and one 1000-level English literature course.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (198, N' History of the English Language', N'2420', N'ENGL', N'A survey of the background and origins of the English language and its various forms from Old English to the end of the eighteenth century. (Prerequisite: Semester 3 standing and one 1000-level English literature course.) (Restricted to English majors.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (199, N' Modern English and Linguistics', N'2939', N'ENGL', N'A survey of linguistics (the study of languages as systems), with particular emphasis on the English language. (Prerequisite: Semester 3 standing and one 1000-level English literature course.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (200, N' Gender and Literature', N'3019', N'ENGL', N'A study of how gender is constructed in texts from a variety of periods, with emphasis on cultural contexts, feminist theory, and notions of gender and sexuality. (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (201, N' Writing About the Arts (Practicum)', N'3002', N'ENGL', N'A practicum in writing about contemporary forms of artistic expression. Students will write in multiple genres, exploring connections between art, its social and cultural contexts, and their own experience. Coursework and assignments will be complemented by interactive explorations of a variety of art forms. (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (202, N' Creative Writing II: Special Topics', N'3710', N'ENGL', N'An advanced workshop featuring a specific genre, approach, or subject. (Portfolio approval is required for admission.) (May be repeated for credit if topics are different.) (Not available on an Audit basis.) (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (203, N' Editing Practicum', N'3059', N'ENGL', N'A practicum in the theory and practice of editing historical, scholarly, and creative works. Students will be directly involved with current editorial projects in the Department. (Permission of the instructor required.) (Not available on an Audit basis.) (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (204, N' Publishing Practicum', N'3069', N'ENGL', N'This course introduces students to theoretical and practical aspects of book production and provides a framework to apply knowledge and skills to concrete projects. Students will oversee the creation of a bound publication working with a finished, edited manuscript. The course will focus on project management, budgeting, author relationships, design, production, publicity, sales and marketing. This course covers the fundamentals of pre-publication planning as well as pre-press and printing production, principles of graphic design, and issues surrounding copyright. The emphasis of the course is on praxis; students are expected to make decisions at each stage of production, from choosing a printing house to determining the best way to promote and market the finished book. Completed projects will be published through a professional Press, or in the format of a scholarly journal. (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (205, N' Digital Journalism and Storytelling (Practicum)', N'3079', N'ENGL', N'A workshop in writing across mainstream and alternative digital and social media. Students will develop strategies for conducting effective interviews, discover new approaches to packaging stories with available contemporary elements, find target audiences, develop their skills using digital communications tools, and identify the best social media platforms to deliver their stories. (Not available on an Audit basis.) (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (206, N' Scholarship and Bibliography (Practicum)', N'3099', N'ENGL', N'A study of literary research methods and textual scholarship. Includes practice in research techniques and in bibliographic description, the study of editing procedures, and the examination of the historical and theoretical contexts of textual production. (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (207, N' Middle English Literature', N'3109', N'ENGL', N'A study of post-1066 Medieval literature, excluding Chaucer. Texts will be read in normalized Middle English. (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (208, N' Chaucer', N'3129', N'ENGL', N'A study of the major works of Chaucer, including The Canterbury Tales. Texts will be read in normalized middle English. (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (209, N' Topics in Medieval Literature', N'2149', N'ENGL', N'Studies in Medieval literature, with changing emphasis on particular themes, genres, or authors. Topics might include Old English or Medieval Romance. (May be repeated for credit if the topics are different.) (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (210, N' Topics in Renaissance Literature', N'3229', N'ENGL', N'Studies in Renaissance literature, with changing emphasis on particular themes, genres, or authors. Topics might include Milton and Paradise Lost, early seventeenth-century lyric, or literature of the English Revolution. (May be repeated for credit if the topics are different.) (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (211, N' Sixteenth-Century Non-Dramatic Literature', N'3239', N'ENGL', N'A study of continuity and change in English literature, culture, and intellectual history in the sixteenth century. Explores canonical and non-canonical poetry and prose by men and women in the context of the European Renaissance and Reformation. (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (212, N' Seventeenth-Century Non-Dramatic Literature', N'3249', N'ENGL', N'A study of continuity and change in English literature, culture, and intellectual history in the seventeenth century. Explores canonical and non-canonical poetry and prose by men and women in an age of religious, political, and scientific revolution. (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (213, N' Shakespeare I', N'3269', N'ENGL', N'A study of selected plays to 1600 (early tragedies, histories, and comedies) from literary and theatrical perspectives. (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (214, N' Shakespeare II', N'3279', N'ENGL', N'A study of selected plays from 1600 (tragicomedies, tragedies, and romances) from literary and theatrical perspectives. (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (215, N' Topics in Renaissance Drama', N'3289', N'ENGL', N'Studies in Renaissance drama, with changing emphasis on particular themes, genres, or authors. Topics might include Shakespeare in Performance (at the Academy at the Stratford Festival) or drama of the English Renaissance (excluding Shakespeare). (May be repeated for credit if the topics are different.) (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (216, N' Restoration Literature', N'3339', N'ENGL', N'A study of literature in the light of the shifting social, political, and intellectual contexts of 1660-1700. Texts include poetry, drama, fiction, and polemical prose by men and women. (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (217, N' Eighteenth-Century Literature', N'3349', N'ENGL', N'A study of literature from the Augustans to the Romantics. Texts range from poetry to short fiction to journalistic prose by men and women. (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (218, N'ENGL-3359 Restoration and Eighteenth Century Drama-', N'3359', N'ENGL', N'A study of English plays and theatre 1660-1800. Writers may include Etherege, Behn, Dryden, Congreve, Steele, Lillo, Goldsmith, and Sheridan. (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (219, N' Topics in Restoration and 18th-Century Literature', N'3369', N'ENGL', N'Studies in Restoration and eighteenth-century literature with changing emphasis on particular themes, genres, or authors. Topics might include eighteenth-century fiction, satire, gender and literature, and colonialism. (May be repeated for credit if the topics are different.) (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (220, N' Early Romanticism', N'3439', N'ENGL', N'A study of the literature of late eighteenth-century Britain in its historical and cultural contexts. Writers may include Burns, Austen, Blake, Wollstonecraft, Godwin, Walpole, Wordsworth, and Coleridge. (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (221, N' Later Romanticism', N'3449', N'ENGL', N'A study of the literature of late eighteenth and early nineteenth-century Britain in its historical and cultural contexts. Writers may include Mary Shelley, Keats, Byron, Hemans, P. B. Shelley, de Quincey, and Clare. (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (222, N' Early Victorians', N'3469', N'ENGL', N'A study of responses to industrialization, urbanization, social reform, gender relations, and late Romantic ideas. Writers may include Dickens, Gaskell, Tennyson, Carlyle, and the Brownings. (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (223, N' Later Victorians', N'3479', N'ENGL', N'A study of responses to changing attitudes and values in art and society from mid-century to the death of Queen Victoria. Writers may include Arnold, Eliot, the Rossettis, Hardy, and Wilde. (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (224, N' Topics in Victorian Literature', N'3489', N'ENGL', N'Studies in Victorian literature with changing emphasis on particular themes, genres, or authors. Topics might include the “Woman Question”, representations of empire, literature of the fin-de-siècle, the Brontës, the working-class question, or Victorian gothic. (May be repeated for credit if the topics are different.) (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (225, N' Topics in Romantic Literature', N'3499', N'ENGL', N'Studies in Romantic literature with changing emphasis on particular themes, genres, or authors. Topics might include Romantic biography/autobiography, landscape and representation, Romantic women writers, the Jacobin novelists, Romanticism and race, or the gothic. (May be repeated for credit if the topics are different.) (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (226, N' Modern Drama', N'3509', N'ENGL', N'A study of drama from the end of the nineteenth century to the mid twentieth century. Writers may include Henrik Ibsen, Anton Chekhov, August Strindberg, Oscar Wilde, George Bernard Shaw, Eugene O’Neill, Gertrude Stein, Eugene Ionesco, Arthur Miller, and Harold Pinter among others. (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (227, N' Modern British Literature', N'3529', N'ENGL', N'A study of works published in the first half of the twentieth century. Writers may include Hopkins, Hardy, James, Conrad, Lawrence, Eliot, Woolf, Ford, and Auden. (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (228, N' Contemporary British Literature', N'3539', N'ENGL', N'A study of works published since the mid-twentieth century. Writers may include Orwell, Jones, Greene, Golding, Spark, Fowles, Pinter, Stoppard, Caryl Churchill, Dylan Thomas, Amis, Larkin, Hughes, and D. M. Thomas. (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (229, N' Literature and Postcolonialism', N'3549', N'ENGL', N'A study of the literature of nations and peoples responding to various forms of oppression, including colonization, racism, assimilation, and genocide. Introduces relevant theory and focuses on contemporary English-language texts from Africa, the Caribbean, South Asia, North America, and elsewhere. (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (230, N' Modern Irish Literature', N'3559', N'ENGL', N'A study of works published since the start of the twentieth century. Writers may include Yeats, Joyce, Synge, O’Casey, Clark, Beckett, Kavanaugh, O’Brien, Kinsella, Trevor, and Heaney. (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (231, N' Contemporary Drama', N'3569', N'ENGL', N'A study of drama from the mid-twentieth century to the present. Writers may include Samuel Beckett, Derek Walcott, Wole Soyinka, Caryl Churchill, Harold Pinter, Cherríe Moraga, David Henry Hwang, Tom Stoppard, Edward Bond, David French, Robert Lepage, among other diverse and popular playwrights, collectives, and performance artists. (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (232, N' Topics in Modern and Contemporary British Literature', N'3579', N'ENGL', N'Studies in modern and contemporary British literature with changing emphasis on particular themes, genres, or authors. Topics might include literary impressionism, poets of WWI, or the mid-length poem. (May be repeated for credit if the topics are different.) (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (233, N' Native Literatures and Cultures', N'3589', N'ENGL', N'A study of literature by First Nations and Aboriginal writers from Canada, the United States, New Zealand, and Australia. (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (234, N' Literatures of the African Diaspora', N'3320', N'ENGL', N'A study of literatures in English produced by writers of African descent in Africa, the Americas, Canada and the Caribbean. The course may take as its focus a specific period or the development of the literature of a particular area. The course may include works by writers and theorists such as: Derek Walcott, Wole Soyinka, Toni Morrison, George Elliott Clarke, Ama Ita Aidoo, Frantz Fanon, Paul Gilroy, and Ngugi Wa Thiong ’O. (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (235, N' Topics in Canadian Literature', N'3619', N'ENGL', N'Studies in Canadian literature with changing emphasis on the literature of a particular region or community, a particular genre, or select authors. (May be repeated for credit if the topics are different.) (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (236, N' Canadian Poetry', N'3669', N'ENGL', N'A study of significant modern and contemporary Canadian poetry. Discussion may include questions of form, voice, place, identity, and community. (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (237, N' Canadian Fiction', N'3679', N'ENGL', N'A study of significant modern and contemporary Canadian short stories and novels. Discussion may include questions of identity, place, form, voice, and community. (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (238, N' Topics in USLiterature', N'3719', N'ENGL', N'Studies in US literature, with changing emphasis on particular themes, genres, or authors. Topics might include American gothic, 19th-century citizenship, African-American literature, or the Harlem Renaissance. (May be repeated for credit if the topics are different.) (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (239, N' US Literature: Colonials to Civil War', N'3729', N'ENGL', N'A study of the emergence and development of US literary identity from the earliest settler writings through to the American Renaissance. (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (240, N' US Literature: Civil War to Realists', N'3739', N'ENGL', N'A study of innovations in style and subject during the period between the Civil War and World War I. (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (241, N' The US Moderns', N'3749', N'ENGL', N'(Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (242, N' The Literature of Contemporary America', N'3759', N'ENGL', N'A study of post-WWII US literature in the contexts of contemporary social and artistic change. (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (243, N' Topics in Literary or Cultural Theory', N'3839', N'ENGL', N'Studies in selected theories, theorists, or movements and countermovements in contemporary literary theory, cultural studies, or intellectual history. (May be repeated for credit if the topics are different.) (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (244, N' Topics in Language and Linguistics', N'3420', N'ENGL', N'Studies in language and linguistics, with changing emphasis on an area or subfield of linguistics (e.g., syntax or sociolinguistics) or of a related field. (May be repeated for credit if the topics are different.) (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (245, N' Advanced Composition Theory and Practice', N'3979', N'ENGL', N'A study of the relationship between theory and practice in Composition. Applying theories of Composition and writing in a variety of genres, students will examine how people write and how discourse is produced and circulated. (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (246, N' Topics in Composition and Rhetoric', N'3410', N'ENGL', N'Studies in Composition and Rhetoric, with changing emphasis on particular aspects of these fields. Topics might include literacy studies, visual rhetorics, or rhetoric and contemporary society. (May be repeated for credit if the topics are different.) (Prerequisite: Semester three standing, and two of ENGL-1002; ENGL-2109 or ENGL-2119.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (247, N' Seminar in Medieval Literature', N'4119', N'ENGL', N'(Prerequisite: Semester 7 standing and ten English courses.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (248, N' Seminar in Renaissance Literature', N'4129', N'ENGL', N'(Prerequisite: Semester 7 standing and ten English courses.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (249, N' Seminar in Restoration and Eighteenth-Century Literature', N'4139', N'ENGL', N'(Prerequisite: Semester 7 standing and ten English courses.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (250, N' Seminar in Romantic Literature', N'4149', N'ENGL', N'(Prerequisite: Semester 7 standing and ten English courses.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (251, N' Seminar in Twentieth-Century British Literature', N'4159', N'ENGL', N'(Prerequisite: Semester 7 standing and ten English courses.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (252, N' Seminar in Canadian Literature', N'4169', N'ENGL', N'(Prerequisite: Semester 7 standing and ten English courses.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (253, N' Seminar in American Literature', N'4179', N'ENGL', N'(Prerequisite: Semester 7 standing and ten English courses.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (254, N' Seminar in Literary or Cultural Theory', N'4189', N'ENGL', N'(Prerequisite: Semester 7 standing and ten English courses, including ENGL-2809.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (255, N' Seminar in Composition and Rhetoric', N'4199', N'ENGL', N'(Prerequisite: Semester 7 standing and ten English courses.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (256, N' Special Topics Seminar', N'4209', N'ENGL', N'(Prerequisite: Semester 7 standing and ten English courses.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (257, N' Seminar in Literature of the Victorian Period', N'4249', N'ENGL', N'(Prerequisite: Semester 7 standing and ten English courses.)', 3, 4, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (258, N' Past to Present: Understanding History', N'1030', N'HIST', N'This course is specifically designed for first semester history majors, to introduce them to the history department, different kinds of historical inquiry, and the basics of historical research. Further, it is designed to create a cohort of the new history majors, both through participating in this class together and by working in small groups.', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (259, N' Europe Encounters the World: Facing Islam, 8th-15th Century', N'1130', N'HIST', N'This course looks at the different forms of contact between Europeans and the rest of the world during the Middle Ages, focusing on conflict and coexistence with Islam. It will consider exchanges between civilizations, whether of an economic, cultural, artistic or spiritual nature. Topics include Muslim Spain, the Crusades, the Ottoman Empire and Venice.', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (260, N' Europe Encounters the World: The Age of Discovery, 15th-18th Century', N'1140', N'HIST', N'This course looks at the different forms of contact between Europeans and the rest of the world during their first period of imperial expansion (15th-18th Century). Special attention will be paid to the discovery, conquest and settlement of India, Asia, and the Americas, as well as the relationship of Europeans with native populations of these continents.', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (261, N' The World in the Twentieth Century, 1914-1945', N'1230', N'HIST', N'An overview of the major events and movements during the first half of the ‘short’ twentieth century. The course will broadly explore the world-wide impact of the world wars, communism, fascism, colonialism, the Great Depression, etc. The geographical focus of the material will vary with the instructor.(3 lecture hours or 2 lecture hours, 1 tutorial hour a week.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (262, N' The World in the Twentieth Century, 1945-Present', N'1240', N'HIST', N'An overview of the major events and movements during the second half of the ‘short’ twentieth century. The course will broadly explore the world-wide impact of the Cold War, communism, decolonization, globalization, terrorism, etc. The geographical focus of the material will vary with the instructor. (3 lecture hours or 2 lecture hours, 1 tutorial hour a week.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (263, N'Selected Topics', N'1970', N'HIST', N'Topics of current interest in history which may vary from year to year. (May be repeated for credit if content changes.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (264, N'Early Modern Europe', N'2010', N'HIST', N'A survey of Europe from the Age of Discovery to the French Revolution. Areas of study will include the formation of a world economy, the industrial revolution, the rise of the nation state, popular culture, the Catholic and Protestant Reformations, the printing revolution, the Renaissance, the scientific revolution, and the Enlightenment. (3 lecture hours a week.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (265, N'Modern Europe', N'2020', N'HIST', N'A survey of Europe from the French Revolution to the present. Areas of study may include political ideologies, revolution, imperialism, world war, cold war, and European union. (3 lecture hours a week.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (266, N' Making History: Methods and Practices', N'2030', N'HIST', N'This course builds on historical skills and knowledge of the discipline introduced in HIST-1030. It emphasizes skills in research, assessing evidence, analyzing primary sources, bibliographic skills, and others tools needed for writing history papers. It will also introduce students to public history, digital history, and the ethics of research. At a larger level, it helps students think critically about the past and to recognize the way historians interpret the past and use evidence. (Pre-requisite: HIST-1030 or consent of instructor) (Anti-requisite: HIST 2030)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (267, N'Early Modern England, 1485-1714', N'2070', N'HIST', N'A survey of England’s transition from a medieval realm to a modern state. Areas of study may include relations with Scotland, Ireland and Europe, as well as dynastic, religious, and constitutional change. (3 lecture hours or 2 lecture hours/1 tutorial hour a week.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (268, N'Modern Britain, 1714 - Present', N'2080', N'HIST', N'A survey of Britain’s experience of industrialism, imperialism and post-colonialism. Areas of study may include political and social reform, the world wars, the welfare state, and the European Union. (3 lecture hours or 2 lecture hours/1 tutorial hour a week.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (269, N' Islamic History: Formative Period 600-1000', N'2100', N'HIST', N'This is a survey course that examines the development of a distinctive Islamic civilization over the course of four centuries in southern Europe, North Africa, the Middle East, and portions of Central Asia. The lectures will emphasize the following themes: 1) the formation of Islamic civilization as a long-term and gradual process engaged in by the conquering Arab Muslims and their conquered subjects; 2)the diversity of expressions of Islamic culture and religious practices; and 3) the important role played by historical memory in the formation of Islamic culture.', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (270, N' Islamic History: Consolidation, Expansion and Empire 1000 - 1800', N'2110', N'HIST', N'This is a survey course that explores the middle and early modern periods of Islamic history from 1000 to 1800 C.E. The middle period was one of continuing change and innovation as new political and religious institutions were developed. Throughout this period, the influence of Islamic civilization continued to expand to new regions including sub-Saharan Africa, Central Asia, and Southeast Asia. From 1500-1800 C.E. Muslims founded the Ottoman, Safavid, and Mughal empires. The subjects of these empires adopted new technologies and new patterns of trade as participants in the developing new world economy. The close of the course will be devoted to considering growing European dominance over these empires over the course of the 18th century, and the military and reforming modernizing schemes adopted by Muslims in response. (3 lecture hours per week.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (271, N' Modern Islamic History 1800-Present: The Rise of Secular Nationalism and Political Islam', N'2120', N'HIST', N'This survey course explores the history of the modern Islamic World. The dissolution of early modern empires led to a loss of sovereignty for most Muslims. The very serious challenges of colonialism, Zionism, war and civil war, and the impact of the oil economy were part of the Islamic world''s encounter with modernity. So was the activism in the public and private spheres with which these challenges were met including the adoption of civil law codes, the creation of new national identities, Islamic reform movements and debates about gender and the role of women driven by intellectuals and elites. The final weeks of the course will address the tumultuous twenty-first century in the region, the Arab Spring and Political Islam in its various forms. (3 lecture hours per week.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (272, N'War in the 20th Century', N'2180', N'HIST', N'An overview of the evolution of military conflict during the last one hundred years. In addition to traditional military history, this course will introduce many facets of the New Military History, such as the social history of soldiers, life on the homefront, gender and war, etc. (3 lecture hours, or 2 lecture hours and 1 tutorial hour per week.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (273, N' History of Africa, 700-1800', N'2200', N'HIST', N'This course is an overview of the major historical shifts in Africa during the pre-modern period (700-1800 AD). Its purpose is to introduce the student to Africa and the Africans: the space and its occupants. Main topics include climatic and linguistic maps, major networks of trade and communication, the cultivation of the `Semitic’ heritage (Christianity and Islam) and its impact on African experiences and relations with the rest of the world.', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (274, N' Canada from Early European Contacts to the Origins of Confederation, 1600-1867', N'2430', N'HIST', N'An overview covering Aboriginal societies, European colonialism, and the emergence of the Canadian federation. Areas may include native-newcomer relations, colonial culture and society, imperial conflict, and the origins of confederation. (3 lecture hours or 2 lecture hours, 1 tutorial hour a week.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (275, N' Canada since Confederation, 1867 to the Present', N'2440', N'HIST', N'An overview of the development of the Canadian federation. Areas may include competing visions of the Canadian “nation”, relations with Aboriginal peoples, industrialization and social change, and shifts in politics and political culture. (3 lecture hours or 2 lecture hours, 1 tutorial hour a week.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (276, N' Aboriginal Peoples in Canadian History: Beginnings to Mid-Nineteenth Century', N'2460', N'HIST', N'Aboriginal peoples and their impact on the history of Canada. Areas will include an overview of aboriginal nations, and the changing dynamics of the relationship between the first peoples and Europeans. (2 lecture, 1 lab hour per week.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (277, N' Aboriginal Peoples in Canadian History: Mid-Nineteenth Century to the Present', N'2470', N'HIST', N'(2 lecture, 1 lab hour per week.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (278, N' Women in Canada and the United States, 1600-1870', N'2490', N'HIST', N'A social history from the period of Native-European contact to the mid-nineteenth century. Work, family and sexuality, cultural ideals, and political status and activism among women of Native, African, and European origins will be examined. (3 lecture hours or 2 lecture hours, 1 tutorial hour a week.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (279, N' Women in Canada and the United States, 1870-Present', N'2500', N'HIST', N'A social history from the mid-nineteenth century to the present. Native, black, immigrant, and native-born white women’s roles in paid and household labour, family and cultural life, and reform movements will be examined. (3 lecture hours or 2 lecture hours, 1 tutorial hour a week.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (280, N' History of Women''s Movements in North America', N'2510', N'HIST', N'An exploration of the collective action of women in the past and present in North America. Areas of study may include women''s involvement with the temperance, civil rights, suffrage, trade union, environmental, reproductive rights, and women''s liberation movements. (Also offered as Women''s and Gender Studies WGST-2510.) (3 lecture hours or 2 lecture hours, 1 tutorial hour a week.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (281, N' History of America, 1600-1877', N'2610', N'HIST', N'The social, economic, and political history of the British North American colonies and the United States. Areas may include Native-European contact and conflict, the growth of the British Empire, slavery, the American Revolution, industrialization, reform movements, and the Civil War and Reconstruction. (3 lecture hours or 2 lecture hours, 1 tutorial hour a week.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (282, N' History of America, 1877 to the Present', N'2620', N'HIST', N'The social, economic, and political history of the United States since Reconstruction. Topics may include urbanization and immigration, Progressive reform, women’s suffrage, the Great Depression, the World Wars, McCarthyism, civil rights and women’s liberation, the Vietnam War, and the end of the Cold War (3 lecture hours or 2 lecture hours, 1 tutorial hour a week.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (283, N' Modern Latin America', N'2720', N'HIST', N'Nation-states in Spanish America, Brazil and the Caribbean, from the revolutions of independence to the present. Covers patterns of political and economic development shared throughout the continent. Country and thematic focus may vary from year to year, and may include the Haitian, Mexican, and Cuban revolutions, modern military dictatorships, resources and the environment, and gender and ethnic relations.', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (284, N' History of Crime', N'2870', N'HIST', N'Examines the ways in which crime and criminal justice were shaped by the societies in which they occurred and the ways in which they changed as these societies changed.', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (285, N' Selected Topics in History', N'2970', N'HIST', N'Topics of current interest, which may vary from year to year. (May be repeated for credit with consent of an advisor in History.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (286, N' History Workshop', N'3020', N'HIST', N'A series of modules that gives students first hand experience in carrying out historical research and exposes them to sources for doing so. Activities may include visiting an archive and cataloguing sources, designing an historical web page, using computers for quantitative research, creating videos', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (287, N' Schools of Historical Thought', N'3030', N'HIST', N'This course is specifically designed to introduce third year history students to a case study in historiography. Each time it is taught, the instructor’s specialization will be the theme, and he or she will outline the various historiographical approaches to that theme.', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (288, N' Gender in Islamic History', N'3100', N'HIST', N'This course is a historical study of gender in Islamic History, with special emphasis given to the modern Middle East and Afghanistan. We will examine the role of gender systems at different times and places in Islamic history through primary sources. Some themes of the course may be 1) the ways in which discourses of gender were constructed in ways usually disadvantageous to women. Though careful attention must be paid to important differences in time and place; 2) The relationship of gender systems to other hierarchical social structures such as class, ethnicity and age; 3) women and mens’ roles in preserving and constructing the gender systems of their society; and 4) the ways in which women and men were able to exercise agency in overcoming or transcending limitations of the dominant discourses on gender.', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (289, N' The European Renaissance', N'3160', N'HIST', N'A study of European intellectual, cultural and artistic life from the 14th to the 16th century. Centered around the notions of Humanism and the revival of Greco-Roman Antiquity, special attention will be given to Italy and the Germano-Flemish lands, but areas of study will also include Spain, France, Eastern Europe, and the Ottoman empire.', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (290, N' Africa and the Atlantic System', N'3200', N'HIST', N'This course explores the nature and terms of West Africa’s interaction with the Atlantic commercial system that materialized after European colonization of the Americas. It revolves around the birth, growth and demise of the Trans-Atlantic Slave Trade (1600s-1800s). The major themes cover the rationale and mechanics of this slave trade, and its impact on the African side of the Atlantic system. Students will be introduced to the general parameters of academic discourses on the Transatlantic Slave Trade and its legacy. (Prerequisite: Semester 4 standing or Permission of instructor.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (291, N' Colonialism in Africa, 1830s-1960s', N'3210', N'HIST', N'This course explores the imposition and liquidation of European colonialism in Africa. It focuses on the political, economic, and cultural forces behind colonialism, and the attitudes of its agents. Emphasis will be placed on highlighting the major similarities and differences between European colonial power structures and African resistance to, adaptation to and adoption of those structures. (Prerequisites: HIST-2200 or semester 4 standing.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (292, N' Becoming Visible: Women in European History', N'3360', N'HIST', N'An introduction to women’s status, roles and significance in European history, with emphasis on feminist ideologies and women’s movements from the eighteenth- through the mid-twentieth centuries. The geographic focus may vary from year to year.', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (293, N' Women, War and Peace', N'3400', N'HIST', N'This course examines the various and distinct ways in which women experience war and peace, in both historical and contemporary contexts. Topics include home fronts during wartime in both combat and non-combat zones, women''s peace activism, displacement, war crimes against women, women in combat, and media coverage of women and war/peace across the 20th and 21st centuries. (Also offered as Women''s and Gender Studies WGST-3400.) (Prerequisite: one 2000-level Women''s and Gender Studies or History course or POLS-2600 or consent of the instructor.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (294, N' Canadian Labour History', N'3490', N'HIST', N'The development of the Canadian labour movement and the working-class experience from the nineteenth century to the present. (Also offered as Labour Studies WORK-3490.) (Prerequisites: semester 4 standing. Labour Studies majors must have Semester 4 or above standing or consent of instructor.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (295, N' Slavery In North America, 1600-1877', N'3610', N'HIST', N'The history of racial slavery, including both Amerindians and Africans, the emergence of the concept of “race”, male and female experiences, resistance to slavery, British abolition, Civil War, and Reconstruction. The Canadian and U.S. experiences will be compared.', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (296, N' African Americans/Canadians After Emancipation, 1877 to the Present', N'3620', N'HIST', N'The history of racial discrimination, violence, and segregation, struggles for political rights, labour, migration and immigration, and the cultural activity of people of African descent in the U.S. and Canada from the end of American slavery to the present. Women’s and men’s lives will be treated equally. (Prerequisite: semester 4 standing.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (297, N'American History, 1945 to the Present', N'3630', N'HIST', N'Selected themes in the political and social history of the United States from the end of World War II to the present. (Prerequisite: HIST-2620 or consent of instructor.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (298, N' North American Popular Culture', N'3680', N'HIST', N'An investigation of North American popular culture from the nineteenth century to the present. Topics of study may include sports and masculinity, youth culture, media representations of women, “the Sixties,” the impact of cinema and television, and popular music.(Prerequisite: One of HIST-2440, HIST-2620, or HIST-3630 or consent of the Instructor.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (299, N' History on the Web', N'3800', N'HIST', N'This course will explore the various ways in which history is currently being learned, studied, researched, created, manipulated, and enjoyed on the internet today. Students will both interrogate and analyze these various uses, as well as participate in each approach to history on the web, including creation. (Prerequisite: Semester 5 standing or above.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (300, N' Selected Topics in History', N'3970', N'HIST', N'Topics of current interest which may vary from year to year. (May be repeated for credit with consent of an advisor in History.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (301, N' Medicine, Healing and the Health Professions', N'4030', N'HIST', N'A social history of medicine, including non-Western and unorthodox traditions, with a cross cultural focus on healers and an emphasis on the evolution of the allied health professions. Topics may include the consolidation of biomedicine, women and indigenous healers, the modern hospital, and the patient’s perspective. (Prerequisite: Semester 7 standing or permission of instructor.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (302, N' Culture and Society in Victorian Britain', N'4080', N'HIST', N'A thematic approach to Victorian studies. Areas may include labour and leisure, science and religion, history and memory, gender and sexuality, class and national identity, literature and education. (Prerequisite: Restricted to History majors and other students with at least semester 5 standing and permission of the instructor.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (303, N' The Life and Legacy of Muhammad', N'4110', N'HIST', N'This course is designed to introduce students to four strands of thought in the history of constructing the life and legacy of the prophet Muhammad. These are 1) the traditional Muslim account of his life, 2) a variety of approaches to the topic by modern social scientists, 3) traditional delegitimizing of Muhammad in historic Western European polemics and their modern equivalents, 4) the role that Muhammad plays in the beliefs and practices of modern Muslims. (Semester 5 standing or above.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (304, N' Religion and Politics in Modern Africa', N'4200', N'HIST', N'This course deals with the intersection between religion and politics in Africa. The main focus of the course is on the role of religion in territorial expansion and political centralization. Comparable examples of the deployment of `providential truth’ to legitimize the conquest of space, control of its resources and the management of its occupants in different geographical settings will be introduced, and how it shaped African interactions with Asians or Europeans with comparable ideas about providential truth. (Prerequisite: Semester 5 standing or Consent of Instructor.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (305, N' State of Apartheid: South Africa (1900s-1990s)', N'4210', N'HIST', N'This course introduces students to the cumulative South African historical experience known as Segregation (1910-1948) and Apartheid (1948-1994). Students will explore how “race” became a determinant of where one could live, what one could do for a living, for ‘how much’, and even who one could marry. To emphasize the casual relation between power relations and the production of knowledge, the readings assigned for this course are, mostly, produced by South African literati with first-hand experience of Segregation and/or Apartheid. (Prerequisites: HIST-2200 or HIST-3210, and Semester 6 standing and/or permission of instructor.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (306, N' The Early Modern Atlantic World', N'4350', N'HIST', N'This course looks at the foundation, development and interaction of the different European empires (Portuguese, Spanish, French, Dutch, British) in the Americas and Africa from the 15th to the 18th century. Topics include encounters with Africans and the native peoples of the Americas, cross-cultural exchanges, circulation of peoples, ideas, and commodities, migration, missions, conversion, and slavery.', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (307, N'Politics and Society in Industrializing Canada, 1890s-1930s', N'4450', N'HIST', N'The impact of modernity on politics and the Canadian state. Topics may include political culture and ideology, political and social movements, and the beginning of state intervention in society. (Prerequisite: Restricted to History majors and other students with at least semester 5 standing and permission of instructor.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (308, N'The Making of Post-War Canada', N'4460', N'HIST', N'The changing relationship between the state and society during and after the Second World War. Topics may include the politics of post-war planning, the welfare state, nationalism, and political and social protest movements. (Prerequisite: Restricted to History majors and other students with at least semester 5 standing and permission of instructor.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (309, N'Early American History, 1600-1800', N'4580', N'HIST', N'Selected themes in the political and social history of early America, which may include European and Native American contacts, the political and social development of the American colonies, slavery, war and society, the changing status of women, and the American Revolution and its aftermath.(Restricted to History majors and other students with at least semester 5 standing and permission of the instructor.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (310, N' United States-Latin American Relations in the 20th Century', N'4620', N'HIST', N'The rise and shaping of U.S. power in the hemisphere, with emphasis on Latin American responses. Topics may include military intervention and anti-imperialist movements, cultural and other non-governmental exchanges, and the evolution of inter-American trade. (Prerequisite: Restricted to History majors and other students with at least semester 5 standing and permission of the instructor.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (311, N'History of Gender and Sexuality', N'4630', N'HIST', N'This course explores major themes in the history of gender and sexuality. These may include reproduction, contraception, and abortion; gender, race, and power; sexuality and the state; heterosexual relations and marriage; gay, lesbian, and transgender identities. Time period and geographical region will vary with the instructor.)(Prerequisite: Semester 5 or above standing and one of HIST-2490, HIST-2500, or HIST-2510/WGST-2510 or permission of the instructor.)(Also offered as Women''s and Gender Studies WGST-4630.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (312, N' History in the Movies', N'4660', N'HIST', N'This course treats films as historical documents through which to lens broader social and cultural phenomena. Distinct from film history, that is the study of the history of cinema, the course pairs more “traditional” historical research materials, such as academic historical writing and primary documentation, to what became the dominant cultural medium of the 20th century. Films function as both historical artefacts (objects implicitly capturing moments in time) and as historical narratives (mediums explicitly disseminating points of view relevant to the time of production). Engaging with them at both levels, students will be expected to examine the experiences, values, politics, and social identities within a particular period (such as 1970s America) or as related to a specific historical theme (gender, for example) as a means of better understanding the history of the subject. (The topic will vary with the instructor and may be re-taken by permission of History’s undergraduate coordinator.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (313, N' The Era of the Great War', N'4700', N'HIST', N'This course will explore the political, military, cultural and social history of the First World War and surrounding period, primarily in Germany, France, and Britain, but including some attention to Eastern Europe, Africa and Asia. The course will address the historiography of the Great War, with a focus on the experience of the war for soldiers, for women on the home front, for artists, and for those under occupation. (Prerequisites: Restricted to History majors with at least semester 5 standing; and restricted to other students with at least semester 5 standing and permission of instructor.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (314, N' Public History', N'4800', N'HIST', N'This course explores the theory and practices of Public History, including the ways in which History is communicated to the wider public by museums, cultural institutions, heritage sites, archives, film, social media, advertising, and national parks. Topics may include approaches to digital history, curating, digitizing archival documents, and exhibit design and presentation. (Prerequisite: Semester 5 standing or above.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (315, N' Public History Practicum', N'4810', N'HIST', N'This practicum provides students in the History program with the opportunity to apply learned concepts and theory to a practical setting and become further familiarized with an area of interest in Public History. Students will be placed in organizations in the Windsor-Essex and Chatham-Kent regions related to their area of interest in Public History (e.g., museums, historical societies, heritage sites, etc.), and will be expected to dedicate a total of 100 hours to both in-class and on-line learning, and practicum components of the course. The course is open to History majors only. (Prerequisites: HIST-4800 and minimum average in History courses of 75% or permission of instructor).', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (316, N'Selected Topics in History', N'4970', N'HIST', N'Topics of current interest which may vary from year to year. (May be repeated for credit with permission of a program advisor.) (May be repeated for credit with consent of an advisor in History.) (Prerequisite: Restricted to History majors and other students with at least semester 5 standing and permission of the instructor.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (317, N' Research Capstone Thesis', N'4990', N'HIST', N'Students will receive training in the methods and skills of advanced historical research, ultimately composing an undergraduate research thesis based on their own original research in the subject area of the course as offered. In the first term of this two-term course, they will identify a practical research topic, perform a review of relevant scholarly literature, and produce a research proposal outlining the topic and identifying a body of relevant research materials, either in manuscript form or available digital archives. In the second term, students will implement the research proposal, spending much of the term engaged in original research. Having collected the relevant research materials, they will write, edit, and revise their final research thesis, the cumulative exercise of the course. The course’s specific theme or region/time-period will vary with the instructor. (Pre-requisite: At least Semester 6 standing, and 75% average in History courses or permission of instructor.)', 3, 5, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (318, N' Intensive French for Beginners', N'1120', N'FREN', N'Designed for beginning students who wish to accelerate their learning of the French language, the course emphasises the acquisition of basic reading and writing skills, aural comprehension and oral practice. Students will obtain credit for two courses. (6 credits hours; 6 hours of class time per week.) (Antirequisites: Grade 10 French or higher.)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (319, N' Intensive Preparatory French: Intermediate Level', N'1130', N'FREN', N'This course targets further development of all four language skills (speaking, listening, reading, and writing) and provides a thorough grammar review. Students will obtain credit for two courses. (6 credits hours; 6 hours of class time per week.) (Antirequisites: Grade 12”U” French or Français, or equivalent, or higher.) (Prerequisite: FREN-1120, Grade 10 French, or equivalent, or permission of instructor).', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (320, N' Preparatory French: Intermediate Level II', N'1140', N'FREN', N'Further training in grammar. Reading and writing skills. Additional aural comprehension and oral practice. Laboratory work. This course is equivalent to Grade 12“U” French or equivalent. (Antirequisite: Grade 12“U” French or equivalent, or higher.) (Prerequisite: Grade 11 French, or equivalent.)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (321, N' French Language Training I', N'1210', N'FREN', N'A study of norms and functions of the French verb system, nouns, pronouns, and modifiers. Oral practice, pronunciation and composition. (Prerequisite: Grade 12“U” French or Français, or equivalent.) (Antirequisite: any previous 2000-level French language training courses.)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (322, N' French Language Training II', N'1220', N'FREN', N'Further study of the norms and functions of the French verb system, nouns, pronouns, and modifiers. Development of reading comprehension. Oral practice, composition. (Prerequisite: Grade 12“U” French or Français, or equivalent.) (Antirequisite: any previous 2000-level French language training courses.)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (323, N' Introduction to Literary Studies', N'1410', N'FREN', N'An introduction to the analysis of literary genres: poetry, drama and prose fiction. (Prerequisite: Grade 12“U” French or Français, or equivalent.) (Note: FREN-1410 is a prerequisite course for all literature courses in French Studies.)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (324, N' Oral Proficiency in French I', N'2150', N'FREN', N'A course designed to strengthen the student''s competence in oral French through intensive training at an intermediate level, in both oral expression and aural comprehension. (May not be taken for credit by native speakers of French.) (Prerequisite: permission of instructor.)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (325, N' French Language Training III', N'2210', N'FREN', N'Effective oral and written communication; practice in the logical development of ideas; vocabulary expansion. (Prerequisites: FREN-1210 and FREN-1220.)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (326, N' French Language Training IV', N'2220', N'FREN', N'(Prerequisites: FREN-1210 and FREN-1220.)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (327, N' Intensive French Language Training II', N'2230', N'FREN', N'This intensive language-training course combines the course content of FREN-2210 and FREN-2220 into a single term. Students will obtain credit for two courses. (6 credit hours; 6 hours of class time per week.) (Prerequisites: FREN-1210 and FREN-1220.) (Antirequisites: FREN-2210 and FREN-2220.)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (328, N' Introduction to Linguistics I', N'2300', N'FREN', N'A survey of fundamental linguistic concepts and ideas of language. Language analysis at the first three levels of description: phonetics, phonology and morphology. (Prerequisites: FREN-1210 and FREN-1220, or permission of instructor.)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (329, N' Introduction to Linguistics II', N'2310', N'FREN', N'(Prerequisites: FREN-1210 and FREN-1220, or permission of instructor.)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (330, N' French Classicism', N'2520', N'FREN', N'An introduction to the literature of seventeenth-century France and its principal doctrines. (Prerequisite: FREN-1410.)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (331, N' The French Enlightenment', N'2530', N'FREN', N'An introduction to the literature and thought of eighteenth-century France. (Prerequisite: FREN-1410.)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (332, N' French Romanticism', N'2550', N'FREN', N'A study of the romantic aesthetic through the poetry, prose, and drama of major romantic writers of the nineteenth century. (Prerequisite: FREN-1410.)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (333, N' Realism and Naturalism', N'2570', N'FREN', N'A study of post-romantic prose writing in the nineteenth century. (Prerequisite: FREN-1410.)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (334, N' Modern French Culture', N'2600', N'FREN', N'A general study of the patterned behaviour which constitutes the modern French cultural system, focusing on its intellectual, sociological, political, and religious aspects. Students will be required to read texts and will also avail themselves of the latest audiovisual technology. Prerequisites: FREN-1210.)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (335, N' Introduction to the Cultural Heritage of French Canada', N'2700', N'FREN', N'An introduction to the francophone cultural experience in Canada.(Prerequisites: FREN-1210)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (336, N' Introduction to the Culture of Sub-Saharan Francophone Africa', N'2810', N'FREN', N'An introduction to various cultures of sub-Saharan Francophone Africa from pre-colonial times to the present. Students will be required to read some historical texts and representative literature. Some television documentaries and films will also be viewed. (Prerequisites: FREN-1210)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (337, N' Introduction to Francophone Culture of the Maghreb and the Middle East', N'2830', N'FREN', N'An introduction to contemporary francophone culture in North Africa (Morocco, Algeria, and Tunisia) and the Middle East (Lebanon), largely through the study of films, popular music, media, newspapers, art exhibits and/or television programs as well as theoretical works that formulate the (post)colonial discourse. (Prerequisites: FREN-1210).', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (338, N' The Novel in Quebec and in Other Francophone Regions of Canada', N'2840', N'FREN', N'From the novel of the land to the post-modern novel in Francophone Canada. (Prerequisite: FREN-1410.)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (339, N' Oral Proficiency in French II', N'3150', N'FREN', N'A course designed to strengthen the student''s competence in oral French through intensive training at an advanced level, both in oral expression and aural comprehension. (Prerequisite: permission of instructor.)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (340, N' French for Business', N'3170', N'FREN', N'A study of business terminology in French, and of the business practices of French speaking countries. (Prerequisites: FREN-2210, and FREN-2220, or FREN-2230.) (Note: FREN-3170 may replace FREN-2150 or FREN-3150.)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (341, N' Error Analysis', N'3250', N'FREN', N'A course designed to strengthen the student''s competence in French through an analysis of the three most common sources of errors: the differences between spoken and written French, the first language and the interlanguage (unfinished French grammar) of the student. (Prerequisites: FREN-2210 and FREN-2220, or FREN-2230.)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (342, N' Aspects of Translation I', N'3280', N'FREN', N'A comparative analysis of French and English structures with special emphasis on translation processes. Accompanied by regular exercises in translation. (Prerequisites: FREN-2210 and FREN-2220, or FREN-2230.)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (343, N' Aspects of Translation II', N'3290', N'FREN', N'A comparative analysis of French and English structures with special emphasis on questions of meaning related to the sentence and its context. Accompanied by regular exercises in translation. (Prerequisites: FREN-2210 and FREN-2220, or FREN-2230.)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (344, N' Applied Linguistics', N'3300', N'FREN', N'An introduction to modern linguistic theories about language acquisition, followed by a comparative study of first and second language acquisition and a survey of second language teaching techniques highlighting the linguistic concepts underlying these techniques. (Prerequisite: FREN-2300 and FREN-2310.)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (345, N' The History of the French Language', N'3320', N'FREN', N'This course will examine successive stages in the development and spread of the French language from Late Antiquity to the present. (Prerequisite: FREN-2300 and FREN-2310, or permission of instructor.)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (346, N' Sociolinguistic Aspects of French in Canada', N'3330', N'FREN', N'A survey of the main characteristics of French in Canada with emphasis on its variations and varieties. Sociolinguistic concepts are introduced and discussed in relation to the situation of French in Canada. (Prerequisites: FREN-2300 and FREN-2310.)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (347, N' French Literature of the Middle Ages and the Renaissance', N'3500', N'FREN', N'This course will examine the major authors and genres of the Middle Ages and the Renaissance, from the twelfth to the sixteenth century, including: the chanson de geste, the love songs of the troubadours, the Arthurian Romance, the sonnets of Ronsard, and the works of Rabelais. (Prerequisite: FREN-1410.)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (348, N' Poetry from Baudelaire to Surrealism', N'3530', N'FREN', N'A study of post-romantic, modernist poetry from the mid-nineteenth century to the mid-twentieth century. (Prerequisite: FREN-1410.)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (349, N' The Modern Novel and Theatre', N'3540', N'FREN', N'A study of modern theatre and of the novel from the turn of the century (1900) until the Nouveau Roman. (Prerequisite: FREN-1410.)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (350, N' Contemporary Francophone Literature and Thought', N'3560', N'FREN', N'The post-modern aesthetic and writing in francophone countries of sub-Saharan Africa and the French West Indies. (Prerequisite: FREN-1410.)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (351, N' Contemporary Critical Theory', N'3570', N'FREN', N'An introduction to a variety of major critical approaches and their applications to the literary text. (Prerequisites: FREN-1410, plus two additional courses in literature.) (Note: FREN-3570 is a required course for students enrolled in an Honours Bachelor of Arts in French Studies, and will be of particular value to those intending to pursue a Master of Arts degree.)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (352, N' Francophone Literature of the Maghreb and the Middle East', N'3580', N'FREN', N'Introduction to Francophone literature of the Maghreb (Morocco, Algeria, Tunisia) and the Middle East (Lebanon) (Prerequisite: FREN-1410)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (353, N' Drama in Quebec and in other Francophone Regions of Canada', N'3830', N'FREN', N'A study of the main dramatic works in Francophone Canada. (Prerequisite: FREN-1410.)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (354, N' Poetry in Quebec and in other Francophone Regions of Canada', N'3850', N'FREN', N'A study of the main works of poetry in Francophone Canada. (Prerequisite: FREN-1410.)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (355, N' The French Lexicon', N'4340', N'FREN', N'Detailed examination of the morphological, semantic and sociolinguistic characteristics of the French lexicon. (Prerequisite: FREN-2310.)', 3, 6, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (356, N' Introduction to Greek Civilization', N'1100', N'GRST', N'An introduction to the cultural values and achievements of the ancient Greeks. Topics will include geography, history, mythology and religion, language and literature, art and daily life. (Recommended for prospective Greek and Roman Studies majors.)', 3, 7, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (357, N' Introduction to Roman Civilization', N'1200', N'GRST', N'An introduction to the cultural values and achievements of the ancient Romans. Topics will include geography, history, mythology and religion, language and literature, art and daily life. (Recommended for prospective Greek and Roman Studies majors.)', 3, 7, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (358, N'Topics in Classical Culture', N'2000', N'GRST', N'A thematic examination of a single social historical topic in Greco-Roman antiquity. Topics may vary from year to year. (May be repeated for credit if content changes.)', 3, 7, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (359, N' Religions of the Ancient World', N'2030', N'GRST', N'An introduction to religions from the ancient Near East and Mediterranean, such as those of Mesopotamia, Egypt, Israel, Greece, and Italy. Topics may include the decline of paganism and polytheism and the rise of Christianity and monotheism, sacred texts and rituals, priests and other religious personnel, and mystery religions and mysticism. (Prerequisite: GRST-1100 or GRST-1200, or consent of instructor)', 3, 7, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (360, N' Greek Prose', N'2110', N'GRST', N'An introduction to ancient Greek prose literature from the fifth century BC to the second century AD, with selected readings in translation. Authors may include historiographers, orators, philosophers, or novelists. Topics may vary from year to year. (May be repeated for credit if content changes.)', 3, 7, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (361, N' Greek Poetry', N'2111', N'GRST', N'An introduction to ancient Greek poetry from the eighth century BC to the second century AD, with selected readings in translation. Authors may include Homer, Hesiod, other early Greek poets, or dramatists (including those of tragedy, comedy, and the satyr play). Topics may vary from year to year. (May be repeated for credit if content changes.)', 3, 7, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (362, N' Greek Art and Archaeology', N'2120', N'GRST', N'A study of the artistic masterpieces and monuments of ancient Greece. Topics will include the search for Troy, the spirit of Greek sculpture, and Athens in the Golden Age. (Prerequisite: GRST-1100 or consent of instructor.)', 3, 7, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (363, N' Latin Prose', N'2210', N'GRST', N'An introduction to ancient Latin prose literature from the third century BC to the second century AD, with selected readings in translation. Authors may include orators, historiographers, novelists, or philosophers. Topics may vary from year to year. (May be repeated for credit if content changes.)', 3, 7, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (364, N' Latin Poetry', N'2211', N'GRST', N'An introduction to ancient Latin poetry from the third century BC to the fourth century AD, with selected readings in translation. Authors may include dramatists, epic poets, elegists, or satirists. Topics may vary from year to year. (May be repeated for credit if content changes.)', 3, 7, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (365, N' Roman Art and Archaeology', N'2220', N'GRST', N'A study of the artistic masterpieces and monuments of ancient Rome. Topics will include the tombs of the Etruscans and treasures of Pompeii and Rome in the days of the Caesars. (Prerequisite: GRST-1200 or consent of instructor.)', 3, 7, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (366, N' Topics in Classical Literature', N'3010', N'GRST', N'An in-depth study of some aspect of ancient Greco-Roman literature that may be thematic or generic in scope. Topics may vary from year to year. May be repeated for credit if content changes. (Prerequisite: GRST-1100 and GRST-1200, or consent of instructor)', 3, 7, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (367, N' The Ancient World on the Screen', N'3011', N'GRST', N'An exploration of modern depictions of ancient Greece and/or Rome in movies and/or on television. (Prerequisite: GRST-1100 and GRST-1200, or consent of instructor.)', 3, 7, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (368, N' Ancient Impacts on the Modern World', N'3012', N'GRST', N'This course explores the reception and (re)interpretation of ancient Greco-Roman culture by, and in, the modern world in a variety of different media (e.g., literature, visual arts, architecture, film). Topics may vary from year to year. (May be repeated for credit if content changes.) (Prerequisites: GRST-1100, GRST-1200.)', 3, 7, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (369, N' Topics in Ancient Material Culture', N'3020', N'GRST', N'This rotating topics course provides an in-depth study of some aspect of ancient material culture that may be thematic or chronological in scope. Required readings more usually consist of ancient sources in English translation and/or articles in modern scholarly journals than formal textbooks. May be repeated for credit if content changes. (Prerequisite: GRST-2120 or GRST-2220).', 3, 7, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (370, N' Greek Mythology', N'3130', N'GRST', N'A study of the myths and legends of ancient Greece. Modern theories of myth will be used to analyze the Greek material. They study will include both literacy sources in translation and the portrayal of myth in visual art and in architecture. (Prerequisite: GRST-1100 and GRST-1200, or consent of instructor.)', 3, 7, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (371, N' Roman Mythology', N'3230', N'GRST', N'A study of the myths and legends of ancient Rome. Modern theories of myth will be used to analyze the Roman material. The study will include both literary sources in translation and the portrayal of myth in visual art and in architecture. (GRST-1100 and GRST-1200, or consent of instructor)', 3, 7, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (372, N' Seminar in Classical Studies', N'4000', N'GRST', N'An in-depth study of an aspect of Greco-Roman antiquity. Topics may vary from year to year. (Prerequisite: at least semester 5 standing, or consent of instructor)', 3, 7, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (373, N' Directed Readings in Classical Civilization', N'4001', N'GRST', N'Designed for the advanced student who wishes to explore a special area of interest with the aid of a faculty advisor. (May be repeated for credit if content changes.) (Hours by arrangement.)', 3, 7, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (374, N'Introduction to Western Philosophy', N'1100', N'PHIL', N'An introduction to philosophy through the study of major figures and movements in the Western philosophical tradition. The figures and themes selected for any given year will be chosen by the instructor.', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (375, N' Philosophy and Human Nature', N'1120', N'PHIL', N'What is human nature? How do we think of ourselves as human beings? The focus of the course will be theories of human nature that have been put forward in Western philosophy. Some non-Western Philosophical sources may be used.', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (376, N'Contemporary Moral Issues', N'1290', N'PHIL', N'A critical examination of philosophical arguments about controversial moral issues. Readings will be chosen by the instructor on issues connected with one or several areas such as: biomedical ethics, euthanasia, suicide, environmental ethics, the treatment of animals, war and violence, pornography, censorship. Some non-Western Philosophical sources may be used.', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (377, N'Philosophy and Popular Culture', N'1300', N'PHIL', N'A philosophical inquiry into one or more of the more important contemporary cultural forms and phenomena. Topics may vary and may include popular music, television, virtual reality, sexual roles and stereotypes, or other topics.', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (378, N'Reasoning Skills', N'1600', N'PHIL', N'An explanation of, and practice in, the basic knowledge, skills and attitudes which are essential components of reasoning well. (Antirequisite: PHIL-1620.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (379, N'Logic and Argumentation', N'1620', N'PHIL', N'Basic deductive logic and argumentation theories and their application to the interpretation, assessment and construction of arguments used in the humanities, social sciences, and sciences as well as in discourse in the public realm. Topics include: deductive, inductive, presumptive reasoning or arguments, elementary sampling, differences between the kinds of support in different fields, elementary rhetoric and dialectic, and common fallacies. (Prerequisite: Open only to students in the IAS program or in the FAHSS Leadership Pathway.) (Antirequisite: PHIL-1600.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (380, N'Introduction to Ethics', N'2210', N'PHIL', N'A survey of the main contending theoretical positions on such basic questions of ethics as: Are all moral values and norms subjective or objective, relative or absolute? What makes right actions right? What is the good life for human beings?', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (381, N' Introduction to Social and Political Philosophy	', N'2220', N'PHIL', N'An examination of some of the main contending theories about the nature of society and the state, or of some of the central controversies in social and political theory. (Prerequisite: Semester 3 or above standing; or consent of the instructor.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (382, N'Business Ethics', N'2240', N'PHIL', N'An introduction to some central ethical notions (e.g., justice, the common good, moral vs. legal obligation); application of these issues and concepts to cases drawn from the experiences of business men and women (concerned with such issues as corporate responsibility, conflict of interest, honesty in advertising, preferential hiring, corporate responsibility for environmental externalities).', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (383, N' Ethics of Life, Death, and Health Care', N'2250', N'PHIL', N'The course will focus on the ethical issues arising from human mortality and vulnerability to sickness. Problems to be explored will vary from year to year and may include: the relation between mortality and the value of life, the ethics of life-extension, the legitimacy of suicide, physician assisted or not, the ethics of human reproduction, allocating scarce medical resources in an ageing population, and the ethics of genetic engineering.', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (384, N'Law, Punishment and Morality', N'2260', N'PHIL', N'An introduction to the philosophical issues related to understanding the nature of law and legal obligation, the relation between law and morality, and the purpose of punishment. The theoretical points and distinctions will be illustrated by their applications to particular current issues. (Prerequisite: semester 3 or above standing, or consent of the instructor.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (385, N'Environmental Ethics', N'2270', N'PHIL', N'What ethical obligations do we have to the non-human environment? The course examines various answers to that question. Topics may include: animal rights, the moral status of non-human life, the intrinsic value of ecosystems, the importance of wilderness, deep ecology, eco-feminism, economic development, environmentalism, and politics.', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (386, N' Indigenous Philosophy of the Americas', N'2300', N'PHIL', N'An introduction to the philosophical thought associated with the narratives, culture, and traditions of the Indigenous people in North, Central, and South America. Topics include (but are not limited to): creation stories, Indigenous responses to European ‘discovery,’ legal reasoning concerning indigenous people/communities, subjects of scientific examination, indigenous epistemology, environmental concerns, identity, activism, and the effects of colonialism (such as residential schools, land allotment/reserves, the 60’s Scoop, and cultural appropriation.) (Prerequisite: Semester 3 standing.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (387, N' Technology, Human Values and the Environment', N'2280', N'PHIL', N'An exploration of the philosophically important ethical concepts of human nature, freedom, progress, the good life, moral responsibility, and the environment as these relate to advances in technology. Topics may include: pollution, mass production, the commodification of nature, new technologies (e.g., biotechnology, nanotechnology).', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (388, N' Feminist Philosophies', N'2360', N'PHIL', N'An examination of key philosophical themes in feminism and philosophical debates among feminists. The themes and subjects of debate addressed may include sexism and oppression; feminist identity; the political significance of language, personal appearance, and pornography; feminist ethics; and feminist theories of knowledge. (Prerequisite: Semester 3 or above standing; or consent of the instructor. Can be taken as an Arts credit) (Also offered as Women''s and Gender Studies WGST-2360)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (389, N' Social Identity, Diversity and Race', N'2380', N'PHIL', N'This course explores the philosophical significance of social identity and the value of diversity with particular attention to issues of race and ethnicity. Philosophical issues to be examined may include: the scientific and socio-political nature of race categories; the status and effectiveness of minority rights in liberal democracy; problems arising from the intersection of race and ethnicity with other political dimensions such as gender and class; the status of general philosophical values across diverse cultures. (Prerequisite: semester 3 or above standing.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (390, N'Philosophy of Religion', N'2400', N'PHIL', N'An examination of the philosophical problems involved with religious belief and language. Can the existence of God be proven? Can the non-existence of God be proven? Can claims to religious knowledge be legitimized? Is there a unique logic of religious language that is cognitively meaningful? Is there any basis for claims about life after death? What is the nature of faith? These are the sorts of questions which are dealt with in this course. (Prerequisite: Semester 3 or above standing.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (391, N'Aesthetics', N'2430', N'PHIL', N'Aesthetics is concerned with problems which arise in the appreciation of objects which are deemed to have aesthetic value. Problems which may be raised in this course include the nature of aesthetic experience and aesthetic objects such as works of art and nature, as well as problems related to aesthetic value and judgment. (Prerequisite: Semester 3 or above standing.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (392, N' Metaphysics', N'2500', N'PHIL', N'An examination of fundamental questions about the nature of reality. What kinds of things are real; what distinguishes the real from the ideal, or the real from the illusory? Are there abstract entities (e.g., numbers)? The nature of necessity and possibility, essence and existence. (Prerequisite: Semester 3 or above standing; or consent of the instructor.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (393, N' Existentialism', N'2520', N'PHIL', N'A study of the views of some of the major existentialists. Figures studied may include Kierkegaard, Nietzsche, Sartre, Merleau-Ponty, Camus, and Jaspers. (Prerequisite: Semester 3 or above standing.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (394, N' Mind, Action, and Personal Identity', N'2530', N'PHIL', N'An examination of: contemporary views of the nature of mind and its relationship to body; whether human action is free, determined, or both; the relationship between a theory of personal identity and the answers to the preceding questions. (Prerequisite: Semester 3 or above standing.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (395, N' Theory of Knowledge', N'2540', N'PHIL', N'An examination of the nature of knowledge, with topics such as: definitions of knowledge, accounts of its structure, the extent and limits of knowledge, the relationship between experience and knowledge, the bases of rational or justified belief formation. (Prerequisite: Semester 3 or above standing; or consent of the instructor.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (396, N' Knowledge, Science, and Society', N'2550', N'PHIL', N'The course explores the relationship between what individuals know and their participation in society, including as members of scientific communities. Topics may include: the ways communities rather than individuals can hold knowledge; how cognitive authority depends on a person''s membership in, and social position in, society; the role of testimony in knowledge; how the legal system creates knowledge; the roles of gender, race, class, and culture in knowledge; and the ethical implications of knowledge. (Prerequisite: semester 3 or above standing.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (397, N'Philosophy of Science', N'2570', N'PHIL', N'What is a scientific explanation? A theory? How does observation relate to theory? Do theories describe reality, or are they just conventional tools? The course examines answers to these and similar questions, and the general conceptions of science behind the answers. (Prerequisite: Semester 3 or above standing.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (398, N' Fallacy', N'2600', N'PHIL', N'The objective is to develop the ability to discriminate between good and bad arguments found in everyday settings, using the concept of fallacy. A variety of kinds of fallacy are explained, and the skill of identifying them is taught. The basic tools for analyzing arguments are presented and put to use. Material for analysis is drawn from newspapers, current periodicals, and other sources of actual arguments. (Prerequisite: PHIL-1600 or PHIL-1620 and semester 3 or above standing; or consent of the instructor.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (399, N' Argumentation', N'2610', N'PHIL', N'The objective is to develop the ability to analyze and evaluate extended arguments found in the public media, books and articles, and to construct a well-argued case. (Prerequisite: PHIL-2600 or PHIL-1600 or PHIL-1620 and semester 3 or above standing; or consent of the instructor.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (400, N' Symbolic Logic', N'2620', N'PHIL', N'The course covers propositional logic as well as an introduction to the basic concepts of predicate logic. Topics include the construction of symbolic representation of natural language sentences, semantic methods for evaluating symbol formulas, and methods of constructing deductions or proofs. (Prerequisite: Semester 3 or above standing, or permission of the instructor. Antirequisite for non-Philosophy majors: COMP-2310, MATH-1020.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (401, N'Reasoning about Weird Things', N'2660', N'PHIL', N'How to evaluate extraordinary claims, such as claims about psychic phenomena (e.g. ESP), subliminal messages, crop circles, and water divining. The course may include topics such as: the limits of personal experience as a source of evidence, expert opinion, assessment of studies, scientific method. (Prerequisite: Semester 3 or above standing; or consent of instructor.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (402, N' Ancient Greek Philosophy', N'2730', N'PHIL', N'The course is a survey of major thinkers and themes in Greek philosophy with particular emphasis on Plato and Aristotle, but may include attention to Pre-Socratic and post-Aristotelian thinkers. The course will concentrate on the main developments in Greek, philosophy, including metaphysics, epistemology, politics, and ethics.', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (403, N' Early Modern Philosophy', N'2760', N'PHIL', N'The course will examine the development and major problems of rationalist and empiricist philosophy during the historical period of the rise of modern natural science. It will emphasize the metaphysical and epistemological changes introduced into Western philosophy during this period. Thinkers studied will include Descartes and Hume. Other thinkers examined may include one or more of Bacon, Locke, Berkeley, Spinoza, and Leibniz.', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (404, N' Special Topics', N'2800', N'PHIL', N'Special Topics courses will be offered occasionally, as resources allow, to meet a demonstrated academic need, where that need cannot be satisfied by any of the regular course offerings. Interested students should inquire in the Philosophy office. (Prerequisites: Semester 3 or above standing or permission of an advisor in Philosophy.)(May be repeated for credit if content changes.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (405, N' Social Pathologies', N'3190', N'PHIL', N'The course examines the emergence of pathological forms of social life that systematically undermine human interaction, distort social communication, and falsify individual and group consciousness. The course may explore the work of major social thinkers such as Rousseau, Hegel, Marx, Lukacs, Weber, Schmidt, Freud, Adorno, Marcuse, Arendt, Habermas and Honneth or investigate one or more specific forms of modern social pathologies such as racism, gender inequality, colonialism, extreme poverty, the destruction of the environment.(Prerequisite: PHIL-2210 or permission of the instructor).(Also offered as POLS-3190.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (406, N' Advanced Topics in Ethical Theory', N'3210', N'PHIL', N'This course will examine issues in ethical theory at an advanced level. Topics may include detailed and critical examination of ethical theories, rigorous exploration of the differences between ethics and other kinds of normative practices and theories, meta-ethical questions regarding the nature of the good, or the relation between ethics, politics and other aspects of social life. The authors studied may be wide ranging, depending on the particular focus of the instructor. (Prerequisite PHIL-2210)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (407, N' Human Rights and Global Justice', N'3230', N'PHIL', N'The course will focus on the meaning and nature of human rights and their relationship to global justice. Topics may include: the historical development of human rights doctrines, their relationship to classical citizenship rights, the relationship between universal human rights and culturally distinct life ways, relationship between legal/moral principles, material reality, and different conceptions of global justice, the strengths and limitations of human rights as principles to advance global justice. (Prerequisite: Semester 3 or above standing, or permission of the instructor.)](Also offered as POLS-3620.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (408, N' Animals and Ethics', N'3290', N'PHIL', N'The course examines philosophical views about our relationship to animals and the relation of these views to the evaluation of moral principles and ethical theories, including notions of justice and rights. It may cover such topics as: attitudes towards animals, animal awareness and autonomy, whether moral consideration should be extended to animals, whether animals have rights. (Prerequisites: Semester 3 standing and at least one prior Philosophy course, or permission of the instructor.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (409, N' Environmental Philosophy', N'3300', N'PHIL', N'This is an advanced philosophical exploration of some of the key intersections between humanity and the environment. The focus will be on articulating, understanding and evaluating important relations between the human and the non-human environment. Issues covered may include: the philosophy of nature, technology and environment, science and environment, metaphysics and environment, ecofeminism, radical ecology, and environmental politics. (Prerequisites: Semester 3 standing and at least one Philosophy course, or permission of the instructor)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (410, N' Philosophy of Education', N'3420', N'PHIL', N'A critical examination of theories about the nature, goals and values of education. The approach of the course may be historical, contemporary or a combination. (Prerequisites: Semester 3 or above standing and at least one prior Philosophy course, or consent of the instructor.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (411, N' Philosophy and Language', N'3520', N'PHIL', N'This course will introduce students to philosophical conceptions of language. Its focus may range from an historical overview of the philosophy of language, from either analytic or continental perspectives. Thinkers covered may include Ludwig Wittgenstein, A. J. Ayer, Robert Brandom or Hans Georg Gadamer. (Preequisite: Fifth semester standing).', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (412, N' Post-structuralist Theory', N'3550', N'PHIL', N'Philosophers studied in this course may include Derrida, Foucault, Lyotard, Deleuze, and Guattari. (Prerequisite: PHIL-1100 or PHIL-1120, or at least one 2000-level Philosophy course, or consent of the instructor.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (413, N' Mind Design and Android Epistemology', N'3560', N'PHIL', N'This course explores the implications of artificial intelligence and cognitive modelling research for issues in the philosophy of mind and epistemology, including: the nature of mental states; thinking as largely linguistic, and alternatives; and effects of the way we think of mental states on the way we think about reasoning and knowing. (No specific background in science required, but an introductory-level course in psychology or computer science recommended.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (414, N' Women, Knowledge and Reality', N'3590', N'PHIL', N'An exploration of feminist theories about knowledge and reality that inform and are informed by scholarship in Women''s Studies. Students examine how gender might affect identity, reasoning, objectivity, and evidence, and in turn, how such variations might affect feminist political practices. (Prerequisites: Two courses at the 2000-level or above from Women''s and Gender Studies and/or consent of the instructor.) (Also offered as Women''s and Gender Studies WGST-3590.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (415, N' Argumentation Theory', N'3600', N'PHIL', N'Topics may include: the nature and uses of argument; the evaluation of argument; arguments and argumentation; the relations between argument and rhetoric, logic, and pragmatics; linguistic theories of argument; ethics and epistemology related to argument; the role of argument in philosophy. (Prerequisite: PHIL-2600 or PHIL-2610, or consent of the instructor.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (416, N' Philosophy of the Enlightenment', N'3700', N'PHIL', N'The Enlightenment ushers in a new era in modern philosophy whose tenets are the autonomy of reason in the face of prejudice, individual dignity as the foundation for social justice, moral progress through human perfectibility, and the scientific explanation of the world of nature. This course explores the emergence and development of these ideas in the work of prominent representatives of the Scottish, French and German Enlightenment such as Hume, Smith, Reid, Rousseau, Voltaire, Diderot, D’Alembert, Lessing, Kant, Wollstonecraft, and Herder. (At least third semester standing, and one philosophy course with a middle digit of seven, or permission of the instructor.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (417, N' Kant', N'3760', N'PHIL', N'A study of the critical philosophical writings of Immanuel Kant. Topics may include Kant''s theories about: the limits of human knowledge, how knowledge in mathematics and the natural sciences is possible, whether it is possible to have moral knowledge, whether it is possible to have religious knowledge. (Prerequisite: PHIL-2760, or consent of the instructor.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (418, N' Nineteenth Century Philosophy', N'3780', N'PHIL', N'Various nineteenth century thinkers may be studied in this course including Kierkegaard and Nietzsche, but also Dilthey, Schopenhauer, Comte, Mill, and others. (Prerequisite: PHIL-1100 or PHIL-1120, or at least one 2000-level Philosophy course, or consent of the instructor.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (419, N' Senior Seminars', N'4000', N'PHIL', N'Senior seminars are the undergraduate sections of M.A. courses. (Normally open only to Philosophy majors in the final year of their program. Consent of the instructor is required. Consult a program advisor during the term preceding planned registration.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (420, N' Legal Philosophy', N'4260', N'PHIL', N'The objective of this course is to introduce the student to contemporary issues concerning the philosophy of law. Particular emphasis will be placed upon the presupposed relation of concepts to society. The course will examine such issues as the difference and relation of legitimacy to legality, the relation of legal analysis to social needs, the relation of morality and ethicality to the content of legal rules and legal reasoning (Pre-requisite: Final Year of Honour’s B.A or M.A. Philosophy students only.) (Cross-listed with PHIL-8260)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (421, N' Recent German Philosophy', N'4700', N'PHIL', N'Significant developments in German philosophy in the twentieth century will be examined. Portions of the course may be devoted to Husserl (the founder of the phenomenological school), Heidegger (a seminal figure in existentialism), Gadamer (a key figure in the development of hermeneutics), Critical Theory (a Freudian and Marxist approach to social and economic issues), and second-generation critical theorists such as Habermas. (Prerequisite: PHIL-1120, and one 2000-level or above Philosophy course, and semester 5 or above standing, or permission of instructor.) (Cross-listed with PHIL-8700.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (422, N' Recent French Philosophy', N'4710', N'PHIL', N'A study of significant developments in recent French thought as found in Bergson, Sartre, Merleau-Ponty, Bataille, and Levi-Strauss, for example. (Prerequisite: PHIL-1100 or PHIL-1120, at least one 2000-level or above Philosophy course, and semester 5 or above standing, or consent of the instructor.) (Cross-listed with PHIL-8710.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (423, N' Analytic Philosophy', N'4720', N'PHIL', N'Advanced study of themes and trends in analytic philosophy, in which logic, language, and scientific evidence play central roles. (Prerequisite: PHIL-1100, or PHIL-1120, and at least one 2000-level or above Philosophy course, and semester 5 or above standing, or permission of instructor.) (Cross-listed with PHIL-8720.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (424, N' Pragmatist Philosophy', N'4730', N'PHIL', N'Advanced study of some of the major themes or figures in pragmatist philosophy, which takes practice, in a broad sense of the term, to have a central role in most if not all philosophical issues. (Prerequisite: PHIL-1100 or PHIL-1120, and at least one 2000-level or above Philosophy course, and semester 5 or above standing, or permission of the instructor.) (Cross-listed with PHIL-8730.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (425, N' Advanced Studies in Ancient or Early Modern Philosophy', N'4760', N'PHIL', N'An in-depth investigation of a philosopher, text, or movement from either the Ancient or Early Modern period. Topics may include individual dialogues or texts of figures such as Plato, Aristotle, Seneca, Descartes or Hume. Or movements such as the Greek Sophists, Stoics, or British Empiricists. (Prerequisite: PHIL-2730).', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (426, N' Hegel and German Idealism', N'4770', N'PHIL', N'A study of early 19th century philosophy centered on the idealism of G.W.F. Hegel, focusing on such problems as the nature of the dialectic, the notion of absolute spirit, and the Hegelian conception of philosophy. (Prerequisite: PHIL-3760, or consent of the instructor.)', 3, 8, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (427, N'	Introduction to Canadian Government and Politics', N'1000', N'POLS', N'An introduction to the politics and government of Canada. The course will focus on political culture, the constitution, federalism, the executive, parliament, public service, courts, political parties, interest groups, and elections. (3 lecture hours or 2 lecture hours, 1 tutorial hour a week.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (428, N' Comparative Politics in a Changing World', N'1300', N'POLS', N'Introduces students to issues such as democracy, authoritarianism, nationalism, political culture, and how political power is organized. The course focuses on the democratic states of the West, but also examines non-democratic states such as China and the transitional democracies of Eastern Europe. (3 lecture hours or 2 lecture hours, 1 tutorial hour a week.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (429, N' Introduction to International Relations', N'1600', N'POLS', N'An examination of competing perspectives on international relations and of such critical themes as power, security, war, imperialism, nationalism, interdependence, development and underdevelopment, human rights, environmental concerns, and the quest for a new world order. (3 lecture hours or 2 lecture hours, 1 tutorial hour a week.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (430, N' Introduction to Diaspora Studies: There''s No Place Like Home', N'1709', N'POLS', N'This course introduces students to diasporas-scattered populations living in exile from their ancestral homelands. The course focuses on the significance of migration, exile, belonging, and nostalgia (for ancestral homelands) for diasporas throughout the world. Students submit projects (incorporating oral histories, for example) on the diaspora of their choice. (Also offered as Diaspora Studies DIAS-1700 and Languages, Literatures and Cultures JWST-1700.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (431, N' Current Issues in Canadian Politics', N'2010', N'POLS', N'An examination of one or more current issues in Canadian politics, for example, energy and resources, the environment, native peoples, aging, women''s rights, urban problems, and health care. (Prerequisite: one of POLS-1000 or POLS-1300.).', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (432, N' Quebec Politics and Society [French]', N'2035', N'POLS', N'This course, which is taught in French, introduces students to political life in the province of Quebec, with a focus on the structure and functions of governing institutions, political culture and ideology, and the origins of key political traditions and practices. Topics may include the origins and evolution of Québécois nationalism, the unique position of Quebec in Canadian federalism, provincial policy initiatives to protect and extend the French language in the public sphere, and external relations with Canada and the international community.', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (433, N' Issues in Quebec Politics [French]', N'2045', N'POLS', N'This course, which is taught in French, examines major historical and contemporary political issues in the province of Quebec. Topics may include the patriation of the Canadian constitution, the Quiet Revolution, the FLQ crisis, referenda on Quebec sovereignty, and current public policy issues such as immigration and the environment.', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (434, N' Contemporary Canadian Political Issues [French]', N'2055', N'POLS', N'This course, which is taught in French, examines contemporary issues in Canadian politics. Topics may include, for example, constitutional change, Aboriginal peoples, demographic shifts, the environment, health care and immigration.', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (435, N' Women and Politics', N'2110', N'POLS', N'An introduction to the principal themes in the study of women in Canadian politics. Topics may include: feminist theory, women in Canadian political institutions, the status of women in the Canadian economy, and gender equality rights in the Charter. (Also offered as Women''s and Gender Studies WGST-2110).', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (436, N' Environmental Policy and Politics', N'2120', N'POLS', N'The course examines the domestic and international context of environmental policy-making in Canada. Topics examined may include global warming, Great Lakes pollution, and endangered species.', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (437, N' Public Opinion, Mass Media and Canadian Democracy', N'2130', N'POLS', N'An evaluation of the relationship between public opinion and democratic politics, how opinions, beliefs and attitudes are shaped by the family, educational system, peer groups, and in particular, mass media. Particular attention will be devoted to the role of the mass media in influencing public opinion and public policy.', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (438, N' Legal Process in Canada', N'2140', N'POLS', N'An introduction to the legal system in Canada, including the sources of Canadian law, the general concepts of constitutional and administrative law, the court structure, and the study and practice of law. (Prerequisite: one of POLS-1000, POLS-1300, or POLS-2120)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (439, N' Introduction to Public Administration', N'2200', N'POLS', N'This course introduces students to the political science sub-field of public administration. Building on classical theories of organization, it examines the institutions of government, the dynamics of public sector management, and the relationship between elected officials and administrators in the political system. (Prerequisite: on of POLS-1000 or POLS-2120, consent of instructor.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (440, N' Canadian Public Administration and Policy', N'2210', N'POLS', N'An introduction to the processes of public policy formation in Canada. Includes an analysis of political/bureaucratic relationships, decision making theory, and the role of interest groups in the context of selected contemporary policy issues. (Prerequisite: POLS-2200 or consent of instructor.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (441, N' Space, Place, and Scale: Foundations of Human Geography', N'2300', N'POLS', N'An introduction to foundational concepts and approaches in the study of human geography, emphasizing the way social, political, economic, and environmental systems shape and are shaped by patterns of geographic and spatial organization.', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (442, N' Government and Politics of the United States', N'2320', N'POLS', N'The organization and structure of national government in the United States, with emphasis upon congressional/executive relationships, political parties, and the electoral process.', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (443, N' Politics of the Developing World', N'2330', N'POLS', N'An examination of the politics of developing areas, with a focus on economic and political development, ethnic conflict and the role of overseas development assistance in building government institutions. In given years, emphasis may be on Africa, Asia or Latin America and the Caribbean.', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (444, N' Government and Politics in the Middle East', N'2350', N'POLS', N'The course provides an overview of the politics of the contemporary Middle East. Particular attention will be paid to state and regime formation, the legitimacy of Middle Eastern governments, state society interaction, the nature of the opposition, and prospects for democratization and improvements to human rights.', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (445, N' Contemporary African Politics', N'2410', N'POLS', N'Characteristic domestic and international problems of African states south of the Sahara, including resource scarcity, ethnic diversity, political stability, and relations with the Great Powers.', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (446, N' Government and Politics in Europe', N'2440', N'POLS', N'Examines contemporary issues such as democratic development, nationalism, and regionalism, immigration and racism, the status of women, social welfare programs, and the consolidation and expansion of the European Union.', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (447, N' Contemporary Issues in International Relations', N'2450', N'POLS', N'This class will focus on some of the key contemporary issues in international relations. Students are expected to have some background knowledge of world politics, particularly developments in the last few decades. Issues to be examined may include economic globalization and its effects, the future of the state system, capitalism (and its challengers) as a model of economic production, development assistance, the US role in global politics, Iran’s nuclear program, and climate change. [The pre-requisite for the class is POLS-1600 (Introduction to International Relations].', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (448, N' The Political Economy of Mass Media', N'2480', N'POLS', N'This course will explore the role of media as a political force in democratic societies. Corporate media’s role in politics and governance is the starting point for looking at what role media play in North American political culture. Communication policy, the role of public relations and advertising, and the exercise of power among the media. political realm and the general public are examined.', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (449, N' Political Economy of Agriculture and Food', N'2490', N'POLS', N'Critical examination of shifts in the political economy of agriculture and food, focusing on political, economic, social, and environmental changes occurring in and affecting agro-food production and consumption systems.', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (450, N' Classical Political Thought', N'2510', N'POLS', N'(Prerequisites: POLS-1000 and one of POLS-1300 or POLS-1600.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (451, N' Modern Political Thought', N'2520', N'POLS', N'Toward the new science of politics from Machiavelli to Rousseau and the French Revolution.', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (452, N' The Politics of Music', N'2550', N'POLS', N'This course will explore the relationship between music and politics. Historical perspectives and critical popular culture theory will inform a look at the rise of politically themed music, how the music industry deals with political themes in music, music and social movements, and music as hate politics, music and patriotism.', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (453, N' Politics, History, and Asian Religions', N'2600', N'POLS', N'An introduction to Hinduism, Sikhism, Jainism, Buddhism, Taoism, Confucianism, and Shinto, with attention to their role in history and politics.', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (454, N' Politics, History, and Western Religions', N'2610', N'POLS', N'An introduction to Judaism, Zoroastrianism, Christianity, Islam, and BaHai, with attention to their role in history and politics.', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (455, N' Introduction to Canadian Foreign Policy', N'2640', N'POLS', N'An overview of the formulation and trends of Canadian foreign policy from World War I to the present, together with an examination of the domestic and external determinants of Canadian foreign policy and of the foreign policy making process. (Prerequisite: POLS-1000, or POLS-1600, or consent of instructor.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (456, N' Strategic Studies', N'2670', N'POLS', N'An examination of the theories, tools, and concepts that explain war and how international violence can be used for political ends. The focus will be primarily on the modern state system, especially on the post-WWII environment. Among topics to be addressed are theories of war, deterrence, arms control, the "democratic peace" thesis, and proliferation of weapons of mass destruction. (Prerequisite: POLS-1600 or consent of the instructor.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (457, N' International Organizations', N'2680', N'POLS', N'An introduction to the problems and possibilities of international co-operation and global governance among states and non-state actors. The course will provide a survey of various international organizations. These may include the United Nations, NATO, the European Union, and NAFTA. The role of non-governmental organizations in world politics is also examined. (Prerequisite: POLS-1600 or consent of instructor.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (458, N' Information Searching and Analysis', N'2700', N'POLS', N'This course is designed to help students become better at analyzing and critiquing information from a variety of sources. We will take a critical look at internet searching and learn to use those resources in more intelligent ways. The focus will be on researching a topic through both the internet and more traditional sources Students will learn how to assess the information contained in websites and how to assess a variety of things which appear as information.', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (459, N' Introduction to Research Methods', N'2750', N'POLS', N'Introduces students to quantitative and qualitative social research. Looks at how surveys and focus groups are used and abused for political and commercial purposes. Examines what field and archival research can teach us about human behaviour and social, political, and economic trends. (Prerequisite: SOSC-2500.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (460, N' Selected Topics: Current Political Issues', N'2880', N'POLS', N'Selected topics based on current political issues. Topics may vary from year to year. (May be repeated for credit if content changes.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (461, N' Canadian Provincial Government', N'3090', N'POLS', N'A comparative study of provincial governments and politics in Canada including an examination of the powers exercised by provinces, the institutions of provincial government and the behaviour of provincial electorates and politicians. In given terms, Ontario, Quebec, the Atlantic or the Western provinces may be given particular attention. (Prerequisite: POLS-1000 or consent of instructor.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (462, N' Constitutional Law and Politics in Canada', N'3140', N'POLS', N'The nature and purpose of constitution and major issues in Canadian constitutional politics. Topics may include judicial review, the development of human rights law in Canada, and the impact of the Charter of Rights on Canadian politics and government. (Prerequisite: POLS-2140 or consent of instructor.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (463, N' Political Parties and Elections', N'3200', N'POLS', N'An analysis of the development and functions of parties and of the social, psychological, and political influences on voting in Canada.', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (464, N' The Legislative Process', N'3210', N'POLS', N'An introduction to representative democracy, parliamentary behaviour, and legislative process. May include role-playing exercises and a simulation of the Federal House of Commons.', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (465, N' Government and Business', N'3230', N'POLS', N'An introduction to the intricate relationship of government to business. Included are: government services to business; business and policy development; and regulation and/or de-regulation and regulatory agencies. (Prerequisite: POLS-1000 or consent of instructor.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (466, N' Public Infrastructure', N'3240', N'POLS', N'This course examines the role of the public sector in the ownership and operation of major capital facilities for transportation, water, sanitation, electric power, health care and education. Topics include alternative funding mechanisms, environmental impacts and regulations, public consultation and the influence of political interest groups, and the role of infrastructure in the economy. The course also provides an introduction to analytical methods used to support infrastructure decision-making.', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (467, N' Local Government', N'3260', N'POLS', N'An introduction to the politics and administration of local government. Topics include local political structure, relationships between municipalities and other levels of government, public policy-making, and current challenges facing local officials. (Prerequisite: POLS-1000)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (468, N'Political Geography', N'3350', N'POLS', N'A systematic examination of the relationship between politics, power, and geography, with focus on the political meanings, uses, and representations of geographic space, and the significance of geography for understanding the organization and exercise of political power at local, regional, and global scales. (prerequisites: POLS-2300).', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (469, N'Asian Government and Politics', N'3460', N'POLS', N'Comparative analysis of institutions and political processes of Southeast Asia, South Asia, China, or Japan.', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (470, N' Topics in Political Thought', N'3519', N'POLS', N'The study of selected topics in political thought and theory. Students are recommended to take POLS-2510 or POLS-2520 before taking this course.', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (471, N'Political Problems of Economic Development', N'3540', N'POLS', N'The course takes a comprehensive approach to the study of economic development, drawing connections between theory (including development paradigms) and practice. In given years, the focus may be on South-East Asia, Central Asia, Africa, China, Eastern Europe, or Latin America. (Prerequisite: POLS-1600 or consent of instructor.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (472, N'Political Economy of International Trade', N'3550', N'POLS', N'An examination of the most prevalent dilemmas facing the global trading system. The main focus is on the World Trade Organization and its global agreements on goods, agriculture, services and intellectual property. Additional topics include regional trade blocs, international trade in money, foreign direct investment, and environmental and labour issues. (Prerequisite: POLS-1600 or consent of instructor.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (473, N'Theories of International Political Economy', N'3560', N'POLS', N'An examination of the major theoretical perspectives in the field of international political economy. This course will cover both classical and modern theories, including mercantilism, liberalism, Marxism, feminism and post-modernism. (Prerequisite: POLS-1600 or consent of Instructor.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (474, N'International Conflict and Its Resolution', N'3600', N'POLS', N'The nature of conflict and how it is managed in the international community; explicit and tacit bargaining strategies and techniques of conflict resolution. (Prerequisite: POLS-1600 or consent of instructor.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (475, N' U.S. Foreign Policy', N'3610', N'POLS', N'The United States policy-making process and the substance of policy in relation to the former communist world, developing countries, and allies such as Canada and Western Europe.', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (476, N'Principles of International Law', N'3630', N'POLS', N'An introduction to the role of international law in international relations, this course will consider the role of justice in the international system and will examine the basic principles of modern international law, including sources, subjects, and procedures. (Prerequisite: POLS-1600 or consent of instructor.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (477, N' The Middle East in International Relations', N'3650', N'POLS', N'The nature and causes of the various conflicts in the region, the role of outside powers and international organizations, and the prospects for conflict resolution. (Prerequisite: POLS-1600 or consent of instructor.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (478, N'The Politics of the European Union', N'3670', N'POLS', N'Examines the development and operation of the European Union. Key issues on the agenda of the European Union are examined, including: the introduction of the single currency; the development of a common foreign and security policy; and the possible enlargement of the European Union. The extent to which the European Union challenges existing state structures is examined throughout the course.', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (479, N'Religious Fundamentalism and Politics', N'3720', N'POLS', N'A study of the modern concept of religious fundamentalism, with case studies of the interface of fundamentalism and politics in North America, Europe, Africa and Asia.', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (480, N' Promotional Culture and Democracy', N'3780', N'POLS', N'This course focuses on the role promotional culture plays in democratic processes. Moving beyond classical conceptions of propaganda, promotional culture incorporates a range of tactics and strategies used to persuade citizens or sell to consumers. While political and issue advertising play increasingly large roles, the interplay between journalism and promotional culture will also be a nexus of concern for the course. (Prerequisite: POLS-1000)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (481, N' Politics and Culture', N'3790', N'POLS', N'An examination of political themes as reflected in different forms of popular culture, including cinema and the media. Topics may include: war and cinema, and how commercial interests are portrayed in mass media. While the course may focus on different forms of cultural expression, the emphasis will be on understanding and evaluating how politically relevant themes are influenced and shaped for the mass public.', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (482, N' Public Service Management Internship Seminar', N'3920', N'POLS', N'A critical examination of selected theories and concepts applicable to research and management practices in the public sector. (Admission by consent of a program advisor.) (Must be taken concurrently with POLS-4920.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (483, N'Practicum in Government and Politics', N'3990', N'POLS', N'Practical work in the office of an elected or appointed official, with oral and written reports to the supervising faculty member. (To be taken only with permission of instructor and a program advisor in Political Science.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (484, N'Canadian Politics: Participation and Processes', N'4110', N'POLS', N'A review of current literature on topics that may include parties, elections, voting behaviour, pressure groups, representation, new social movements, public opinion, and ideologies. (Restricted to Semester 7 and 8 Political Science majors.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (485, N'Canadian Federalism', N'4120', N'POLS', N'A review of current literature and development on such topics as federalism, intergovernmental relations, and the role of Quebec. (Restricted to Semester 7 and 8 Political Science majors.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (486, N'Seminar in Canadian Public Policy', N'4210', N'POLS', N'A detailed analysis of the Canadian public policy process. (Restricted to Semester 7 and 8 Political Science majors and Semester 7 and 8 International Relations majors.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (487, N'Seminar in Public Policy Analysis', N'4220', N'POLS', N'A survey of the evaluative side of public policy including formulation, adoption, program operations and evaluation techniques.(Restricted to Semester 7 and 8 Political Science majors.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (488, N'Seminar in Comparative Politics', N'4310', N'POLS', N'A comparative examination of national political systems emphasizing areas such as political culture, political parties, elites, and interest groups. In given terms, the focus may be on industrialized or developing countries. (Restricted to Semester 7 and 8 Political Science majors and Semester 7 and 8 International Relations majors.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (489, N' Seminar in Politics of the United States', N'4340', N'POLS', N'An analysis of selected topics in United States politics and government. May include an examination of foreign perspectives on U.S. politics, Canadian-U.S. relations, parties and elections, civil liberties and civil rights, or other important topics in United States politics. (Restricted to Semester 7 and 8 Political Science majors.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (490, N' Remaking North America: Geographic Perspectives on US-Canada Politics', N'4400', N'POLS', N'The political geography of the United States and Canada, in regional and global context, focusing on the political, cultural, and economic factors and processes shaping the two countries’ internal political character, external relations, and bilateral relationship. Topics covered may include regional political cultures in the US and Canada, political and economic integration under NAFTA, American and Canadian electoral geographies, comparative analysis of immigration policy, and the meaning of the US/Canada border in the context of globalization. (Restricted to Semester 7 and 8 Political Science majors and Semester 7 and 8 International Relations majors. Students are recommended to take POLS-2300 before taking this course).', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (491, N' The New Scramble for African Resources', N'4410', N'POLS', N'This course examines the evolving political and economic relationships between countries in Sub-Saharan Africa and the rest of the world. As Africa moves from a “forgotten” continent to a “rising” one in the eyes of foreign policy analysts and the global business community, this course re-examines Africa''s role in international relations and international security. Through the lens of the "new scramble for Africa", the course will examine contemporary issues such as land, oil and minerals before focusing specifically on case studies of Africa''s changing relations with the world. (Prerequisites: POLS-1600, POLS-2410 or consent of instructor. Restricted to Semester 7 and 8 Political Science majors and Semester 7 and 8 International Relations majors.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (492, N' Politics and Security in Russia and Eurasia', N'4420', N'POLS', N'This course examines the politics of and security issues facing the Russian Federation, the five Central Asian Republics of Kazakhstan, Kyrgyzstan, Tajikistan, Turkmenistan and Uzbekistan, and the Caucasian Republics of Georgia, Armenia and Azerbaijan. For the purposes of this course, the role and influence of Turkey, Iran, China and the United States on the politics and security of these Eurasian states will also be considered. The approach taken in this course is inter-disciplinary, drawing heavily from the fields of political science, history, and economics. The students will be introduced to political, economic and social issues affecting the region and taught how to research these issues through the lens of historical analysis and political economy. (Restricted to Semester 7 and 8 Political Science majors and Semester 7 and 8 International Relations majors.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (493, N' Collective Action and Contentious Politics', N'4430', N'POLS', N'This course explores the conditions and processes shaping protests, riots, revolutions, and other forms of contentious politics. The course examines major theories about revolutions and social movements and considers competing explanations for the emergence of collective action. Particular attention is paid to the conditions under which people do or do not rebel and various aspects of the strategic interaction between social movements and states, and the determinants of movement outcomes. The students will read theoretical works and review several cases of revolutions and social movements in both democratic and nondemocratic state settings, thinking through parallels and differences among them. (Restricted to Semester 7 and 8 Political Science majors and Semester 7 and 8 International Relations majors.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (494, N'Seminar in Contemporary Political Theory', N'4510', N'POLS', N'An examination of selected topics in political theory, with special emphasis on the literature of the twentieth century. (Restricted to Semester 7 and 8 Political Science majors and Semester 7 and 8 International Relations majors.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (495, N'Seminar in Theories of International Relations', N'4610', N'POLS', N'A survey of competing perspectives and approaches employed in the contemporary study of international relations. (Restricted to Semester 7 and 8 International Relations majors)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (496, N'Interdisciplinary Approaches to Research in International Relations', N'4620', N'POLS', N'A research oriented seminar that will encourage the use of interdisciplinary perspectives in the examination of selected problems in international relations.(Restricted to Semester 7 and 8 International Relations majors.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (497, N' International Political Economy', N'4640', N'POLS', N'An overview of the major theoretical perspectives and issues in international political economy. Issues addressed may include: international trade, foreign investment and multinational corporations, international monetary institutions, and crisis and change in the international system. (Restricted to Semester 7 and 8 Political Science majors and Semester 7 and 8 International Relations majors.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (498, N'Seminar in Globalization', N'4650', N'POLS', N'A critical overview of major theories, debates, and case studies related to the politics of globalization. Specific topics addressed may include changes in the nature of state sovereignty, the creation and regulation of a global economy, and cultural change and resistance. (Restricted to Semester 7 and 8 Political Science majors and Semester 7 and 8 International Relations majors. Students are recommended to take POLS-2300 before taking this course).', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (499, N'Selected Topics in Political Science', N'4880', N'POLS', N'Topics of current interest which may vary from year to year. (May be repeated for credit with the permission of a program advisor in Political Science.)(Restricted to Semester 7 and 8 Political Science majors and Semester 7 and 8 International Relations majors.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (500, N'Public Service Management Internship Practicum', N'4920', N'POLS', N'Supervised work experience in a public service management environment. (Admission only by consent of program advisor.) (Offered on a Pass/Non-pass basis only.) (Must be taken concurrently with POLS-3920.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (501, N' Advanced Topics in Canadian Foreign Policy', N'4950', N'POLS', N'This seminar will focus on issues that are driving the contemporary Canadian foreign policy agenda. Members of the seminar will read and discuss recent research on topics including Canada’s defence and security policy, trade and aid policies, environmental record, as well as more recent foreign policy initiatives. Students are expected to learn through active participation in the class. Students are also expected to have some background knowledge of both Canadian history and Canadian government and politics. The course is restricted to Semester 7 and 8 Political Science majors and Semester 7 and 8 International Relations majors. Students must have taken POLS-1600 (Introduction to International Relations) before taking this course. While not a pre-requisite, it is also recommended that students take POLS-2640 (Introduction to Canadian Foreign Policy) before this class, although the instructor will permit students into the course without it.', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (502, N' Advanced Topics in International Security', N'4960', N'POLS', N'This seminar will focus on issues that are driving the contemporary fields of strategic and security studies. Members of the seminar will read and discuss recent research on topics including Canadian and American defence and security policy, proliferation, the arms trade, energy security, and changes in military strategy. Students are expected to learn through active participation in the class. Students are also expected to have some background knowledge of global politics and some understanding of recent conflict. The course is restricted to Semester 7 and 8 Political Science majors and Semester 7 and 8 International Relations majors. [Students must have taken POLS-1600 (Introduction to International Relations) before taking this class. While not a pre-requisite, it is also recommended that students take POLS-2670 (Strategic Studies) before this class, although the instructor will permit students into the course without it.]', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (503, N' Political Science Thesis 1: Research Design', N'4970', N'POLS', N'This course provides students instruction and guidance in identifying, designing, and planning an original, independent research project, resulting in the development of a research proposal under the supervision of the course instructor and a faculty supervisor. The course includes both regular individual meetings with the faculty supervisor and seminar meetings with the course instructor to discuss issues related to research planning and design, including but not limited to developing a research question and methodology, constructing a literature review, conducting research ethically, and collecting, organizing, and analyzing data. (Prerequisites: POLS-2750 and Semester 7 or 8 standing in a Political Science or International Relations program; a cumulative average of at least 80%; a major average of at least 80%; and consent of the course instructor.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (504, N' Political Science Thesis 2: Writing and Presentation', N'4980', N'POLS', N'This course builds on the research design students develop in POLS-4970. Students complete an undergraduate thesis under individual faculty supervision, and present the results in written and oral formats. (Prerequisite: POLS-4970 and consent of the course instructor.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (505, N'Directed Reading in an Approved Special Field', N'4990', N'POLS', N'(May be repeated for credit if content changes.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (506, N' Introduction to Customs Compliance', N'2000', N'CBMI', N'This course equips the learner with an overview of evolving customs procedures and regulations applicable to the Canada Customs Act, Customs Tariff Act and Export/Import Permits Act and the necessary skills to inquire into, and stay abreast of, current developments and regulations with respect to import, export and reporting customs clearances. Transactions and accounts based customs procedures are introduced and Canadian procedures are compared with those of other countries. The course provides learners with a basic understanding of goods classification under the Harmonized System Code and knowledge of how to complete the CBSA customs documents while considering multiple perspectives: broker, client, government and other Government of Canada agencies that regulate international trade.', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (507, N' The Global Business Environment and Intercultural Aspects of Integrative Trade', N'3000', N'CBMI', N'The aim of this course is to provide each learner with an overview of the role of border agencies and the principles of border management. This course raises participants’ awareness of the intricacies involved in the communication process with various government agencies, border officials, and business partners and equips participants with specific techniques for effective communication in a variety of business situations. Attentiveness to the ethical dimension of business in a global setting is cultivated within each learner and decision-making models for resolving ethical dilemmas are utilized. The integrative trade approach is introduced within a cross-border and international trade environment and intercultural aspects of such trade are considered. In particular, learners focus on recognizing, understanding, and respecting differences in trading practices due to cultural influences. (This is a four-week course. 9 lecture hours/week) (Graded on a pass/fail basis - percentage grade optional.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (508, N' International Trade Law, Policy and the Political Environment', N'3100', N'CBMI', N'This course focuses on how to use the border, by discussing practical implications of cross-border trade from various stakeholders’ perspectives, including government, business professionals, and entrepreneurs. This course provides learners with a contextual understanding of policy and the political environment in international trade while exploring practical implications for border users. Topics explored include existing legal mechanisms that govern international trade, including corporate law, tax law, and trade compliance practice s. Emphasis is placed on understanding the risks associated with integrative trade and developing contingency strategies to protect organizations. The course engages learners on an individual basis to develop and share their diverse perspectives. Learners will benefit from an array of guest speakers from the Government of Canada, stressing the importance of the tools, access, and support needed to reach and succeed in global markets. (This is a four-week course. 9 lecture hours/week) (Graded on a pass/fail basis - percentage grade optional.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (509, N' Supply Chain and Border Management', N'3150', N'CBMI', N'In this course, participants will gain an in-depth understanding of customs procedures and services offered by freight forwarders and customs brokers. This course aims to help learners manage the relationships associated with multiple directional flows of goods and services in a complex, global system. Learners increase their understanding of existing, and evolving, rules and regulations that govern international trade to ensure compliance for the international import/export of goods and services. Learners also develop technical and systems knowledge pertaining to transportation modes, distribution, and logistics. (This is a four-week course. 9 lecture hours/week) (Graded on a pass/fail basis - percentage grade optional.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (510, N' International Business Development, Operations and Planning', N'3200', N'CBMI', N'In this course, learners gain essential knowledge and skills to expand a business internationally. Key considerations and activities for international business are explored alongside cross-border opportunities and challenges. Correspondingly, learners develop core competencies in business planning, understanding the roles of government agencies, managing strategic alliances, working with multinational workforces, and identifying different sources of funding. Relationships between funding initiatives, government policy, and an organization’s objectives are examined. Course participants utilize tools to develop growth-focused business and entrepreneurial strategies that reflect a dynamic understanding of rules and regulations that govern international trade – equipping the manager/entrepreneur with the ability to tackle international business growth in accordance with trade compliance for effective and seamless flow of cross-border goods and services. (This is a four-week course. 9 lecture hours/week) (Graded on a pass/fail basis - percentage grade optional.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (511, N' International Investment and Growth Strategy', N'3250', N'CBMI', N'This course equips learners with the tools to identify and evaluate market entry options against a company’s objectives and abilities. Participants learn how to perform a company strategic-needs assessment and determine potential barriers to market entry. Barriers such as regulatory, legal, political, environmental, and cultural vary by case; in turn, a custom, multi-faceted market entry strategy needs to be designed for each case, inclusive of these considerations. The role of free trade agreements and foreign investment promotion are explored to enhance competition across a range of sectors. Participants learn how to identify potential international partners to help bring a company into foreign markets. Finally, participants learn how to negotiate global partnership agreements, and monitor their compliance, to ensure mutual benefit between parties. (This is a four-week course. 9 lecture hours/week) (Graded on a pass/fail basis - percentage grade optional.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (512, N' International Trade Compliance', N'3300', N'CBMI', N'This course equips learners with various tools to plan, and carry out, trade research effectively and efficiently. Research addresses evolving rules and regulations that govern international trade to poise the learner with the necessary skills to inquire into, and stay abreast of, current developments and regulation. To ensure compliance related to the import/export activities of goods and services and, at the same time promote an authentic international business objective, participants learn to use market intelligence to identify the most promising foreign markets and international business opportunities. (This is a four-week course. 9 lecture hours/week) (Graded on a pass/fail basis - percentage grade optional.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (513, N' International Risk Management and Trade Finance', N'3350', N'CBMI', N'This course covers the nature and scope of international trade finance. In particular, facilitating trade payment flows across a network of relationships is addressed to ensure effective trade activities. The course topics include the provision of information for a global trade transaction, commercial and country risks that may be encountered during import-export operations, and risk management and mitigation how to manage and mitigate these risks. Risk-mitigation techniques are discussed in terms of legal implications and appropriate practices and the use of these techniques. Participants develop a financial plan for a company’s short-, medium- and long-term needs, including essential data considerations for cash flow analysis. (This is a four-week course. 9 lecture hours/week) (Graded on a pass/fail basis - percentage grade optional.)', 3, 9, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (514, N' Positive Psychology', N'1070', N'PSYC', N'An introduction to theory and research pertaining to the study of positive psychology, the psychology of human strengths and coping resources. Selected topics include: happiness, living a meaningful and gratifying life, resilience, hardiness, emotional intelligence, optimism, hope, creativity and moral motivation.', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (515, N' Introduction to Psychology as a Behavioural Science', N'1150', N'PSYC', N'Introduction to selected areas in psychology including learning, perception, physiological psychology, emotion, and motivation.', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (516, N' Introduction to Psychology as a Social Science', N'1160', N'PSYC', N'Introduction to selected areas in psychology including developmental, social, personality, and clinical.', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (517, N' Everyday Conflicts and Their Resolution', N'2180', N'PSYC', N'Students design and practice techniques for resolving everyday conflicts with friends and co-workers effectively and respectfully, and without damaging interpersonal relationships. Students learn to focus on the problem, not the person; identify and respond to hidden agendas and subvert personal attacks. Pre-requisite: Semester 3 or above standing or permission of the instructor. (Also offered as SJST-2180, WORK-2180)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (518, N' Introduction to Adjustment and Personality', N'2200', N'PSYC', N'A survey of major approaches to the study of personality with a particular focus on the processes involved in adjustment.', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (519, N' Developmental Psychology: The Child', N'2230', N'PSYC', N'The study of normal child development from conception to puberty, including physical, cognitive, and social development within the child''s family, school, and cultural contexts. Specific topics include temperament, language development, intelligence testing, personality development, and parenting styles.', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (520, N' Developmental Psychology: Adolescence', N'2240', N'PSYC', N'The study of normal adolescent development from puberty to early adulthood. Topics include physical changes at puberty, cognitive and social development, and the impact of adolescent development within various contexts, including families, peer groups, and schools.', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (521, N' Developmental Psychology: Adulthood and Aging', N'2250', N'PSYC', N'The study of adult development including stages in adulthood, problems of aging, and issues related to death and dying.', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (522, N' Abnormal Psychology', N'2280', N'PSYC', N'This course is a survey of psychopathology, with a focus on the structure and application of the Diagnostic and Statistical Manual (DSM) of Mental Disorders. Content to be covered will include historical and contemporary theory and research regarding the etiology and progression of abnormality, including biological, psychological and socio-cultural understandings. Attention will also be given to critiques of classification schemes and diagnosis. Finally, implications for the treatment of specific disorders will be addressed. (Prerequisite: PSYC-1150 and PSYC-1160.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (523, N' Social Science Research Methods', N'2300', N'PSYC', N'Introduction to experimental and non-experimental research methods and designs commonly used in psychology and other social sciences (e.g., experiments, quasi-experiments, survey research, observational studies, content analysis). Includes conducting research exercises/projects dealing with social, personality, developmental, and/or educational issues, writing research reports, and consideration of research ethics. (2 lecture, 2 laboratory hours per week; limited enrolment.) (Antirequisites: KINE-2700.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (524, N' Introduction to Social Psychology', N'2360', N'PSYC', N'An introduction to the theories, methods, findings, and problems associated with the study of the individual in the social context. Topics include social cognition; interpersonal behaviour (attraction, aggression, altruism); social attitudes, prejudice, and discrimination; social influence and group processes (conformity, leadership, intergroup relations).(Prerequisites: PSYC-1150 and PSYC-1160.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (525, N' Psychology of Sex and Gender', N'2400', N'PSYC', N'Review of philosophical, historical, theoretical, and research literature in the psychology of sex and gender. Topics include male/female stereotypes; similarities/differences based on research data; and current social issues.', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (526, N' Introduction to the Brain and Human Behaviour', N'2560', N'PSYC', N'Reviews basic research relating brain and behaviour with a focus on human functioning. Includes the study of neuronal and synaptic activity and results from current research and case histories which link human behaviour to basic neuroanatomical and biochemical brain systems.', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (527, N' Tutorial', N'3100', N'PSYC', N'Individual projects in specific areas of psychology. (May be repeated once for credit.) (Prerequisites: PSYC-1150, PSYC-1160, and four additional courses in Psychology.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (528, N' Advanced Statistics', N'3130', N'PSYC', N'Required for students anticipating honours thesis projects in their fourth year. One-way, two-way, and higher order ANOVA, repeated measures ANOVA, multiple comparisons, correlation and univariate regression, introduction to multivariate regression and the general linear model, with an emphasis on computer data analysis. (Prerequisite: SOSC-2500.) (Credit can only be obtained for one of PSYC-3130 or SACR-3080.) (2 lecture hours, 1 laboratory hour a week.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (529, N' Tests and Measurement', N'3200', N'PSYC', N'An introduction to basic concepts of psychological testing, with a focus on test development, measurement, and test evaluation. Standard tests used to assess personality, achievement, and aptitudes will be surveyed. (Prerequisite: SOSC-2500.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (530, N' Child Psychopathology', N'3220', N'PSYC', N'An overview of theory and research related to the assessment, diagnosis, and treatment of childhood and adolescent disorders. Risk factors, vulnerability to stress, and protective factors will be addressed in relation to adjustment disorders, conduct disorder, depression, and anxiety in children and adolescents. (Prerequisite: PSYC-2230 or PSYC-2240.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (531, N' Developmental Disabilities', N'3230', N'PSYC', N'An overview of theory and research related to the biological foundation of childhood and adolescent developmental disabilities. Mental retardation, sensory and motor impairments, learning disabilities, and disorders with physical manifestations are included in the topics covered. (Prerequisite: PSYC-2230 or PSYC-2240.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (532, N' Educational Psychology', N'3240', N'PSYC', N'Psychology of the learning process and the variables that affect learning such as intelligence, motivation, attitudes, interpersonal relations, and cultural background. (Prerequisite: PSYC-2230 or PSYC-2240.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (533, N' Psychological Perspectives on Parenting', N'3270', N'PSYC', N'Contemporary theories and practices of parenting throughout the life cycle, with an emphasis on the psychological aspects of the family system. (Prerequisite: PSYC-2230 or PSYC-2240.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (534, N' Personality Theory and Research', N'3300', N'PSYC', N'Survey of personality theories and relevant research. Theories may include psychoanalytic, trait, behavioural, humanistic, cognitive, and biological. Some research topics relevant to personality theory will be outlined and illustrated with reference to selected content areas of personality.', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (535, N' Conducting Research in Psychology', N'3310', N'PSYC', N'Required for students anticipating honours thesis projects in their fourth year. Topics include: how to generate research ideas, philosophy and place of research in psychology, utilizing research advisors and supervision, using library resources, writing research reports, ethical issues in student research, planning effective research designs, dealing with participant recruitment and data collection, locating research measures, making data analysis decisions, using statistical packages such as SPSS, disseminating research ideas and findings. (Prerequisites: SOSC-2500, or PSYC-2300; and consent of the Undergraduate Program Chair.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (536, N'PSYC-3330 Introduction to Clinical Psychology', N'3330', N'PSYC', N'This course surveys topics and issues in the field of clinical psychology, including biopsychosocial theories of functioning and dysfunction. Emphasis is placed on major approaches to assessment and treatment (e.g.humanistic, cognitive-behavioral, psychodynamic). The course also will cover the scientific basis for clinical psychology, as well as historical, ethical, professional, cultural and legal issues. Sub-specialties and contemporary issues are also addressed. (Prerequisite: PSYC-2280 or PSYC-3220)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (537, N' Applied Social Psychology', N'3340', N'PSYC', N'The application of social psychology to solving social issues. Topics include improving job satisfaction and organizational life, promoting community health, meeting social welfare needs, dealing with environmental problems, improving educational systems, and addressing the issues of social justice and equality. The course may involve a fieldwork component. (Prerequisite: PSYC-2360.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (538, N' Human Sensation and Perception', N'3350', N'PSYC', N'The study of underlying mechanisms and processes of human sensation and perception, methods of measuring human sensory abilities, and the perceptual processes for integrating and interpreting such information. (Prerequisite: PSYC-2300) (2 lecture, 2 laboratory hours per week.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (539, N' Human Cognitive Neuroscience', N'3370', N'PSYC', N'Issues relevant to the brain and human behaviour. Recent research selected from areas of developmental, cognitive, and clinical neuropsychology. Introduction to cognitive neuroscience modelling. (Prerequisites: PSYC-1150, PSYC-1160, either PSYC-2560 or BIOL-2040 and three additional courses in Psychology.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (540, N' Health Psychology', N'3390', N'PSYC', N'Application of psychology to the areas of health promotion, prevention and treatment, and improvement of health-care delivery. Theory, research, and practice in health psychology and behavioural medicine will be examined. Specific areas of emphasis may include stress, illness, and coping; patient-practitioner interaction; adjustment to chronic illness; reproductive health issues; and cross-cultural conceptions of illness and healing. (Prerequisite: PSYC-2360.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (541, N' Culture and Psychology', N'3420', N'PSYC', N'This course examines psychological theory and research on the commonalities and variations in human behaviour within and across cultures. Topics may include individualism and collectivism, perspectives on the self, communication and interpersonal relations, intergroup relations, organizational behaviour, and the consequences of cross-cultural contact. (Prerequisite: PSYC-2360.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (542, N' Practical Strategies for Social Change: Intervening to Prevent Sexual Violence', N'3500', N'PSYC', N'This course introduces students to sexual violence as a social problem why it matters, the forms it takes, and how it can be changed. The importance of personal and community responsibility for social change is emphasized. This course also provides students with the background knowledge that is needed to successfully teach sexual violence prevention workshops for their peers.Restricted to students who have attained a cumulative GPA of 66% or higher at the time of application. (Prerequisite: Semester 4 standing or above and permission of the instructor by online application at bystander initiative.ca) (Also offered as SOSC-3500, SARC-3500, SWRK-3500, and WGST-3500.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (543, N' Learning and Behaviour', N'3530', N'PSYC', N'Basic principles and theories of classical and operant conditioning and their application to human behaviour. (Prerequisite:PSYC-2300.) (2 lecture, 2 laboratory hours per week.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (544, N' Comparative Psychology', N'3550', N'PSYC', N'Introduction to animal behaviour from the viewpoint of its role in the natural life of the individual and the species. A synthesis of contributions by comparative psychologists, ethologists, ecologists, and zoologists. (Prerequisites: PSYC-1150, PSYC-1160, and four additional courses in Psychology.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (545, N' Cognitive Processes', N'3580', N'PSYC', N'A review of current contributions to the understanding of attentional, memory, problem solving, and reasoning processes. (Prerequisite: PSYC-2300) (3 lecture, 1 laboratory hours per week.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (546, N' Industrial-Organizational Psychology', N'3700', N'PSYC', N'The study of employees, workplaces, and organizations. Topics include job analysis and competency models, recruitment, selection, and decision making, performance management, training, group and team processes in organizations, employee attitudes, affect, and behaviour, motivation, leadership, productive and counterproductive work behaviour. (Prerequisites: PSYC-1150 and PSYC-1160; or Labour Studies students with at least Semester 4 standing; or consent of instructor.) (Also offered as WORK-3700.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (547, N' Selected Topics in Psychology', N'4000', N'PSYC', N'Seminar on a selected topic in psychology. Content will vary with instructor. (Prerequisites: PSYC-1150, PSYC-1160, four additional courses in Psychology, including courses as specified by the instructor. ) (May be repeated for credit if content changes.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (548, N' History and Systems of Psychology', N'4150', N'PSYC', N'The emergence and development of psychology as a science. A review and evaluation of major systems of psychology, such as structuralism, functionalism, behaviourism, Gestalt psychology, and psychoanalysis. Emphasis will be placed on the contributions of the systems to contemporary theoretical conceptions and trends. (Prerequisites: PSYC-1150, PSYC-1160, and four additional courses in Psychology.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (549, N' The Psychology of the Family', N'4210', N'PSYC', N'An analysis of theory and research examining the impact of family context on individual development throughout the life cycle. (Prerequisites: PSYC-2230; restricted to 4th year Psychology/Developmental Psychology majors).', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (550, N' Advanced Developmental Psychology: Emotional Development', N'4220', N'PSYC', N'This seminar examines issues related to emotional development from infancy through adult years. The experience, expression, and regulation of emotion will be discussed from various historical and academic perspectives. (Prerequisite: PSYC-2230 or PSYC-2240.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (551, N' Advanced Developmental Psychology: Cognitive Development', N'4230', N'PSYC', N'An examination of current theories, research methods, and findings in the area of cognitive development. (Prerequisite: PSYC-2230 or PSYC-2240.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (552, N' Advanced Developmental Psychology: Social Development', N'4240', N'PSYC', N'This seminar examines established and newly-emerging theories of development and their impact on social development research. Emphasis is placed on describing social developmental outcomes (e.g., gender, emotion, morality), and the influence of biological factors (e.g., temperament) and socialization agents (e.g., parents, media) on such outcomes. (Prerequisite: PSYC-2230 or PSYC-2240.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (553, N' Seminar in Developmental Psychology', N'4250', N'PSYC', N'Focus on a particular topic within the area of developmental psychology. Topics will vary from term to term. (May be repeated for credit.) (Prerequisites: PSYC-1150, PSYC-1160, PSYC-2230 and three additional courses in Psychology.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (554, N' Methods of Behavioural Change', N'4270', N'PSYC', N'Survey of theories and methods of behavioural change, including behavioural assessment and analysis, relaxation training, graduated exposure, contingency management, and cognitive restructuring. (Prerequisite: PSYC-3350 or PSYC-3530 or PSYC-3580.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (555, N' Practicum in Developmental Psychology', N'4280', N'PSYC', N'Supervised practicum in learning-based behaviour change strategies. Students will practice behavioural assessment and systematic remediation of clinical problems through application of operant techniques in community placements. (A 6.00 credit hour course.)((Restricted to 4th year Developmental Pschology majors with cumulative and major averages of at least 70%.) (Prerequisites: PSYC-4270 and consent of instructor.) (Antirequisite: PSYC-4290.) (1.5 lecture hours and 8 practicum hours per week.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (556, N' Practicum in Psychology', N'4290', N'PSYC', N'Supervised practicum in a university or community setting. Students will consolidate and enhance their knowledge about psychology and skills in connection with a specific project, activity, or task. The practicum experience may include the opportunity to be involved in some aspect of community change, peer counselling, or research, among other possibilities. (A 6.00 credit hour course.)(Prerequisites: consent of instructor; restricted to 4th year Psychology majors with cumulative and major averages of at least 70%.) (Antirequisite: PSYC-4280.) (1.5 lecture hours and 8 practicum hours per week.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (557, N' Clinical and Counselling Psychology', N'4300', N'PSYC', N'The principles and techniques underlying clinical interviewing and modern psychotherapeutic methods. Emphasis will be placed upon the application of clinical interviewing and modern psychotherapeutic methods, as well as the application of clinical methods in clinics, hospitals, schools, mental health settings, and community agencies. (Prerequisites: PSYC-3330.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (558, N' Community Psychology', N'4320', N'PSYC', N'An examination of societal and environmental influences on the community and individual community members, the development of the community mental health movement, and current issues in theory, research, and practices in community psychology. Emphasis will be placed on prevention, crisis intervention, and effecting social change. An overview of community-based professional and volunteer services will be presented. Community responses to issues such as homelessness, suicide, and violence against women will be considered. (Prerequisite: PSYC-2360.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (559, N' Seminar in Law and Psychology', N'4330', N'PSYC', N'Issues in the interaction between law and psychology; discussion of the use/misuse/nonuse of behavioural sciences in the law; emphasis on how psychology can best be applied to family, criminal, and civil law. (Prerequisites: PSYC-1150, PSYC-1160, and four additional courses in Psychology.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (560, N' Seminar in Psychopathology', N'4360', N'PSYC', N'Focus on a particular topic within the area of abnormal behaviours. Topics will vary from term to term. (Prerequisites: PSYC-3330 and PSYC-2280)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (561, N' Seminar in the Psychology of Women', N'4400', N'PSYC', N'An examination of the feminist critique of mainstream psychology research of the last century, of feminist approaches to research and theorizing, and applications of feminist psychology to the study of a number of topics in the psychology of women (e.g., mental health, violence against women, sexuality). (Prerequisite: PSYC-2400 or consent of instructor.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (562, N' Special Topics in Health Psychology', N'4410', N'PSYC', N'This specialized course is designed for students who wish to pursue their interests in health psychology in greater depth. The course will focus on a particular topic within the area of health psychology, and topics will vary from term to term, depending on the emphasis of the instructor. Possible special topics could include Women''s Health, Personality and Health, and the Social Psychology of Health. (Prerequisite: PSYC-3390.)(May be repeated for credit if content changes.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (563, N' Stereotyping, Prejudice, and Discrimination', N'4450', N'PSYC', N'Psychological theory and research on stereotyping, prejudice, and discrimination; their formation and function; the role of individual and sociocultural factors in their development and maintenance; individual responses and psychological interventions. (Prerequisite: PSYC-2360.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (564, N' Practicum in Social Change', N'4500', N'PSYC', N'Supervised practicum in a university setting. Students consolidate and enhance their knowledge of sexual assault and bystander intervention. Students co-facilitate the Bringing in the Bystander® In Person Prevention program for one or more small groups of students on campus. The practicum experience equips students to deliver educational content on sensitive issues. (Prerequisite: SOSC-3500/PSYC-3500/SWRK-3500/SACR-3500 and permission of the instructor.) (Also offered as SOSC-4500, SWRK-4500, SACR-4500, and WGST-4500.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (565, N' Comparative Cognition', N'4570', N'PSYC', N'Evidence of general and specialized cognitive processes in human and non-human organisms will be investigated. Topics to be covered include perception, attention, and memory, concept formation, ecological and evolutionary bases of cognitive processes. Current research on these and other topics will be reviewed and discussed in a seminar format. (Prerequisite: Any two of the following undergraduate courses or their equivalents from other universities: PSYC-3530, PSYC-3580 or PSYC-3350.) (Also offered as BIOL-8470.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (566, N' Popular Literature in Psychology', N'4750', N'PSYC', N'A forum for critical examination and analysis of popular literature written by psychologists or others functioning in the role of counselor or lay expert. The main goal is to identify both traditional and novel criteria for assessing the validity of popular literature and its contribution to knowledge. (Prerequisites: PSYC-1150, PSYC-1160, and four additional courses in Psychology.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (567, N' Thesis: Seminar', N'4960', N'PSYC', N'Experience in conducting psychological research is viewed as necessary for graduate work in psychology. This course is designed to provide such experience through planning, developing, and writing a research proposal under individual faculty supervision. Group sessions on research ethics, procedures, writing, and data analysis. (Prerequisites: PSYC-2300; PSYC-3130 or equivalent; PSYC-3310;PSYC-3200; a cumulative average of at least 75%; a major average of at least 78%; and consent of Undergraduate Program Chair.) (Prerequisites for B.Sc. Honours Behaviour, Cognition and Neuroscience students: PSYC-2300; PSYC-3130 or equivalent; PSYC-3350, PSYC-3530, and PSYC-3580; an average of 78% or higher in Psychology courses; and consent of Undergraduate Program Chair.)', 3, 10, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (568, N'Meeting Human Needs through Social Welfare', N'1170', N'SWRK', N'This course examines the historical, philosophical and political aspects of the development and delivery of the Canadian Social Welfare System. Special attention will be focussed on ways to identify and assess the needs of, and services to, vulnerable populations within the context of social and cultural diversity.', 3, 11, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (569, N'Meeting Human Needs through Social Work', N'1180', N'SWRK', N'This course examines the ways in which social workers in generalist practice intervene to meet the needs of clients within the Canadian social welfare system. Attention will be paid to the development of an understanding of generalist social work practice within an ecological and systems perspective.This course provides an introduction to social work processes. Attention will be given to the needs of vulnerable and marginalized populations within traditional and alternative social work perspectives such as ecological, systems, strengths, feminist, and anti-oppressive practice. Students will gain an understanding of personal, professional, agency, and societal needs and values and how they influence social work practice. Students will be introduced to the generalist social work practice perspective within a problem-solving process that includes focused assessment, intervention, termination, and evaluation of practice. Ethical and professional issues such as confidentiality and accountability will be introduced.', 3, 11, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (570, N' Statistics for Social Workers', N'2000', N'SWRK', N'This online course is an introduction to statistics. Topics include measurement of variables, testing hypotheses, descriptive statistics, normal distribution, sampling, significance levels, and indicating appropriate quantitative analytical methods. Emphasis is given to how statistics can aid the social work practitioner. This course can also meet the statistics requirement for admission to the master’s program. (Semester 3 standing)', 3, 11, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (571, N'Issues and Perspectives in Social Welfare', N'2040', N'SWRK', N'Examines various ideologies that underpin the social welfare system and their impact on citizens, clients, communities, organizations, and society as a whole. The impact of these diverse perspectives on the different roles of social workers are examined with particular emphasis on value conflicts and how these conflicts shape and affect policies and programs. Further, the role and development of professional ethics relative to social service delivery through social welfare systems are explored.(Prerequisites: SWRK-1170 and SWRK-1180 or permission of instructor.)', 3, 11, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (572, N'Social Work and Diversity', N'2100', N'SWRK', N'This course examines oppression and anti-oppressive social work practice from a broad ecological framework as they relate to social inequality and life circumstances. Various forms of oppression such as racism, ableism, anti-semitism, heterosexism, and sexism are analyzed at the individual, cultural, and institutional level while applying the professional values and ethics of social work practice. The experiences, needs, and responses of populations that have been historically excluded, marginalized, and disadvantaged are examined. Students develop analytical and self-reflective skills as they relate to social work practice which fosters inclusion, participation, advocacy, and social justice. (Prerequisites: SWRK-1170 and SWRK-1180 or permission of instructor.)', 3, 11, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (573, N'Selected Topics in Social Work', N'3120', N'SWRK', N'Selected topics according to faculty and student interests may be offered. Topics may include issues related to social work practice, social welfare, or fields of practice. (May be repeated for credit if the course content differs.) (Open to senior students. Social Work Majors and Combined Majors in Social Work will be given registration priority.)', 3, 11, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (574, N'Social Work with Children', N'3340', N'SWRK', N'This course is designed to provide students with knowledge and understanding of social work practice with children. The focus is upon the development of a framework of knowledge for making practice decisions involving the child client. The design, implementation, and evaluation of alternative social work intervention strategies are included. The special set of techniques utilized in social work practice with children will be highlighted. (Open only to senior students. Social Work Majors and Combined Majors in Social Work will be given registration priority.)', 3, 11, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (575, N'Theory and Practice of Social Work with Individuals', N'3360', N'SWRK', N'This course examines the knowledge base, principles and techniques of social work generalist practice and the use of social work values in the context of offering help focusing on individuals (micro-level). Included are the use of interpersonal relationships as a medium for helping and the use of theories of human interaction within various systems as a base for problem assessment. Emphasis will be on practice with individuals in their social context. (Must be taken prior to field education courses)', 3, 11, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (576, N'Theory and Practice of Social Work with Small Groups', N'3370', N'SWRK', N'This course builds upon the generalist practice model of social work presented in SWRK-3360. It extends generalist social work concepts to small groups as client systems. Focus will be on analysis and application of generalist knowledge, values and skills for assessment and intervention with small groups. (Must be taken prior to field education courses) (Must be taken concurrently with SWRK-3360).', 3, 11, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (577, N' Theory and Practice of Social Work with Families', N'3380', N'SWRK', N'This course builds upon the generalist model of social work practice. It extends generalist social work concepts to families as multi-client systems. Focus will be on analysis and application of generalist knowledge, values and skills for assessment and intervention with families. (Prerequisites SWRK-3360 and SWRK-3370) (Must be taken prior to or concurrently with SWRK-3710.)', 3, 11, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (578, N' Theory and Practice of Social Work with Communities and Organizations', N'3390', N'SWRK', N'This course applies the generalist social work practice model at the macro level. It focuses on planning, implementing, and evaluating interventions with communities and human service organizations. Special emphasis will be placed on addressing the needs and issues of diverse, at-risk, and oppressed populations. (Prerequisite: SWRK-3360 and SWRK-3370.) (Must be taken prior to or concurrently with SWRK-3710.)', 3, 11, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (579, N'Research I: Foundations of Social Work Research', N'3440', N'SWRK', N'This course will prepare students to critically analyze generalist social work practice research. Foundation principles of rational and empirical inquiry will be examined across the continuum of qualitative and quantitative research designs that can be appropriately used in various practice contexts. It aims to enable the practical interpretation of research used integratively in practice with diverse clients, particularly those at risk of being marginalized or oppressed. (Prerequisite: 3rd or 4th year Social Work Major or Combined Major in Social Work, or permission of instructor.)', 3, 11, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (580, N' Risk and Resilience: Perspectives on Human Development', N'3460', N'SWRK', N'Examines human development in the social environment from a strengths-based perspective. Using a biopsychosocial orientation, emphasis is placed on identifying risk and protective factors that affect coping and adaptation to stressful life events. Focuses on how social systems (families, groups, organizations, institutions, and communities) promote or deter such efforts. Implications for multilevel interventions are discussed and evaluated. (Open to senior students. Social Work Majors and Combined Majors in Social Work will be given registration priority.)', 3, 11, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (581, N' Social Work and Violence', N'3470', N'SWRK', N'Examines aspects of violence in society, particularly against marginalized groups. The primary focus is on generalist social work intervention related to violence. (Open to senior students. Required course for Social Work/Women''s and Gender Studies students; elective for BSW students. Pre-requisite: One Women''s and Gender Studies (WGST-) course or permission of the instructor. (Also offered as WGST-3470.)', 3, 11, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (582, N' Professional Issues in Social Work', N'3480', N'SWRK', N'This course examines topics of current relevance for the profession of social work. Professional issues such as social control, suitability, ethical dilemmas, work-place issues of power, oppression and marginalization will be considered. The Social Work Code of Ethics will be used as the "lens" to explore case examples of situations that present professionals with ethical dilemmas, that challenge personal and professional values and require a sound grounding in professional ethical decision-making in the face of grave circumstances. Empowerment practice will serve as an integrative framework for the consideration of professional issues. (Open to senior students. Social Work Majors and Combined Majors in Social Work will be given registration priority.)', 3, 11, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (583, N' Practical Strategies for Social Change: Intervening to Prevent Sexual Violence', N'3500', N'SWRK', N'This course introduces students to sexual violence as a social problem why it matters, the forms it takes, and how it can be changed. The importance of personal and community responsibility for social change is emphasized. This course also provides students with the background knowledge that is needed to successfully teach sexual violence prevention workshops for their peers.Restricted to students who have attained a cumulative GPA of 66% or higher at the time of application. (Prerequisite: Semester 4 standing or above and permission of the instructor by online application at bystander initiative.ca) (Also offered as SOSC-3500, PSYC-3500, SACR-3500, and WGST-3500.)', 3, 11, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (584, N' Feminist Social Work Practice', N'3550', N'SWRK', N'This course prepares students to apply the principles, processes, and techniques of feminist social work practice. Required course for Social Work/Women''s and Gender Studies students; elective for BSW students (Prerequisites:(SWRK-3360, SWRK-3370 plus three (WGST-xxx) Women Studies courses or, consent of instructor.) (Also offered as WGST-3550)', 3, 11, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (585, N'Serving Older People', N'3560', N'SWRK', N'The role of the social worker in such areas as institutionalization, community care and social support, separation and loss, family structures, and retirement, with emphasis on social policy as a determinant of services and practice. (Open to senior students. Social Work Majors and Combined Majors in Social Work will be given registration priority.)', 3, 11, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (586, N'Child Welfare', N'3570', N'SWRK', N'Examines issues in the present structure and functioning of services for children. The rights of children and their need for services will be examined in relation to existing services, such as protection, adoption, foster care, health services, and compulsory education, with special attention to extra-family parenting responsibilities. (Open to senior students. Social Work Majors and Combined Majors in Social Work will be given registration priority.)', 3, 11, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (587, N'Social Work and Mental Health', N'3580', N'SWRK', N'This course focuses on social work practice in the field of mental health. It will integrate policy, practice and research to mental health issues across the life-span. The course will examine social work practice assessment and intervention techniques. Both chronic and acute mental health issues will be examined. Community-based care and institutional care perspectives will be presented. (Open to senior students. Social Work Majors and Combined Majors in Social Work will be given registration priority.)', 3, 11, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (588, N'Social Work and Health', N'3590', N'SWRK', N'Explores generalist social work practice with various client systems in transaction with the Canadian health care system. Systems'' structural characteristics as well as their philosophical underpinnings will be surveyed in a historical and theoretical context. This will be accomplished with current social work models using the person-in-environment framework and general systems theory. (Open to senior students. Social Work Majors and Combined Majors in Social Work will be given registration priority.)', 3, 11, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (589, N'Social Work and Addictions', N'3600', N'SWRK', N'Provides social work students with practice knowledge concerning the etiology, implications, and treatment issues related to addictions and substance abuse. Topics will include the history and consequences of addictions and substance abuse, addictions and the family, special at-risk groups, social policy and legal issues, medical and social-psychological aspects, and current research issues as well as the implications for intervention. (Open to senior students. Social Work Majors and Combined Majors in Social Work will be given registration priority.)', 3, 11, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (590, N' Field Education Preparation', N'3710', N'SWRK', N'The Field Education Preparation course (SWRK-3710) examines student progress in the social work program with the purpose of evaluating student readiness for a field practicum. Students will demonstrate the foundation level social work knowledge, skills, and values required to meet field education expectations and to ensure success in the field. The course integrates classroom and field learning through co-instructed learning sessions that include skill development facilitated by Field Learning Specialists with extensive practice experience, community resources training from community agencies and practicing professionals, exposure to service users, and assignments that require interaction with agencies and the community. This course will facilitate student development and growth on personal, professional, and civic levels. In addition, the course will ensure a smooth transition from the classroom setting into the field setting by demonstrating how connecting theory and practice results in successful individual, family, group, and community intervention. Finally, the course facilitates the development of professional, reflective, self-evaluating, and competent social workers. (Pre-requisites SWRK-3360 and SWRK-3370. Correquisites SWRK-3380 and SWRK-3390).(Contact Hours: 3.0)', 3, 11, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (591, N' Social Policy and Social Welfare', N'4230', N'SWRK', N'This course introduces the student to the formulation and analysis of social policy. The student uses knowledge of social services as a basis for assessing and recommending changes in existing programs or services, and for introducing new services. Special attention is given to identifying policy gaps in services and unmet needs of vulnerable populations within the general practice framework. (Prerequisite: 4th year Social Work Major or Combined Major in Social Work, or permission of instructor.)', 3, 11, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (592, N' Integration Seminar I', N'4300', N'SWRK', N'The Integration Seminar I course is taken concurrently with the Field Education I course (SWRK-4730). This seminar course builds on the knowledge, skills, and values developed in the theory and practice courses SWRK-3360, 3370, 3380 & 3390, along with problem solving models covered in SWRK-3440 and SWRK- 3710. The focus of learning for this course will be the application of concepts from theories and practice models to the process of assessment and intervention planning. The seminar enables students to integrate academic learning and field work experience to test and advance social work knowledge, values, and skills in preparation for professional social work practice. The course will provide students with opportunities to share their field placement experiences with the instructor and peers. This course will include classroom instruction, simulations, self reflection, discussions, group sharing and problem solving, and presentations to facilitate the development of professional, reflective, self-evaluating, and competent social workers. (Prerequisites: SWRK-3710, SWRK-3360, SWRK-3370, SWRK-3380, SWRK-3390, SWRK-3440) (Co-requisite: SWRK-4730)', 3, 11, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (593, N' Integration Seminar II', N'4310', N'SWRK', N'The Integration Seminar II course is taken concurrently with the Field Education II course (SWRK-4750). This seminar course builds on the knowledge, skills, and values developed in Field Education I (SWRK-4730) and Integration Seminar I (SWRK-4300). The seminar enables students to integrate academic learning and field work experience to test and advance social work knowledge, values, and skills in preparation for professional social work practice. The course will provide students with opportunities to share their field placement experiences with the instructor and peers. Students will participate in a variety of activities, including self reflection, discussions, group sharing and problem solving and presentations to facilitate the development of professional, reflective, self-evaluating, and competent social workers. Students will be expected to engage in critical reflection to integrate knowledge and skills from generalist practice into their own developing practice framework. (Pre-requisite: SWRK-4300, SWRK-4730) (Co-requisite: SWRK-4750).', 3, 11, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (594, N' Practicum in Social Change', N'4500', N'SWRK', N'Supervised practicum in a university setting. Students consolidate and enhance their knowledge of sexual assault and bystander intervention. Students co-facilitate the Bringing in the Bystander® In Person Prevention program for one or more small groups of students on campus. The practicum experience equips students to deliver educational content on sensitive issues. (Prerequisite: SOSC-3500/PSYC-3500/SWRK-3500/SACR-3500/WGST-3500 and permission of the instructor.) (Also offered as SOSC-4500, PSYC-4500, SACR-4500, and WGST-4500.)', 3, 11, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (595, N' Field Education I', N'4730', N'SWRK', N'The Field Education I course examines student progress in the field practice component of the Bachelor of Social Work program with the purpose of evaluating student readiness for Field Education II. Students will demonstrate the foundation level social work knowledge, skills, and values required to meet field practice expectations and ensure success in Field Education II (SWRK-4750). This course integrates classroom and experiential learning through placement in a community organization. Under the supervision of professional social workers, students are expected to apply beginning level knowledge, skills, values and ethics to generalist social work practice in “real-life” situations. The objectives and competencies outlined in the Field Education I Learning Agreement are minimum expectations for all students. The course fosters professional development wherein personal and professional skills are promoted in the interest of competent professional practice. Students will develop competence as an entry level professional in generalist social work practice within the four contexts of practice: organization, community, social work skills and professional context. Supported by Field Learning Specialists with extensive practice experience and professional social workers, students will develop substantive understanding of social work knowledge, values and skills and demonstrate an understanding of and commitment to the principles which underlie professional social work practice.This course operates as a practicum three days a week in the Fall semester. (Prerequisites: SWRK-3710, SWRK-3360, SWRK-3370, SWRK-3380, SWRK-3390, SWRK-3440) (Co-requisite: SWRK-4230, SWRK-4300).(Credit weight 9)', 3, 11, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (596, N' Understanding Social Life', N'1000', N'SACR', N'Understanding society through the exploration of contemporary social issues. (SACR-1000 is intended as a course for students who are not majors or minoring in Sociology, Criminology, Anthropology, and Family and Social Relations programs.) (Students who complete SACR-1000 may subsequently enroll in SACR-1100 for credit.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (597, N' Foundations of Social Life', N'1100', N'SACR', N'This course will introduce students to the key concepts, theories, and methods appropriate to Sociology, Anthropology, and Criminology. Focus will be on application of issues important to studying social life using multiple perspectives while exercising the sociological imagination. Topics may include discussion of culture, gender, social stratification, race and ethnicity, family, and crime and deviance.(Open only to Program Majors and Minors in the Department of Sociology, Anthropology, and Criminology and students enrolled in BES and International Relations and Development Studies). (Students who complete SACR-1100 may not subsequently enroll in SACR-1000 for credit).', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (598, N' Sociology of Families', N'2040', N'SACR', N'Sociological perspectives on cross-cultural variations and changes in family forms. Topics may include discussion of marriage, kinship, family structure and organization of intimate relationships. (Prerequisites: third semester standing.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (599, N' Sociology of Sexualities', N'2050', N'SACR', N'An analysis of sexual differentiation, sex role acquisition, sexual attitudes, sexual behaviour, and the sex structure of Canadian society. (Prerequisites: third semester standing.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (600, N' Perspectives on Culture', N'2130', N'SACR', N'An examination of the emergence, development, meaning, and idea of culture. The different ways the concept is used in applied anthropology and sociology, ethnographic research, and popular discourse are discussed. (Prerequisites: third semester standing.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (601, N' Gender, Culture, and Power', N'2140', N'SACR', N'A feminist exploration of how sex/gender system intersects with other organizing categories to frame masculinity and femininity and people’s roles and status in Canada and globally. It will examine how gender works in areas such as: popular culture, family, marriage, education, work, crime, migration, globalization or politics.(Prerequisites: third semester standing.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (602, N' Principles of Physical Anthropology', N'2150', N'SACR', N'A biocultural perspective of human genetic and phenotypic variation in an evolutionary context including but not limited to: comparisons to other primates; identifying sources and nature of variation in living humans and critique of race; investigations of illness in the past and present; and the study of extinct species (Prerequisite: third-semester standing.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (603, N' Education and Society', N'2160', N'SACR', N'This course employs sociological perspectives on education as central to social reproduction (in the transmission of knowledge, skills and values), as well as its place within broader social struggles and the creation and maintenance of social inequalities. Central theories are reviewed in light of empirical studies. Topics may include: schools as agents of socialization, stratification in education, social mobility, schooling and the labour market, cross-cultural educational practices, alternative forms of schooling, peer group influence, higher education, teacher-student relations, apprenticeships, arts-training, and the challenges associated with school violence. (Prerequisites: third semester standing.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (604, N' Religion and Society', N'2170', N'SACR', N'This course investigates religion as a social institution, examining beliefs, symbols, relations and practices. Central theories and methods are reviewed in light of historical and cross-cultural variations. Interactions with culture, education, family, community, the economy, and politics are considered. Topics may include: the sacred and the profane, secularization, myth, totemism, cults, fundamentalisms, integration, exclusion, violence, new religions and/or new age movements. (Prerequisites: third semester standing.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (605, N' Social Dilemmas: Social Science Perspectives', N'2200', N'SACR', N'This course will examine major sociological issues facing countries today, such as economic inequality, political polarization, racism and racialization, immigration, human rights, education, media, globalization, incarceration, precarious work, gender and family, and climate change. It will explore how social science research measures and tracks changes in these areas. It will examine, for example, how different ideologies, ethical frameworks, and political positions on these issues address these issues, and how public policies, popular media, and politicians use social science research. (Prerequisite: second-semester standing).', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (606, N' Morals, Markets, and Myths', N'2220', N'SACR', N'An introduction to economic sociology, anthropology, and criminology; this course examines how markets and forms of exchange are embedded in social institutions, are regulated by morals and suffused with moral tales (concerning debts, gifts, and taxes for instance), and depend on specialized knowledge and technology. Topics covered may include: consumerism, debt, the financialization of everyday life, market formation and regulation, corporations, capital accumulation regimes, the critical consideration of assumptions in economic research and social policy, struggles for tax justice, workers'' organizations, the gendered division of social labour, digital commerce, charity, black markets, and the impacts of economic crises on societies. (Prerequisite: Third semester standing or above.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (607, N' Globalization, Development and Social Change', N'2270', N'SACR', N'This course examines such issues as the impact of colonialism on global poverty and trade policies, global restructuring, neoliberal policies, global governance, poverty alleviation efforts, cultural resistance, gendered patterns of development, population displacements and popular responses to globalization. (Prerequisites: third semester standing.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (608, N' Class, Wealth and Power in Canada', N'2280', N'SACR', N'The study of structured social inequality. The existence of class and power structures and their effects on the lives of Canadians. The relation of different forms of inequality based on class, ethnicity, and gender. The various strategies people employ to respond to inequality. (Prerequisites: third semester standing.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (609, N' Introduction to Race and Ethnicity', N'2400', N'SACR', N'An introduction to race and ethnic relations, with global and Canadian perspectives, which may draw on both sociological and anthropological literature. Topics may include Canadian cultural, indigenous, ethnic and racial identities; multiculturalism; im/migration and integration; separatist movements; pursuit of collective rights; transnationalism and diaspora. (Prerequisites: third semester standing.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (610, N' Gender, Sexuality and Social Justice', N'2100', N'SACR', N'This course examines the personal and cultural meanings of women’s sexual identities in Canada today. Students consider how these identities are created and experienced in conjunction with other identities such as race/ethnicity, social class, and (dis)ability and how women challenge the personal, social, political, and economic inequities that continue to be based on these identities. Students are encouraged to analyze how their beliefs and behaviours are shaped by heterosexual privilege.(Also offered as Women''s and Gender Studies WGST-2100.) (Prerequisites: WGST-1000)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (611, N' Introduction to Criminology', N'2600', N'SACR', N'Theories and research in crime causation, the nature and extent of crime, and policy responses. (Prerequisites: SACR-1000 or SACR-1100)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (612, N' Introduction to Criminal Justice', N'2620', N'SACR', N'This course will examine the creation and administration of law and justice. Topics may include: legal systems, legal and justice professionals, civil and criminal law, courts and sanctions. Focus of the course will vary by instructor. (Prerequisites: SACR-1000 or SACR-1100)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (613, N' Researching Social Life', N'2900', N'SACR', N'Introduction to social research with focus on guiding students through the research process. This includes: constructing a research problem; formulating research questions; conducting a literature review; evaluating journal articles; understanding research ethics; and becoming familiar with quantitative and qualitative research methods. At the end of the course, it is expected that students will obtain a Research Ethics Certificate (TCPS2). (Prerequisites: SACR-1100 (strongly recommended) or SACR-1000; students must be in semester 3 of their program to register for this course).', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (614, N' Theorizing Social Life', N'2910', N'SACR', N'This course introduces students to theoretical work by focusing on classical social theorists such as Marx, Weber, and Durkheim and showing how classical approaches inform contemporary social analysis. Students learn how to interpret and persuasively appropriate theory, think conceptually, reflect on the basic assumptions of social analysis, and assess explanations. Topics covered typically include approaches to social order and change, modernity, ways of conceptualizing society and social life, methodology, institutional dynamics, class, inequality, and culture (Prerequisites: SACR-1100 (strongly recommended) or SACR-1000; students must be in Semester 3 of their program to register for this course).', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (615, N' Sociology of Childhood', N'3010', N'SACR', N'This course explores the experience of childhood as a moment within the human life cycle, yet one subject to great variation according to the family and social context. Students will become familiar with ongoing debates about the nature of children and childhood, the concept of socialization, the role and place of children in family, social, and economic life, as well as children’s own agency in shaping their lived experience. (Prerequisites: SACR-2040 and semester 5 or higher standing.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (616, N' Sexuality and Health', N'3050', N'SACR', N'Contemporary topics in sexuality and health examined from Canadian and international perspectives, such as HIV and AIDS, sexual health movements, and the social construction of sexual dysfunction. (Prerequisite: SACR-2050 and semester 5 or higher standing)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (617, N' Intermediate Statistics', N'3080', N'SACR', N'Basic inferential statistics, including estimation, confidence intervals, and hypothesis testing. Also included is the application of computer packages to selected statistical problems. (Prerequisite: SOSC-2500, SACR-2900 (or equivalent), and semester 5 or higher standing.) (Credit can only be obtained for one of PSYC-3130 or SACR-3080.) (2 lecture hours, 1 laboratory hour a week.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (618, N' On Death and Dying', N'3150', N'SACR', N'A critical exploration of topics related to the sociology and anthropology of death and dying. Topics covered may include: historical and cross-cultural perspectives on dying and death; memorial and commemoration; palliative care and medical aid in dying; death and popular culture; genocide and mass deaths; the construction and medicalization of death; and death statistics (pre-requisites SACR-2200 and semester 5 or higher standing).', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (619, N' Humanitarianism and Counterinsurgency Warfare', N'3170', N'SACR', N'Humanitarianism and counterinsurgency warfare are two sides of the same coin in the post-Cold War global security paradigm. This course will examine how each became pillars of that paradigm: humanitarianism as the ostensible commitment to save the victims of natural and political disasters worldwide; and counterinsurgency warfare as the strategy both to eliminate criminal and terrorist networks that challenge the legitimacy of the inter-state system and to “win the hearts and minds” of local populations whom they threaten. In that context, the course will investigate responses to armed insurrections as well as natural disasters, epidemics, and acute famines that, today, combine humanitarian with militarized responses. The course will also consider how humanitarianism and counterinsurgency warfare draw heavily on academic expertise (e.g. anthropology, sociology, criminology, geography, history, political science, religious studies, and psychology), thus inviting the controversial issue of the relationship between the scholar, the university, and the security apparatus. (Prerequisite: semester 5 standing).', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (620, N' Forensic Anthropology', N'3230', N'SACR', N'An overview of anthropological methods as applied to death investigations. Topics may include detection, recovery, and examination of human remains; problems of identification and individualization; and the reconstruction of events that occurred around the time of death. (Prerequisite or Corequisite: SACR-2150, and semester 3 or higher standing, or consent of instructor.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (621, N' Social Movements', N'3270', N'SACR', N'An examination of theories and case studies of world revolutions, class struggles, and various social movements, such as the feminist, gay and lesbian, labour, native, ecological, and other movements. (Also offered as WORK-3270) (Prerequisites: SACR-1100 or WORK-1000 and semester 5 or higher standing) (Credit can only be obtained for either SACR-3270 or WORK-3270).', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (622, N' Contemporary Families', N'3290', N'SACR', N'Examines the empirical sociological literature on families and their formation in the context of postwar change with emphasis on the Canadian experience, including key demographic trends such as the rise of cohabitation and two-earner families, and changes in divorce rates. (Prerequisites: SACR-2040 or SACR-3010 and semester 5 or higher standing', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (623, N' Crimes of the State: Genocide, War Crimes and Ethnic Cleansing', N'3330', N'SACR', N'This course examines complex issues related to genocide, war crimes, ethnic cleansing and other related crimes of the state in comparative contexts and considers the possibilities and constraints of international law and humanitarian intervention in detecting and preventing future mass atrocities. (Prerequisites: SACR-2400 and semester 5 or higher standing.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (624, N' Stuff: Seeing Culture Through ''Things''', N'3380', N'SACR', N'Why do we need so much stuff and how has it changed over time? An examination of the representation and interpretation of the material artifacts of culture in a global context, including theoretical approaches to objects and cultural products way artifacts and objects are interpreted, used and represented in our social and economic institutions, our everyday lives and in a global context. We ask: What is the function of objects in contemporary capitalist societies? To what degree do we ‘need’ things to make our way in the world? How do objects inform our social identities? The course draws on a variety of theoretical approaches to explore the meaning, circulation and production of objects, including the relationship of objects to consumption. Topics may include cultural products and commodities, advertising, consumption and technologies, places and museums, media and visual displays. (Prerequisites: SACR-2130 and SACR-2910, or SACR-2200 and SACR-2910, and semester 5 or higher standing).', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (625, N' Refugees, Borders, and Human Rights', N'3390', N'SACR', N'Who are refugees, illegal migrants and asylum seekers? Where do asylum seekers come from and why? What do states do about them? Which agencies, actors and interest groups engage with refugees? What are the links between refugees and nationalism and racism, borders, immigration policies and attitudes, state sovereignty, diaspora and transnationalism, human rights and cultural identities? How do we understand borders, boundaries, and biopolitics in our contemporary world? This course provides the student with the analytical skills to interpret historical and contemporary claims, vested interests and local, regional and global complexities of these issues. (Prerequisites: one of SACR-2130, SACR-2200, SACR-2270 or SACR-2400, and semester 5 or higher standing).', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (626, N' Food and Global Sustainability', N'3400', N'SACR', N'This course offers a comparative examination of the emergence of a global food system and its implications for culture, environment, working conditions, health, and population movements. (Prerequisites: one of SACR-1100, SACR-2130, SACR-2200, SACR-2270 or SACR-2400, and semester 5 or higher standing or instructor’s consent).', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (627, N' Human-Animal Studies', N'3410', N'SACR', N'A sociologically-informed examination of the growing field of human-animal studies, focusing on the effects of social, legal, political, economic, technological, and cultural change on our relations with and representations of nonhuman animals. Topics covered may include examining the socio-political constructions of animals, policies governing the (ab)use of animals, consumptive practices involving animals, use of animals for leisure and entertainment, and intersectional conceptions of social, environmental and species justice. (Prerequisites: semester 5 or higher standing.', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (628, N' Practical Strategies for Social Change: Intervening to Prevent Sexual Violence', N'3500', N'SACR', N'This course introduces students to sexual violence as a social problem why it matters, the forms it takes, and how it can be changed. The importance of personal and community responsibility for social change is emphasized. This course also provides students with the background knowledge that is needed to successfully teach sexual violence prevention workshops for their peers.Restricted to students who have attained a cumulative GPA of 66% or higher at the time of application. (Prerequisite: Semester 4 standing or above and permission of the instructor by online application at bystander initiative.ca) (Also offered as SOSC-3500, PSYC-3500, SWRK-3500, and WGST-3500.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (629, N'Gay and Lesbian Studies', N'3510', N'SACR', N'A multidisciplinary review of critical issues in the social organization and representation of same-sex bonding, including discussion of: cross-cultural studies, historical constructions of homosexuality and romantic friendship, coming out and identity, relationships and family, theories of homophobia and sexual repression, the development of communities and social movements in modern societies, the impacts of AIDS, and the emergence of queer theory. (Prerequisites: SACR-2050 and semester 5 or higher standing.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (630, N' Citizenship, Rights, and Social Justice', N'3520', N'SACR', N'An examination of the impact of the ‘global’ on social and economic processes, human rights and struggles over rights in specific locales worldwide. Topics may include: gender-based violence, poverty and ‘development’, children’s rights, changing labour practices; human rights principles and institutions; and cultural and political struggles for rights in European, North American, and post-colonial settings (Prerequisite: one of SACR-2130, SACR-2140, SACR-2200, SACR-2270, SACR-2400 or SACR-2910 and semester 5 or higher standing or instructor’s consent.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (631, N' Women, Power, and the Environment', N'3530', N'SACR', N'This course focuses on environmental issues as they affect women across cultures. It provides a feminist critical analysis of the power relations in modern societies that cause environmental degradation and examines the theories, policies, and institutions that contribute to unsustainable practices. Emphasis is placed on the women-nature debate within various environmental social movements and the historical role women have played as activists. (Also offered as Women''s and Gender Studies WGST-3530.) (Prerequisite: Semester 5 or higher standing and one course at the 2000-level or above from Women''s and Gender Studies.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (632, N' Gender, Space, and Time', N'3540', N'SACR', N'An examination of sociological and anthropological approaches to the study of space-time relations within the field of gender studies, including a focus on the development of gendered environments and cultural practices. (Also offered as Women''s and Gender Studies WGST-3540.) (Prerequisites: SACR-2140 or WGST-1000 and semester 5 or higher standing.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (633, N' Cultural Theory', N'3560', N'SACR', N'Through a selective examination of social theory, this seminar examines key ideas that inform identities and reflect contemporary issues. Topics may include: race, culture and ethnicity, colonial and post-colonial theory. (Prerequisite: SACR-2130 and semester 5 or higher standing)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (634, N' Youth in Conflict with the Law', N'3610', N'SACR', N'The course covers theories of delinquency causation, the youth justice system, Youth Criminal Justice Act, prevention, and treatment programs. (Prerequisites: SACR-2600, SACR-2620, and semester 5 or higher standing).', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (635, N' Victimology', N'3620', N'SACR', N'Victimology is a subfield of criminology that Focuses on victims within the study of crime. Topics explored may include: victimology patterns, the process and aftermath of the victimization experience, the involvement and treatment of victims in the criminal justice system, legal remedies and services available to victims, restorative justice initiatives, and victims'' rights. (Prerequisites: SACR-2600, SACR-2620, and semester 5 or higher standing).', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (636, N' Penology', N'3630', N'SACR', N'Study of the correctional institution including the impact of prison on inmates, the prison subculture, prison architecture, and administration, special institutions, and the assessment of education, occupational, recreational, and treatment programs. (Prerequisites: SACR-2600, SACR-2620, and semester 5 or higher standing).', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (637, N' Green Criminology', N'3650', N'SACR', N'This course will introduce undergraduate students to green criminology, a new and growing sub-field within criminology examining harms (criminal and otherwise) perpetrated against the environment and human and non-human animals. It examines the conceptual and theoretical developments within this field, as well as specific substantive harms, the various layers of actors involved, and the potentials and limitations of regulation. (Prerequisites: SACR-2600, SACR-2620 and semester 5 or higher standing).', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (638, N' White Collar Crime', N'3670', N'SACR', N'This course focuses on criminal and non-criminal harms perpetrated by powerful individuals, organizations, and institutions. Topics covered may include political corruption, genocide, environmental crime, workplace injury and death, food contamination, stock market manipulation and various other kinds of fraud. The development and enforcement of criminal, administrative, and civil law will be examined along with different theoretical perspectives on this specific type of criminal behaviour. (Prerequisites: SACR-2600, SACR-2620 and semester 5 or higher standing)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (639, N' Policing and Security', N'3680', N'SACR', N'This course will provide an overview of the development of public policing and security in Canadian society. Topics will include the history, development, organization, role and mandate of public policing. The course will also introduce students to the concept of security and will cover interpretive models for assessing how policing and security are governed and practiced both in Canada and internationally (Prerequisites: SACR-2600, SACR-2620, and semester 5 or higher standing).', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (640, N' Selected Topics in Criminology', N'3700', N'SACR', N'Course content varies by instructor and can be taken more than once if content changes. Details about the course will be made available through the department. (Prerequisites: SACR-2600, SACR-2620, and semester 5 or higher standing.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (641, N' Drugs and Society', N'3710', N'SACR', N'Using a sociological perspective, this course aims to provide a foundation for the critical understanding of drugs and society. In particular, this course explores the various processes (i.e., social, cultural, political, economic) that shape our understanding of and policies towards drugs and drug use in historical and contemporary society. (Prerequisites: SACR-2600, SACR-2620, and semester 5 or higher standing.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (642, N' Contemporary Criminological Theory', N'3730', N'SACR', N'A review of modern theoretical approaches in criminology. (Prerequisites: SACR-2600 and SACR-2620 and semester 5 or higher standing.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (643, N' Crime, Media and Culture', N'3740', N'SACR', N'This course explores the relationships between crime, crime control, media and popular culture by focusing on the theoretical understanding of the interplay between crime, media and culture, how crime and crime control are represented through various forms of media, and the potential effects of various media on crime and crime control policy. Focus and topics may vary by instructor. (Prerequisite: SACR-2600; SACR-2620; and semester 5 or higher standing).', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (644, N' Surveillance and Society', N'3820', N'SACR', N'The course provides an overview of surveillance in contemporary society. Substantive topics may include surveillance in relation to national security, covert police activities, social media, consumers, workplace, biometrics and inequality, social sorting, privacy, and privacy law/regulation. Focus of the course will vary by instructor. (Prerequisites: SACR-2600; SACR-2620; and semester 5 or higher standing).', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (645, N' Qualitative Approaches to Social and Cultural Research', N'3900', N'SACR', N'An exploration and application of interpretive research strategies. Student will be taught to employ various techniques as they bear on real world issues and research questions. Techniques may include for example, participant observation, historical comparative analysis, oral histories, interviewing, cultural and discourse analysis. Students are expected to hold their TCPS certificate and learn to apply ethical issues specific to the interpretive and qualitative methodologies employed in the course. (Prerequisites: SACR-2900/210 (or equivalent), SACR-2910, and semester 5 or higher standing)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (646, N' Contemporary Social Theory', N'3910', N'SACR', N'Investigates influential contemporary approaches to understanding and explaining social life. Emphasis is placed on epistemology, ontology and normativity, and on critically evaluating and creatively using theory. Theories covered may include Symbolic Interactionism, Structural-Functionalism, Phenomenology, Structuralism, Neo-Marxism, Psychoanalysis, Feminism, Postmodernism, and Postcolonialism among others. (Prerequisite: SACR-2910, SACR-2900 (or equivalent), and semester 5 or higher standing.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (647, N' Selected Topics in Sociology', N'3970', N'SACR', N'Course content will vary by instructor. This course may be taken more than once if content changes. (Prerequisites: semester 5 or higher standing.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (648, N' Advanced Seminar in Culture and Ideology', N'4030', N'SACR', N'The study of the influence of social location on human understanding, including the social organization, creation, and distribution of knowledge. Topics may include how social practices shape scientific knowledge, the origins of common sense and conventional wisdom, how politics affect medical definitions, and cultural constructions of class, gender, race, and sexuality. (Prerequisite: SACR-3910 or SACR-3560 or SACR-3730; SACR-3900, SACR-3080 and semester 7 or higher standing, or consent of instructor.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (649, N' Advanced Seminar in Social Theory', N'4050', N'SACR', N'An exploration of contemporary social theory as it bears on sociology and related disciplines. Theoretical approaches examined will vary by instructor. (Prerequisite: SACR-3910 or SACR-3560 or SACR-3730; SACR-3900, SACR-3080 and semester 7 or higher standing, or consent of instructor.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (650, N' Advanced Seminar in Feminism', N'4080', N'SACR', N'This course explores contemporary feminist thought; it includes the application of feminist theories to the understanding of social issues, political engagement and cultural struggles. Key topics may include diversity and identities, globalization, the politics of pleasure, reproductive politics, gender, sexualities, and social movements. (Prerequisite: SACR-3910 or SACR-3560 or SACR-3730; SACR-3900, SACR-3080 and semester 7 or higher standing, or consent of instructor.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (651, N' Advanced Seminar in Family, Gender and Culture', N'4090', N'SACR', N'A critical examination of key issues and debates in multiple family forms and relations in contemporary society. The course will adopt a cross-cultural approach examining and analyzing family forms and processes with emphasis on the intersectionalities of gender, culture, age, ethnicity, and class. (Prerequisites: SACR-2040, SACR-3910 or SACR-3560 or SACR-3730; SACR-3900 SACR-3080 and semester 7 or higher standing, or consent of instructor.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (652, N' Human Skeletal Variation', N'4120', N'SACR', N'This course is an advanced critical review of theories and methods for collecting, analyzing, and interpreting data from human skeletal remains in bioarchaeological (paleopathology, paleodemography, etc.) and forensic contexts. Students will learn to pursue a biocultural approach for the study of human skeletal variation.(Prerequisite or Corequisite: SARC-3230, and semester 5 or higher standing, or consent of instructor.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (653, N' Advanced Seminar in Culture, Power, and Globalization', N'4150', N'SACR', N'A critical and substantive examination of culture in a global context, including cultural practices, political economy and culture, culture and representation and culture and identity. Topics may include: material culture, commodities and cultural products; colonialism; globalization; ideas of place and time; transnational networks. (Prerequisites: SACR-3910 or SACR-3560 or SACR-3730; SACR-3900, SACR-3080 and semester 7 or higher standing, or consent of instructor.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (654, N' Survey Research Capstone', N'4160', N'SACR', N'The design, implementation, and analysis of sample surveys of topical and timely issues related to social life. (Prerequisite: SACR-2900 (or equivalent), SACR-3080, and three 2000 or 3000-level courses; a minimum of 73% in the program; semester 7 or higher standing, and permission of the instructor) (3 lecture hours, or 2 lecture hours, one tutorial/laboratory hour a week.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (655, N' Special Topics in Sociology and Criminology', N'4210', N'SACR', N'Topics may vary by instructor; consult the departmental website for details. (Prerequisites: SACR-3910 or SACR-3560 or SACR-3730; SACR-3900, SACR-3080 and semester 7 or higher standing, or consent of instructor.) (May be repeated for credit if content changes.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (656, N' Advanced Seminar on Race and Ethnicity', N'4220', N'SACR', N'This course explores theoretical approaches to race and ethnicity with a focus on political and cultural struggles and issues encountered by racialized and ethnic minorities. Topics may include: multicultural politics, anti-racist strategies, transnational and diasporic mobilization, and in intersectionality. (Prerequisites: SACR-2400; SACR-3910 or SACR-3560 or SACR-3730; SACR-3900 or SACR-3080 and semester 7 or higher standing, or consent of instructor.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (657, N' Social Life in the City Capstone', N'4250', N'SACR', N'The course is designed to give advanced students the opportunity to engage in qualitative research projects on social issues and cultural practices in the Windsor-Essex Region. (Prerequisites: a 73% in the program, SACR-3910 or SACR-3560; SACR-3900; and semester 7 or higher standing and permission of the instructor).', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (658, N' Advanced Seminar in Family Studies', N'4290', N'SACR', N'Discussion of major themes in family theory, which may include explanations for family forms, functioning, processes and structure. (Prerequisites SACR-2040, SACR-3910 or SACR-3560 or SACR-3730; SACR-3900, SACR-3080 and semester 7 or higher standing, or consent of instructor.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (659, N' Advanced Seminar in Sexualities and Identities', N'4510', N'SACR', N'A critical engagement with the historical, contemporary and newly burgeoning sociological approaches to sexualities and sexual identities. The course will adopt a cross-cultural approach in examining and analyzing human sexualities, with emphasis on the intersectionalities of other forms of inequality such as gender, race, ethnicity, disability, and class. Topics may vary from year to year. (Prerequisite: SACR-2050, SACR-3910 or SACR-3560 or SACR-3730; SACR-3900, SACR-3080 and semester 7 or higher standing, or consent of instructor.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (660, N' Practicum in Social Change', N'4500', N'SACR', N'Supervised practicum in a university setting. Students consolidate and enhance their knowledge of sexual assault and bystander intervention. Students co-facilitate the Bringing in the Bystander® In Person Prevention program for one or more small groups of students on campus. The practicum experience equips students to deliver educational content on sensitive issues. (Prerequisite: SOSC-3500/PSYC-3500/SWRK-3500/SACR-3500/WGST-3500 and permission of the instructor.) (Also offered as SOSC-4500, PSYC-4500, SWRK-4500, and WGST-4500.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (661, N' Advanced Seminar in Constructions of Deviance', N'4600', N'SACR', N'This course explores theory and research concerned with constructions of deviant behavior and social issues. It involves the application of constructionist theory to a variety of behaviours and issues including the role of moral entrepreneurs, symbolic crusades and the medicalization on deviance. Substantive topics will vary by instructor. (Prerequisites: SACR-2600, SACR-3910 or SACR-3730 or SACR-3560; SACR-3900 or SACR-3080 and semester 7 or higher standing; at least one 3000-level course from the 300 level criminology course selection, or consent of instructor.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (662, N' Advanced Seminar in Law and Social Policy', N'4610', N'SACR', N'This course explores theory and research concerned with legal and government policies and their impact on individuals, social institutions and society. Substantive topics will vary by instructor. (Prerequisites: SACR-3910 or SACR-3730 or SACR-3560; SACR-3900 or SACR-3080 and semester 7 or higher standing; at least one 3000-level course from the 300 level criminology course selection or consent of instructor.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (663, N' Advanced Seminar in Sociology of Law', N'4640', N'SACR', N'An investigation of theory and research in the sociology of law. Topics may include the social construction of law, the legal profession, law and social change, legal consciousness, law as governance, legal avoidance, moral regulation, and popular representations of law. Criminal and other forms of law will be discussed in relation to these topics. Focus of the course will vary by instructor. (Prerequisites: SACR-3910 or SACR-3730 or SACR-3560; SACR-3900 or SACR-3080 and semester 7 or higher standing; at least one 3000-level course from the 3000 level criminology course selection; or consent of instructor.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (664, N' Advanced Seminar in Gender, Law, and Crime', N'4650', N'SACR', N'This course will examine the ways gender intersects with the law and crime. It focuses on the importance of taking gender into consideration in understanding offending and victimization, the development and impacts of legislation, and the work of the criminal justice system. Focus of the course will vary by instructor. (Prerequisites: SACR -3910 or SACR -3730 or SACR -3560; SACR-3900 or SACR-3080 and semester 7 or higher standing; at least one 3000-level course from the 3000 level criminology course selection; or consent of instructor.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (665, N' Criminology Professional Development Practicum', N'4670', N'SACR', N'This practicum provides students in the Criminology program with the opportunity to apply learned concepts and theory to a practical setting and to become further familiarized with an area related to Criminology. Students will be placed in organizations in the Windsor-Essex region related to their area of interest within Criminology (e.g., the law, corrections, etc.) and will be expected to dedicate a total of 100 hours to both the in-class learning and practicum components of the course. Additionally, students will be required to complete assignments as assigned by the instructor. This course is open to Criminology majors only.(Prerequisites: SACR-2600, SACR-2620, SACR-3730 or SACR-3910 or SACR-3560, semester 7 or higher standing, and minimum major average of 73%.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (666, N' Advanced Seminar in Criminology', N'4910', N'SACR', N'This course exposes students to advanced criminological topics through the lens of the instructor’s current research. Practical and/or theoretical implications of the research within local, national, global and/or cyberspace communities will be examined. Topics will vary by instructor. (Prerequisites: SACR-2600; SACR -2620; SACR-3910 or SACR-3730 or SACR-3560; SACR-3900 or SACR-3080 and semester 7 or higher standing; at least one 3000-level course from the 3000 level criminology course selection; or consent of instructor.)', 3, 12, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (667, N' Women in Canadian Society', N'1000', N'WGST', N'This course illustrates and account for the position of women in Canadian society. We explore how gender identities are informed by the process of social construction which privileges some women while disadvantaging others.', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (668, N'Gal Pals: Women and Friendship', N'1200', N'WGST', N'This course examines a diverse range of women’s friendships. Through discussion, reading, and films we will explore topics such as the meaning of friendship for women, how women’s friendships have been portrayed in literature and film, the link between friendship and social activism for women, and the political meanings of women’s friendship in cultures resistant to woman-centered consciousness. (Can be taken for Social Science or Arts credit.)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (669, N' Imagining Women', N'1300', N'WGST', N'This course examines a broad cross-section of historical and contemporary representations of western women in popular culture, and visual media – photographs, film and video, the fine arts, and advertising. The student will be introduced to feminist and gender-related theories of representation. (Can be taken for either Social Science or Arts credit.)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (670, N' Queer Activism', N'1400', N'WGST', N'Students engage with LGBTQ+ activism, past and present. Students investigate how queer communities are created and sustained through protest, alliance-building, symbols, and memes. (Also offered as SJST-1400).', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (671, N'Women and Religion', N'1600', N'WGST', N'A comparative, feminist-critical exploration of the status of women and women''s religious experience in selected examples of traditional (e.g., Judaism, Christianity, Islam, Hinduism, Buddhism) and non-traditional (e.g., Goddess religions, Wicca, Feminism) religions, ancient and modern. (Can be taken for either Social Science or Arts credit.)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (672, N' Gender, Sexuality and Social Justice', N'2100', N'WGST', N'This course examines the personal and cultural meanings of women’s sexual identities in Canada today. Students consider how these identities are created and experienced in conjunction with other identities such as race/ethnicity, social class, and (dis)ability and how women challenge the personal, social, political, and economic inequities that continue to be based on these identities. Students are encouraged to analyze how their beliefs and behaviours are shaped by heterosexual privilege.(Also offered as Sociology SACR-2100.) (Prerequisites: WGST-1000.)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (673, N' Women and Politics', N'2110', N'WGST', N'An introduction to the principal themes in the study of women in Canadian politics. Topics may include: feminist theory, women in Canadian political institutions, the status of women in the Canadian economy, and gender equality rights in the Charter. Also offered as Political Science POLS-2110.', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (674, N' Gender, Movement, and Performance', N'2190', N'WGST', N'This course explores how people come to know themselves and others through their bodies.Students investigate movement as a cultural message and explore how stereotypes connected to gender, race, sexuality, and ability are created and contested through the body. (Prerequisite: WGST-1000 or DRAM-1000 or with permission of the instructor)(Cross-listed as an Arts course in Dramatic Art DRAM-2190)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (675, N'Women, Race, and Social Justice', N'2200', N'WGST', N'This course examines the personal and cultural meanings of women''s racial and ethnic identities in Canada today. Students consider how these identities are created and experienced in conjunction with other identities such as sexuality, social class, and (dis)ability and how women challenge the personal, social, political, and economic inequities that continue to be based on these identities. Students are encouraged to analyze how their beliefs and behaviours are shaped by white privilege. (Prerequisite: WGST-1000 or permission of the instructor.)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (676, N'Gender and Moral Choice', N'2300', N'WGST', N'This course examines how gender affects personal and public definitions of moral problems and their resolution. Issues such as reproductive choices, childrearing, poverty, and war will be used to explore these ideas. (Can be taken for either Social Science or Arts credit.) (Prerequisite: Semester 3 or above standing and one Women''s and Gender Studies course or consent of the instructor.)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (677, N' Feminist Philosophies', N'2360', N'WGST', N'An examination of key philosophical themes in feminism and philosophical debates among feminists. The themes and subjects of debate addressed may include sexism and oppression, feminist identity; theories the political significance of language, personal appearance, and pornography; feminist ethics; and feminist theories of knowledge. (Prerequisite: PHIL-1100 or PHIL-1120 or semester 3 or above standing, or consent of the instructor. Can be taken as an Arts credit.) (Also offered as Philosophy PHIL-2360).', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (678, N' Work and Equality', N'2400', N'WGST', N'This course explores how paid and unpaid work are gendered, valued and rewarded. Students investigate how public policy, law, collective bargaining, and advocacy enhance equality in pay and working conditions for women and racialized workers. (Prerequisites: WORK-1500 or WGST-1000 or SACR-1100 (Also offered as Work and Employment Issues WORK-2400)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (679, N'Women''s Bodies, Women''s Health', N'2500', N'WGST', N'This course examines and critiques commonly cited biological evidence in support of sex differences and male superiority, including research on anatomy, genetics, hormones, and differential brain functioning. Students explore the social, cultural, and political meanings of the female body and consider how these understandings influence medical and non-medical definitions of “health” for women. Students investigate how sexism, classism, racism, ageism, and homophobia shape how individuals think about and value different female bodies. (Prerequisite: One Women’s and Gender Studies course or permission of the instructor.) (Can be taken for either Science or Social Science credit.)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (680, N'History of Women''s Movements in North America', N'2510', N'WGST', N'An exploration of the collective action of women in the past and present in North America. Areas of study may include women''s involvement with the temperance, civil rights, suffrage, trade union, environmental, reproductive rights, and women''s liberation movements. (Also offered as History HIST-2510.)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (681, N' Women and Globalization', N'2600', N'WGST', N'This course introduces students to gender-sensitive analysis of the role of women in the global economy. Course materials cover the place of women in the international division of labour, the role of women in export-oriented industries in the "Third World," and women as "homeworkers" in the First and Third World. Students will utilize relevant empirical material to develop critical thinking and an understanding of gender inequalities in the "development process." (Prerequisites: at least Semester 3 standing.)(Also offered as Work and Employment Issues WORK-2600.)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (682, N' Gender, Power, and Language', N'2700', N'WGST', N'This course makes visible the politics hidden in the English language. Students learn how to identify and challenge aspects of language structure and use which perpetuate power and privilege. Topics include naming, slang, metaphor, grammar rules, and humour. (Prerequisite: Semester 3 standing or above and one Women''s and Gender Studies course or consent of the instructor.) (Can be taken for either Social Science or Arts credit.)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (683, N' Boys to Men: A critical exploration of masculinities', N'2800', N'WGST', N'This course uses personal narrative and popular culture to examine the lived realities of boys and men. The course explores dominant models of masculinity in order to challenge gender stereotypes that often flatten and narrow the lives of boys and men, and also the lives of women and girls. (Semester 4 standing or above.)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (684, N' Special Topics in Women''s Studies', N'2990', N'WGST', N'Special Topics courses will be offered occasionally to meet a demonstrated academic need that cannot be satisfied by regular course offerings. (Prerequisites: will vary depending on the subject.)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (685, N' Feminist Theories', N'3050', N'WGST', N'This course traces the evolution of contemporary feminist theories, the connection between theory and practice, and illustrates the significance of theory-driven practice on women''s lives. (Prerequisites: two Women''s and Gender Studies (WGST-) courses at the 2000-level or above and at least semester 5 standing.)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (686, N' Frameworks for Feminist Research', N'3060', N'WGST', N'An exploration of the diverse approaches to feminist research in a variety of fields. Students will examine the core questions and approaches that various disciplines bring to the study of women. (Prerequisites: Two courses at the 2000-level or above from Women''s and Gender Studies or consent of the instructor.)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (687, N'Women and the Law', N'3100', N'WGST', N'This course examines the relationship between gender inequality and the legal system. Topics include abortion, marriage, divorce, custody, equal pay, sexual harassment, rape, pornography, and prostitution. Students are introduced to basic legal research tools, such as statutes, regulations, cases, and legal literature. (Prerequisites: two Women''s and Gender Studies courses.)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (688, N' Victims, Survivors, and Warriors: Violence in the Lives of Women and Girls', N'3300', N'WGST', N'An interdisciplinary exploration of male violence against women and girls locally and globally. The course will explore the power of language to shape our understanding of issues, the many forms of subtle and explicit violence, the impact of violence on the individual and the status of women, and the creative resistance of women and girls, among other issues. (Prerequisites: two Women''s and Gender Studies courses and at least semester 4 standing.)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (689, N' Love, Honour, and Obey: Marriage and Gender', N'3340', N'WGST', N'Romantic heterosexual love shapes contemporary notions of marriage for both straight and gay women. But marriage is about much more than love. It shapes women’s lives and organizes relations between women and men. This course examines the interpersonal, economic, social, cultural, and legal aspects of marriage past and present, primarily in North America. (Pre-requisite: One Women''s and Gender Studies course or HIST-2510 or permission of the instructor.)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (690, N' Women, War and Peace', N'3400', N'WGST', N'This course examines the various and distinct ways in which women experience war and peace, in both historical and contemporary contexts. Topics include home fronts during wartime in both combat and non-combat zones, women''s peace activism, displacement, war crimes against women, women in combat, and media coverage of women and war/peace across the 20th and 21st centuries. (Also offered as History HIST-3400.) (Prerequisite: one 2000-level Women''s and Gender Studies or History course or POLS-2600 or consent of the instructor.)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (691, N'Social Work and Violence', N'3470', N'WGST', N'Examines aspects of violence in society, particularly against marginalized groups. The primary focus is on generalist social work intervention related to violence. (Open to senior students. Required course for Social Work/Women''s and Gender Studies students; elective for BSW students. Pre-requisite: One Women''s and Gender Studies (WGST-) course or permission of the instructor. (Also offered as SWRK-3470.)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (692, N' Practical Strategies for Social Change: Intervening to Prevent Sexual Violence', N'3500', N'WGST', N'This course introduces students to sexual violence as a social problem why it matters, the forms it takes, and how it can be changed. The importance of personal and community responsibility for social change is emphasized. This course also provides students with the background knowledge that is needed to successfully teach sexual violence prevention workshops for their peers.Restricted to students who have attained a cumulative GPA of 66% or higher at the time of application. (Prerequisite: Semester 4 standing or above and permission of the instructor by online application at bystander initiative.ca) (Also offered as SOSC-3500, PSYC-3500, SWRK-3500, and SACR-3500.)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (693, N' Women, Power, and Environments', N'3530', N'WGST', N'This course focuses on environmental issues as they affect women across cultures. It provides a feminist critical analysis of the power relations in modern societies that cause environmental degradation and examines the theories, policies, and institutions that contribute to unsustainable practices. Emphasis is placed on the women-nature debate within various environmental social movements and the historical role women have played as activists. (Also offered as Sociology SACR-3530.) (Prerequisite: Semester 3 or above standing and one course at the 2000-level or above from Women''s and Gender Studies or Sociology.)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (694, N' Gender, Space, and Time', N'3540', N'WGST', N'An examination of sociological and anthropological approaches to the study of space-time relations within the field of gender studies, including a focus on the development of gendered environments and cultural practices. (Also offered as Sociology SACR-3540.) (Prerequisites: SACR-2140 or WGST-1000 and semester 5 or higher standing.)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (695, N' Feminist Social Work Practice', N'3550', N'WGST', N'This course prepares students to apply the principles, processes, and techniques of feminist social work practice. Required course for Social Work/Women''s and Gender Studies students; elective for BSW students (Prerequisites:(SWRK-3360, SWRK-3370 plus three (WGST-xxx) Women Studies courses or, consent of instructor.) (Also offered as SWRK-3550)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (696, N'Women, Knowledge, and Reality', N'3590', N'WGST', N'An exploration of feminist theories about knowledge and reality that inform and are informed by scholarship in Women''s Studies. Students examine how gender might affect identity, reasoning, objectivity, and evidence, and in turn, how such variations might affect feminist political practices. (Prerequisites: Two courses at the 2000-level or above from Women''s and Gender Studies and/or Philosophy and at least semester 5 standing.) (Also offered as Philosophy PHIL-3590.) (Can be taken for Arts credit.)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (697, N' Madonnas, Monsters, and Machines: Representations of Motherhood', N'3700', N'WGST', N'This course examines representations of mothers and motherhood in literature, arts, and popular culture, and the effects of these representations on women’s lives. Topics may include constructs of “good” and “bad” mothers, diverse pathways to motherhood, availability and use of reproductive technologies, and feminist activism around mothering. (Prerequisite: Semester 4 standing or above and two Women''s and Gender Studies courses.)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (698, N' Masculinity, Crime and Punishment', N'3800', N'WGST', N'Students examine the gendered nature of violence, crime and punishment in Canada today. Students will investigate how masculinity is lived on the streets, in the courts and in prisons, and imagine whether new forms of masculinity are possible. (WGST-2800 or permission of the instructor.)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (699, N'WGST-3850: Tough Chicks: Representations of Women’s Strength and Anger in Popular Culture and Society', N'3850', N'WGST', N'This course examines popular interest in “tough chicks.”. Students investigate the social construction of women’s and girls’ anger and aggression in fiction, popular media, and real life. and how these understandings are rooted in sexist, racist, homophobic, and other oppressive discourses. Students are encouraged to examine how their own lives and the lives of other women are impacted by these understandings. This course can be used for either Arts of Social Science credit. (Prerequisites: WGST-1000 and one 2000-level Women''s and Gender Studies (WGST-) course or consent of the instructor)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (700, N' Feminisms in the 21st Century', N'3900', N'WGST', N'This course investigates the meaning and importance of contemporary feminisms through literature, zines, websites, and "manifestos" written by, for, and about young women. Specific topics of interest are young women''s activism with respect to the environment, globalization, and the media, as well as their efforts to confront racism, sexism, and classism. (Prerequisites: two Women''s and Gender Studies courses and at least semester 3 standing.)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (701, N'Special Topics', N'3990', N'WGST', N'The content will vary to reflect student need and faculty expertise and may be offered as a cross-listed course with other programs. (Prerequisite: at least semester 4 standing. Additional prerequisites may apply depending on subject.)(May be repeated for credit if content changes.)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (702, N' Exploring the Feminist Voice', N'4000', N'WGST', N'Students apply theoretical learning in Women’s Studies to individual lived experiences and actions. Through writing and other modes of representation, they communicate their feminist ideas to target audiences. (Prerequisites: WGST-3590 (or WGST-3050) and WGST-3060 or consent of the instructor.)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (703, N' Women in Protest', N'4100', N'WGST', N'This course examines women at the forefront of protest in historical and contemporary contexts. It analyzes gendered constructs of protest and social and political change. Students will study the role of women in protest movements such as suffrage, reproductive rights, anti-racism, labour rights, environmental justice, anti-globalization, and nationalist and religious movements. Case studies are explored in detail, with particular emphasis on primary-source material from women activists themselves. These studies are complemented by an examination of feminist, social movement, and protest theories. (Prerequisite: WGST-2510 or HIST-2510 and one 3000-level Women''s and Gender Studies course or consent of the instructor.)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (704, N' Practicum in Social Change', N'4500', N'WGST', N'Supervised practicum in a university setting. Students consolidate and enhance their knowledge of sexual assault and bystander intervention. Students co-facilitate the Bringing in the Bystander® In Person Prevention program for one or more small groups of students on campus. The practicum experience equips students to deliver educational content on sensitive issues. (Prerequisite: PSYC-3500/SWRK-3500/SACR-3500/WGST-3500 and permission of the instructor.) (Also offered as SOSC-4500, PSYC-4500, SWRK-4500, and SACR-4500.)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (705, N' History of Gender and Sexuality', N'4630', N'WGST', N'This course explores major themes in the history of gender and sexuality. These may include reproduction, contraception, and abortion; gender, race, and power; sexuality and the state; heterosexual relations and marriage; gay, lesbian, and transgender identities. Time period and geographical region will vary with the instructor.)(Prerequisite: Semester 5 or above standing and one of HIST-2490, HIST-2500, or HIST-2510/WGST-2510 or permission of the instructor.)(Also offered as History HIST-4630.)', 3, 13, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (706, N' Principles of Financial Accounting', N'1510', N'ACCT', N'An introduction to the theory and concepts of financial accounting including generally accepted accounting principles and issues as to classification, recognition, realization, measurement and reporting. The emphasis of the course is from the perspective of the user of accounting information, allowing the student to become familiar with the information available and its content value. (Prerequisites or corequisites: ECON-1100, MATH-1980 (or equivalent) and STEN-1000).', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (707, N' Intro to Financial Accounting Theory', N'2510', N'ACCT', N'This is the first of 3 courses of intermediate accounting that presents the current developments in the theory of generally accepted accounting principles and CICA (Canadian Institute of Chartered Accountants) standards are introduced. An in-depth, theoretical examination of the determination, measurement, classification and reporting of assets is presented. The conceptual framework of accounting is stressed.(Prerequisites: ACCT-1510. Business students must also have completed MGMT-1000).', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (708, N' Accounting Theory I', N'2520', N'ACCT', N'This is the second of three courses of intermediate accounting theory that provides an in-depth examination of the determination, measurement, classification and reporting of liabilities and owners’ equity. Emphasis is given to the accounting use of the actuarial techniques in the accounting for bonds, pensions, and leases. Where applicable, the interpretation of accounting theory and concepts is presented for transaction analysis, measurement, and classification. The conceptual framework of accounting is stressed. (Prerequisite: ACCT-2510.', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (709, N' Principles of Managerial Accounting', N'2550', N'ACCT', N'An introduction into management’s use of internal accounting information for planning, managing, controlling and evaluation of business operations. Topics include cost concepts and costing techniques (including activity based costing), budgeting, cost-volume-profit analysis, standard costing, performance evaluation and product pricing. (Prerequisite ACCT-1510 or consent of instructor).', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (710, N' Accounting Theory II', N'3520', N'ACCT', N'This is the final course in the intermediate accounting theory sequence covering various special topics in financial accounting such as EPS, interperiod tax allocation, a rigorous study of accounting changes and error analysis and of the statement of cash flows, interim and segmented reporting. Financial statement analysis including business valuations will be covered, both from a quantitative and qualitative viewpoint and the concept and techniques of earnings management are also explored. Cases are used to integrate theory and practice. The conceptual framework of accounting is stressed (Prerequisite: ACCT-2520 or consent of the instructor.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (711, N' Advanced Managerial Cost Accounting and Analysis', N'3560', N'ACCT', N'This course is designed to focus on the role and use of accounting information in management decision making, and for formulating policy and strategy. The application of some of the advanced techniques for planning, controlling and performance evaluation will be discussed. Behavioural and ethical issues will be considered. (Prerequisite: ACCT-2550 with a minimum grade of 65%.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (712, N' Accounting Information Systems', N'3580', N'ACCT', N'The design and operation of manual and computerised accounting systems. The study of control environment, management and control of transactions and accounts, such as, accounts receivable, accounts payable and inventory. Emphasis will be given to the acquisition and input of information into accounting information systems; modes and methods of file structures and storage of accounting information; retrieval inquiry and report creation of information in files as well as financial statement preparation, analysis, and managerial decision making. Related issues such as audit trail, data retrieval, and data security will also be covered. (Prerequisite: ACCT-2550 and MSCI-2130.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (713, N' Auditing I', N'3600', N'ACCT', N'An introductory course designed to provide a broad foundation for all major aspects of auditing. This course focuses on objectives, concepts, standards, strategies, processes, and communications relating to external audits. Other services provided by public accountants and current developments affecting auditing and the auditing profession are considered. (Pre-requisite ACCT-2510, Pre or co-requisite ACCT-3580.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (714, N' Taxation I', N'3610', N'ACCT', N'This is the first of two courses designed to examine the Income Tax Act. This course focuses on the determination of residency and of income for tax purposes. Other tax related topics such as tax planning concepts, and concepts underlying the Act will be discussed. (Prerequisite: ACCT-2510)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (715, N' Advanced Accounting I', N'4570', N'ACCT', N'A study of concepts, standards and procedures underlying intercorporate investments including portfolio investments, investments involving significant influence, and investments involving control. The preparation of consolidated financial statements under a variety of circumstances is studied in detail. Other topical areas, such as foreign currency transactions and translation, governmental accounting and accounting for not-for-profit organisations will also be covered. (Prerequisite: ACCT-3520.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (716, N' Advanced Accounting II', N'4580', N'ACCT', N'This course examines various theoretical perspectives in financial theory such as decision model approach, information economics, capital markets theory, agency theory, economic consequences, management incentives for financial reporting, earnings management, and accounting policy choice. Standard setting issues and other current and emerging issues in financial accounting theory and practice are discussed. Cases and readings are used to further integrate theory and practice and concepts from previous accounting courses. (Prerequisite or co-requisite ACCT-3520) (Open to Business students only.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (717, N' Advanced Topics in Managerial Accounting', N'4590', N'ACCT', N'An elective advanced topics course that explores the different types of organizational controls. It focuses on the key decisions that must be made in using controls, such as choices of performance measures, performance standards and targets, and performance-based incentives. Limitations of traditional financial performance measures are discussed (e.g., their tendency to make managers excessively short-term oriented) and recently developed approaches to deal with these shortcomings are analyzed (e.g., EVA, Balanced Scorecard). The course is designed to develop skills that are desirable in managers, management consultants, compensation consultants, financial specialists, or human resource specialists. The course is taught by the case method of instruction. The cases allow for the exploration of the management control issues in a broad range of settings such as start up firms, manufacturing firms, service organizations of different sizes. (Prerequisite: 4th year students only, or by permission of the instructor.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (718, N' Auditing II', N'4600', N'ACCT', N'This course is designed to provide an in-depth knowledge of the major aspects of auditing. It will examine topics such as audit sampling; public accountants'' communications to users of accounting and non-accounting information; and emerging issues in auditing. (Prerequisites: ACCT-3580 and ACCT-3600.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (719, N' Taxation II', N'4610', N'ACCT', N'This course will focus on the computation of taxable income for individuals and corporations, and determination of tax. Tax planning techniques in business in a variety of situations will be discussed and other topics such as the Goods and Services Tax will also be considered. (Prerequisite: ACCT-3610.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (720, N' EDP Auditing', N'4620', N'ACCT', N'This course is designed to focus on the integration of auditing concepts, standards and procedures in a computerized environment. It will examine EDP general and application controls, the similarities and differences between manual and EDP systems from the auditor''s perspective, and will introduce computer-assisted audit techniques, and emerging technologies in EDP auditing. (Prerequisites: ACCT-3580 and ACCT-3600.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (721, N' Special Topics in Accounting', N'4910', N'ACCT', N'This is a seminar course covering major concepts or current problems or issues in the area of Accounting. The topic to be covered in a particular semester will vary and will be announced in the previous semester. Interested students should consult the Area Chair of Accounting. (May be taken for credit twice if content is different.) (Prerequisite: consent of the instructor.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (722, N' Independent Study in Accounting', N'4950', N'ACCT', N'This course must be taken under the direct supervision of an accounting faculty member. (May be taken for credit twice if content is different.) (Prerequisite: consent of the instructor and Area Chair.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (723, N' Business Communications', N'1000', N'MGMT', N'Research has shown that effective communication skills are as necessary to career advancement as technical competence, work experience and academic qualifications. The importance of communication skills is not surprising when you consider that the average business manager spends 75-80% of the day communicating in one form or another. Thus, the focus of this course is to help you to sharpen your ability to communicate and manage conflict effectively - with individuals, within small groups, and with large audiences. This course stresses practical skill building for leaders. Time is spent on communication concepts and techniques, planning, organizing and making presentations, as well as the application of behavioural science theory to business communication and leadership. (Prerequisite or corequisite: STEN-1000) (Not open to non-Business students.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (724, N' Management and Organizational Life', N'2400', N'MGMT', N'This course provides an overview of the basics of management theory, coupled with a more applied view of how that theory may work in an organizational setting. It will build upon the understanding of strategic positioning and context provided by its new prerequisite Introduction to Business (STEN-1000) where stakeholders, the environment, and business issues provide the background for understanding the challenges facing today’s manager. The course will provide some experiential opportunities to develop team and leadership skills, while looking at what affects the role of the individual and the group within the structure of the organization. An understanding of the influences upon and ways to motivate behaviour in organizations will be developed. (Prerequisite: STEN-1000. Business students must also have completed MGMT-1000)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (725, N' Human Resources Management', N'2430', N'MGMT', N'Human Resources Management (HRM) is concerned with the management of people at work - a key responsibility of people at work - a key responsibility of every manager within an organization. Topics include: integrating HRM decision making within a business strategy, recruiting and selecting qualified employees, developing and evaluating human resources and retaining and motivating employees through compensation systems, labour relations, and quality of work life initiatives. In recognition of the importance of the increasingly global context to Canadian organizations, the course incorporates a continuing international focus. (Prerequisite: STEN-1000. Business students must also have completed MGMT-1000.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (726, N' Business Ethics in a Global Context', N'3000', N'MGMT', N'This required third year course examines ethical issues encountered in the management of business organizations operating domestically and globally. The course is designed to increase student awareness of the ethical dimension of business and to provide a decision making model for resolving ethical dilemmas encountered in business operations. The course begins with an examination of the basic philosophical perspectives on ethical behaviour and then focuses on issues such as discrimination and employee equity, environmental effects of business activities and advertising ethics. The overall goal of the course is to contribute to the development of the moral manager. (Prerequisites: MGMT-2430.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (727, N' Compensation Management', N'3420', N'MGMT', N'This course is intended to give an understanding of the power of organizational rewards and managing this power for organizational effectiveness. This course entails an outline of the major concepts and principles of equitable reward design within organizations. Topics include the planning of salary and wages, pay equity, incentive pay, benefits, non-financial rewards, and the clarification of the linkages between rewards and desired behaviours. Special emphasis is given to reward system design and the evaluation of compensation program effectiveness. (Prerequisite: MGMT-2430.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (728, N' Labour-Management Relations', N'3440', N'MGMT', N'A comprehensive introduction to the dynamic world of labour and management relations focusing on the unionized sector. The problems, issues, and challenges growing out of the labour-management relationship are examined against a broad background of information, including: the differences between union and non-union workplaces; the development and operation of labour unions; the impact of labour legislation; the negotiation and administration of collective agreements; and the resolution of industrial conflict. Given the size and importance of this unionized workforce in Canada, the knowledge and skills developed in this course have wide application. (Prerequisite: MGMT-2430 or Semester 3 or above standing for students in the Labour Studies program.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (729, N' International Human Resources Management', N'3830', N'MGMT', N'The focus of this course is the management of people in the international context. Issues covered include culture, communication, and differences in the economic, social and legal environments as they affect people in organizations. Particular attention is paid to staffing, training, and compensating parent country nationals, host country nationals and third country nationals. (Prerequisites: MGMT-2430)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (730, N' Training and Development', N'4410', N'MGMT', N'This course has an experiential focus: student teams are responsible for developing and presenting their own training programs. The focus of this course is on the three major aspects of training and development efforts: (1) needs assessment, (2) program development, and (3) evaluation. Course topics include the design of training programs, adult learning models, development managerial skills, and the design of effective workshops. This course has an experiential focus; student teams are responsible for development and presenting their own training programs. (Prerequisite: MGMT-2430.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (731, N' Organization Design', N'4450', N'MGMT', N'control, power and politics, intergroup relations and conflict, structure-strategy relations and organizational renewal. This course utilizes the case method and other applied problem-solving skills in analyzing and evaluating organizational structures and processes. (Prerequisite: MGMT-2400.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (732, N' Labour Relations Law and Employment Legislation', N'4480', N'MGMT', N'Legislation, administrative agencies and courts play a significant role in shaping employer-employee relationships. this course aims to increase the knowledge and provide analytical skills to students who are interested in employment relationships in union and non-union workplaces. The course includes an analysis of labour relations law, employment standards law, the occupational health and safety law. Emphasis will be placed on Ontario laws. Lectures and case discussions will be used. (Prerequisite: MGMT-3440.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (733, N' Negotiations', N'4490', N'MGMT', N'Various aspects of union-management negotiations in the private and public sector will be discussed. A key aspect of the course is a bargaining simulation played by students assuming the role of union and management negotiators. Grievance arbitration and other dispute settlement procedures will also be discussed. Students will learn negotiation and conflict resolution skills relating to the union-management relations. (Prerequisite: MGMT-2430 or Semester 3 or above standing for students in the Labour Studies program.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (734, N' Organizational Staffing', N'4510', N'MGMT', N'At the heart of the organizational staffing process is the forming of matches between people and jobs that will result in an effective workforce for the organization. The course identifies the key influences upon, and components of staffing and overviews such support activities as job analysis, external and internal recruitment, selection and the assessment of staffing effectiveness. The course goes on to examine the deployment processes that represent the end pont of the person/job match. This match does may not be permanent, and so the course does go on to give consideration to equitable termination and outplacement practices which are required when people leave the organization. (Prerequisite: MGMT-2430.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (735, N' The Management of Organization Health, Wellness and Safety', N'4520', N'MGMT', N'Health and safety plays a prominent role in the development of a strong organizational culture and a productive workforce. This course emphasizes the key goal of managers and HR professionals to create, develop and nurture a culture that is fully aware of the importance of safety and the advantages of a proactive employee wellness culture, and is willing to take the necessary steps to achieve it. Students will evaluate practices in the areas of health, safety, security and Workers'' Compensation and the importance of due diligence and meeting safety legislation. An emphasis in the course is the adoption of proactive programs of employee wellness and assistance, careful medical and safety testing and the implementation of strategies to minimize compensation costs and maximize compliance with safety guidelines. (Prerequisite: MGMT-2430.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (736, N' Diversity in the Workplace', N'4810', N'MGMT', N'This course will address the knowledge and skills managers must develop in meeting the opportunities and challenges created by the diversity in the labour force. It will draw on the literature from a number of disciplines in focussing on interpersonal relationships as managers interact with and work with persons who are different from themselves. The human rights legislation will provide the framework for discussions on managing and valuing diversity in terms of gender, age, race, religion, ability and other groups. The course will use lectures and case discussions on the role of union and management in implementing equity in the workplace. (Prerequisite: MGMT-2430 or Semester 3 or above standing for students in the Labour Studies program.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (737, N' Human Resources Planning', N'4850', N'MGMT', N'This course is concerned with planning of the human resources needs of organizations, focusing, in particular, on the role of the Human Resources Management function in this task. The objective is to provide an understanding of how the essential elements of the human resources planning process, in both unionized and non-unionized organizations, can be designed to match the wider organizational context. Topics include the assessment of human resources strategy and the application of planning principles to the different activity areas of human resources management, such as staffing, development and the management of diversity. An ongoing theme is the evaluation of how strategic human resources management contributes to organizational effectiveness. (Prerequisite: MGMT-3440.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (738, N' Special Topics in Management and Labour Studies', N'4910', N'MGMT', N'This is a seminar course covering major concepts or current problems or issues in the area of Management and Labour Studies. The topic to be covered in a particular semester will vary and will be announced in the previous semester. Interested students should consult the Area Chair of Management and Labour Studies. (Prerequisite: consent of the instructor.) (May be taken twice for credit if content is different.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (739, N' Independent Study in Management and Labour Studies', N'4950', N'MGMT', N'The student, with the agreement of the instructor, will select, research and report on a topic. (Prerequisite: consent of the instructor and Area Chair). (May be taken for credit twice if content is different.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (740, N' Personal Finance', N'2000', N'FINA', N'This course provides an introduction to personal financial planning to non-Business students. Topics covered include: the planning process and goal setting, personal budgeting, spending and saving, assessing alternative sources of credit, debt management, basic tax planning, the fundamentals of investing, and financial planning information sources. The goals of the course are i) improving financial literacy and ii) empowering students to improve their personal financial security. (Pre-requisite: Grade 11 math or equivalent.) (May not be used for credit in any Business program.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (741, N' Business Finance I', N'2700', N'FINA', N'This course serves as an introduction to the area of business finance. The primary objective is to understand the fundamental concepts and principles of financial management of the business enterprise. After an introduction to the goal financial management, the course will cover the valuation of financial and real investments, risk and return, financial analysis, planning and control, and working capital management. International financial management will also be introduced. (Prerequisites: ACCT-1510, MATH-1980 or equivalent,MSCI-1000 and STEN-1000. Business students must also have completed MGMT-1000.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (742, N' Business Finance II', N'2710', N'FINA', N'This course focuses on long-term corporate financial decisions. The goal is to develop an understanding of the concepts and principles of the management of capital assets and resources. Topics include capital budgeting, cost of capital, capital structure, sources of long-term financing, and corporate risk management. (Prerequisites: MSCI-2020 (or equivalent) and FINA-2700.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (743, N' Intermediate Finance', N'3710', N'FINA', N'This is a recommended course for students wishing to continue in finance and compulsory for those aiming for a finance concentration. The course covers key topics in capital markets and corporate finance that lay the foundation for material to be covered in advanced finance courses. Areas covered include: fixed income markets and interest rate determination; raising funds in equity markets; the cost of capital; derivatives markets and applications to business finance; and the market for corporate control. (Prerequisite: FINA-2710.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (744, N' Investments', N'3720', N'FINA', N'Appraising bonds, preferred, and common stocks as vehicles for investment. The course also involves the study of alternative investments, the market setting, technical analysis, and securities legislation in Canada. (Prerequisite: FINA-2710.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (745, N' Working Capital Management', N'3730', N'FINA', N'A seminar in working capital management using case studies. Emphasis is placed on domestic and international cash management, control of accounts receivable, principles of inventory management, short and intermediate term financing. (Prerequisite: FINA-2710.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (746, N' Financial Markets and Institutions', N'3780', N'FINA', N'A central theme of this course is the management of Canadian financial institutions through the analysis of their assets and liabilities. This course examines different types of risk exposures faced by these institutions. With real-world examples, this course will also address the current institutional issues in the context of domestic and international financial markets. (Pre-requisite or co-requisite: FINA-3710.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (747, N' International Financial Management', N'3790', N'FINA', N'A study of international corporate financial management, international banking, and financial markets. Emphasis is placed on foreign exchange and exposure management. The financial problems and risks faced by multinational corporations and banks are also discussed. (Co-requisite: FINA-3710.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (748, N' Portfolio Management', N'4720', N'FINA', N'The shaping of portfolios to fulfill the needs of individuals and institutions including risk-return concepts, diversification, beta analysis, and market efficiency. (Prerequisite: FINA-3710.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (749, N' Corporate Financial Strategy', N'4740', N'FINA', N'A seminar course in long-term financial management. Particular attention is directed toward long-term sources of funds, the firm''s capital structure, and the cost of the various sources of long-term funds. Principles are illustrated by means of case studies. (Prerequisite: FINA-3710.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (750, N' Derivatives and Risk Management', N'4770', N'FINA', N'An introduction to the use of options and futures with an emphasis on managing risk. Review of the markets and trading of equity and currency options; forwards and futures contracts; and options on futures. Principles of the valuation of options and futures. Application of hedging techniques under a variety of circumstances such as for personal investments, portfolio management, corporate risk management, foreign exchange risk management and agriculture. (Prerequisite: FINA-3710.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (751, N' Pension Finance and Management', N'4780', N'FINA', N'The course provides an introduction to the principles of fiduciary management of pensions. Important concepts in pension finance such as asset liability management; tactical asset allocation; performance evaluation and risk management will be discussed. The course will also provide an overview of the management and regulatory framework of pensions in Ontario and Canada. (Prerequisite: FINA-3710.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (752, N' Special Topics in Finance', N'4910', N'FINA', N'This is a seminar course covering major concepts or current problems or issues in the area of Finance. The topic to be covered in a particular semester will vary and will be announced in the previous semester. Interested students should consult the Area Chair of Finance. (May be taken for credit twice if content is different.) (Prerequisite: consent of the instructor.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (753, N' Independent Study in Finance', N'4950', N'FINA', N'The student, with the agreement of the instructor, will select, research and report on a topic. (Prerequisite: consent of the instructor and Area Chair.) (May be taken twice for credit if content is different.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (754, N' Introduction to Business Data Analysis', N'1000', N'MSCI', N'This course focuses on giving students the knowledge and skills to be used in a world in which spreadsheets are an integral part and which requires graduating business students to be proficient in its use. It also provides students with the knowledge and skills to be used in other courses in which spreadsheets’ powers can play an important role in analyzing data and presenting information in a professional manner. This knowledge and skills include: effectively entering data on spreadsheets so that they can be efficiently manipulated and converted into relevant information, both numerical and graphical; and, creating and interpreting this relevant information in a professional manner. To accomplish this, students will learn how to create professional looking graphs and charts and how to use and apply various Excel functions and capabilities including pivot tables, filtering, sorting, merging, lookup formulas, conditional formulas, relative and absolute formulas, range labelling, descriptive statistics functions, probability functions and financial functions. (Corequisites: MATH-1980)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (755, N' Business Data Analysis', N'2020', N'MSCI', N'Statistical inference in a business environment. Topics include one population inferences, two population inferences, analysis of variance, Chi-Square tests, linear regression and correlation. (Prerequisites: STEN-1000,MSCI-1000 and MATH-1980 (or equivalent).', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (756, N' Introduction to Management Information Systems', N'2130', N'MSCI', N'This course provides an overview of Management Information Systems (MIS). Topics include: various types of MIS such as Information Reporting Systems, Decision Support Systems, and Office Automation Systems; introduction to hardware and software technology; personal, functional and enterprise information systems; and the value added to an organization by MIS.(Prerequisite: STEN-1000. Business students must also have completed MGMT-1000 and MSCI-1000. Non-Business students must have completed COMP-1047 or COMP-1400 or its equivalent)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (757, N' Quantitative Decision Models I', N'2200', N'MSCI', N'An introduction to the use of quantitative approaches to decision making. Topics include linear programming (model formulation and applications, computer solution, sensitivity analysis, and interpretation), transportation model, project management; PERT/CPM, inventory control. (Prerequisites: MSCI-2020 (or equivalent), and MATH-1980 (or equivalent). Business students must also have completed MGMT-1000.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (758, N' Introduction to Data Science for Business', N'2230', N'MSCI', N'This course provides an overview of the basic principles and approaches used to analyze and extract key insights from business data. All phases of the data science process will be examined in some depth including problem formulation, data acquisition, data modeling, model evaluation, and model deployment. Particular emphasis will be placed on expressing business problems in forms that can be addressed using data science tools and techniques. As an introduction to data science, this course focuses on providing a broad survey of the field as a foundation for more advanced level courses. (Prerequisite: MSCI-2130)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (759, N' Statistical Quality Design and Control', N'3050', N'MSCI', N'The course discusses some of the important statistical concepts and methods for quality design and improvement. Topics include: statistical process control, development and interpretation of different kinds of control charts for variable and attribute data, designs of experiment for product/process improvement. A software package may be required to simulate the operation of an actual process, and to illustrate the methodology. (Prerequisites: MSCI-2020 (or equivalent))', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (760, N' Introduction to Data Base Management', N'3110', N'MSCI', N'A study of the planning and design of data base systems in a business organization. Topics include: data concepts and modelling, data base planning, data structure and storage techniques, and data base design. A micro-computer-based data base software package will be used for regular assignments and team projects. (Prerequisite: MSCI-2130.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (761, N' Business Process and Data Analytics', N'3120', N'MSCI', N'This course integrates the macro (processes) and micro (data analysis) view of businesses. The first half of this course focuses on the concept and evolution of business process management (BPM) and its impact on organizations. Topics will include how organizations benefit from BPM to enhance its competitiveness, sustainability, innovation and growth; techniques and evolution of process mapping; workflow management; and enterprise applications. The second half of this course focuses on the data underlying business processes. Topics will include data visualization and predictive modeling techniques using state-of-the-art data analysis software. (Prerequisites: MSCI-2130)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (762, N' Quantitative Decision Models II', N'3200', N'MSCI', N'An introduction to the use of quantitative approaches to decision making under uncertainty. Topics include: inventory management under probabilistic demand, waiting line models or queues, computer simulation, decision analysis, multi-criteria decision making. (Prerequisite: MSCI-2200.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (763, N' Data Science Tools and Methods', N'3230', N'MSCI', N'This course offers an in-depth exploration of key tools and techniques used to assemble, analyze, and evaluate large business datasets obtained from diverse sources. Techniques for communicating the results of this analysis and techniques for introducing analytical models into business processes will also be examined. Students will develop the practical skills needed to acquire, manage, and analyze business data using current methodologies and software applications. (Prerequisite: MSCI-2230)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (764, N' Operations Management I', N'3310', N'MSCI', N'An introduction to the problems and techniques encountered in the production of goods and services. Topics include: forecasting, capacity planning, facility location and layout, aggregate planning, inventories and materials requirement planning. (Prerequisite: MSCI-2200, anti-requisite: MSCI-3410.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (765, N' Supply Chain Management I: Introduction and Fundamentals', N'3410', N'MSCI', N'This course introduces problems and techniques encountered in the management of supply chain. Topics include: supply chain performance, drivers, and metrics, design of distribution networks, uncertainties along the supply chain and demand forecasting, aggregate production planning, managing inventory under uncertainty, product availability and supply chain profitability. (Prerequisite: MSCI-2200.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (766, N' IT in Project Management', N'4200', N'MSCI', N'This course focuses on introducing students to the organizational, managerial, and technical constructs associated with IT project and program management. The nine PMI (Project Management Institute) specified knowledge areas of project management are explored, while introducing students to a wide array of tools and techniques that seasoned project managers use. Students are introduced to popular IT tools such as the MS Project (how it can help the managers manage projects) and the SAP Project Systems module (how it is integrated with Sales and Distribution, Materials Management, Financial Accounting, Controlling, and Human Capital Management other functions in the organization). (Prerequisites: MSCI-2130 and MSCI-2200)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (767, N' Business Analytics in Practice', N'4230', N'MSCI', N'In this course students will apply their business analytics knowledge and skills to the resolution of practical business problems in marketing, human resource management, supply chain management, accounting, finance, and other business related domains. Students will be challenged to analyze and understand business problems in these areas, formulate and evaluate data analytic models that can address these problems, acquire the data needed to support their models, and then refine and deploy these models within business settings. Emphasis will be placed on linking business analytic knowledge and skills to the problems encountered in specific business domains. (Prerequisites: MSCI-3110, MSCI-3120, and MSCI-3230)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (768, N' Operations and Supply Chain Management II', N'4310', N'MSCI', N'The course explores other substantive and analytical issues in the planning and control of operations and supply chain management. Topics include: sales and operations scheduling,; and supply chain decisions. Team or individual presentations on selected topics may be required. (Prerequisite: MSCI-3310 or MSCI-3410.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (769, N' Special Topics in Management Science', N'4910', N'MSCI', N'(May be taken for credit twice if content is different.) (Prerequisite: consent of the instructor.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (770, N' Independent Study in Management Science', N'4950', N'MSCI', N'(Prerequisite: consent of the instructor and Area Chair.) (May be taken for credit twice if content is different.) The student, with the agreement of the instructor, will select, research and report on a topic.', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (771, N' Modeling and Analysis in Management Science and Systems', N'4980', N'MSCI', N'This course is concerned with modelling, analysis and presentation of results using tools and techniques developed in the areas operations management, operations research, statistics and information systems. Problems are selected from case studies, simulation and real-life projects. A major part of the evaluation is based on team and individual reports and presentations. (Prerequisite: MSCI-3310)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (772, N' Principles of Marketing', N'1310', N'MKTG', N'An introduction to the principles, concepts and techniques of marketing. A significant objective of the course is the development of a basic understanding of the marketing process and its role in the organization, in the economy, and in global markets.', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (773, N' Marketing Problems-Applications and Decisions', N'2320', N'MKTG', N'The application of concepts and techniques in marketing through the use of cases and simulation gaming. The course will apply the concepts learned in MKTG-1310, Principles of Marketing, in a managerial, decision-making format. (Prerequisites: MGMT -1000 and MKTG-1310 Pre or corequisite: FINA-2700.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (774, N' Research Methods in Marketing', N'3320', N'MKTG', N'The use of analytical methods to improve the efficiency of the marketing operations of companies and other organizations with emphasis on the development of a broad understanding of the uses and methods of research as applied to marketing. (Prerequisite: MSCI-2020 and MKTG-1310.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (775, N' Consumer Behaviour', N'3340', N'MKTG', N'An analysis of consumer and buyer behaviour and their implications for marketing decisions. The course examines theories of, and research in, consumer behaviour through cases and group projects. (Prerequisite: MKTG-1310.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (776, N' Marketing Channels', N'3350', N'MKTG', N'The subject of marketing channels deals with the flow of ownership of a product from manufacturer to final user. Major topics include principles of marketing channel design, the types and roles of wholesalers and retailers, the impact of the other elements of the marketing mix, and issues in marketing channel management, such as power, conflict and legal concerns. (Prerequisite: MKTG-2320).', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (777, N' Quantitative Analysis for Marketing Decisions', N'3370', N'MKTG', N'The application of quantitative techniques to marketing problems and strategy. (Prerequisite: MSCI-2020 and MKTG-1310.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (778, N' Retail Marketing Management', N'3380', N'MKTG', N'An introduction to retailing concepts and the examination of various managerial issues related to retailing, including retail marketing strategy formulation, customer care and service, product assortments, retailer-supplier relations, pricing, inventory control, and location and layout decisions. (Prerequisite: MKTG-2320.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (779, N' Logistics and Supply Chain Management', N'3390', N'MKTG', N'The planning, implementing and controlling of logistics activities associated with the flow of goods and related information, from the raw materials stage to the end user. This course discusses the fundamentals of business logistics and supply chain management, including transportation, order management, warehousing, reverse distribution, logistics information technology, and the impacts of product, price and promotion. (Prerequisite: MKTG-1310 or permission of instructor.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (780, N' Product Planning for Marketing Management', N'4320', N'MKTG', N'An overall view of the product planning function (including the planning of services) in a company or institution, including the development and appraisal of product ideas, optimal organization of the planning process, product audits, financial and legal aspects of product planning, and intra-organizational factors. (Prerequisite: MKTG-2320.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (781, N' Internet Marketing', N'4330', N'MKTG', N'This course explores the impact of the Internet on traditional marketing strategy and actions. Specific emphasis will be placed on customer segmentation/targeting, consumer behaviour and issues of on-line/off-line consistency. Students will be expected to develop an understanding of web site design and basic html coding. Classes consist of lectures, lab work, case analysis, and student discussions/presentations. The course requires both qualitative and quantitative treatment of issues. (Prerequisites: MSCI-2130 and MKTG-2320.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (782, N' International Marketing', N'4350', N'MKTG', N'This course is concerned with the problems and opportunities of marketing in foreign environments. It will focus on the cultural, economic, and geographical problems encountered in managing the marketing function from a Canadian manager''s perspective. (Prerequisite: MKTG-2320.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (783, N' Advertising Management', N'4360', N'MKTG', N'A study of how to approach the management of advertising in business enterprises. The focus will be on making advertising decisions (e.g., setting advertising objectives, creating advertising campaigns, developing media strategies, and measuring advertising results) in relation to the overall marketing strategy of the business or non-business enterprise. (Prerequisite: MKTG-2320.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (784, N' Sales Management', N'4370', N'MKTG', N'The study of the personal selling area, including an examination of the role and responsibilities of the salesperson, the sales management, and sales management functions. (Prerequisite: MKTG-2320.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (785, N' International Logistics', N'4380', N'MKTG', N'This is an applied course discussing the physical movement of products across international borders. The course examines the decisions that a logistics manager must make when shipping products internationally and the background knowledge that a logistics manager should possess to make these decisions. Topics include international ocean and international air transportation; customs duties; government influences on international logistics; international terms of carriage; and international cargo insurance and documentation. (Prerequisite: MKTG-1310 or permission of the instructor.).', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (786, N' Marketing Strategy and Planning', N'4390', N'MKTG', N'An advanced course in the management of the marketing function. The course will include an appraisal of the key issues in the management of the marketing function with major emphasis on the development, formulation, implementation, and control of the firm''s marketing plan. Emphasis will also be placed on current key issues in the marketing area and global marketing considerations. (Prerequisites: MKTG-2320 and any 4 of MKTG-3340, MKTG-3320, MKTG-3350, MKTG-3370, MKTG-3380, MKTG-3390, MKTG-4320, MKTG-4330, MKTG-4350, MKTG-4360, MKTG-4370, MKTG-4910 and MKTG-4950)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (787, N'MKTG-4450, Services Marketing', N'4450', N'MKTG', N'This course will be of particular interest to students who wish to explore marketing and management issues related to service industries (such as travel, hospitality, financial, sports, and other service related businesses). The course recognizes that service organizations require a distinctive approach to marketing and operations strategy, both in their development and execution. This course explores ways that goods-producing and -selling firms might use and market “service” as a source of competitive advantage. Topics include the service dominant logic; the systems perspective; research; branding; e-service principles; overlap of marketing/operations/human resource systems in service organizations (i.e., internal marketing); service blueprinting; service recovery; and service customer relationship management (CRM). Throughout, heavy emphasis will be placed on understanding the service consumer experience. (Prerequisites: MKTG-2320, MKTG-3340)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (788, N' Special Topics in Marketing', N'4910', N'MKTG', N'This course examines major concepts, industries, ideas, issues or current problems in Marketing. Topics, and the method of delivery, may vary from semester to semester. Please contact the instructor for further information. (May be taken for credit twice if content is different.) (Prerequisite: Consent of instructor.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (789, N' Independent Study in Marketing', N'4950', N'MKTG', N'This course is of varying content dealing with topical issues in marketing. The course might focus on a specific functional area or a particular environment for the application of marketing concepts. Administration of the course will vary as appropriate with its content and might involve a literary survey, research project, experiential exercise, or other format. (Prerequisites: MKTG-2320 and consent of the instructor and Area Chair.) (May be taken for credit twice if content is different.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (790, N' Introduction to Business', N'1000', N'STEN', N'This course takes a holistic approach in helping students develop an understanding of their future places, as entry-level managers, in business and other forms of organizations. Functional business learning is undertaken using the lecture method. In parallel, the basic elements of strategic management are introduced in order to develop students’ strategic thinking capabilities. Project work focuses on adapting students’ career strategies to the employment environment, and on adapting companies’ strategies to their competitive environments. Finally, the case method is used to emphasize ethical self-management, group dynamics and organizational governance, and entrepreneurial processes involved in starting and managing a small business. The course demands that students: use their initiative; develop their analytical, decision-making and interpersonal management skills; and take responsibility for achieving success.', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (791, N' Co-op Work Term I', N'2050', N'STEN', N'Supervised experience in an approved career-related setting with a focus on the application of theory and the development of transferable skills. The co-op work experience is designed to provide students with an enriched learning opportunity to integrate academic theory and concepts in an applied setting. (Prerequisite: Student must be enrolled in a co-operative education program. Offered on a Pass/non-Pass basis. Supervised practicum requires the successful completion of a minimum of 420 hours. Students who do not pass the course can not continue in the co-op program.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (792, N' Fundamentals of Entrepreneurship', N'2900', N'STEN', N'This is a survey course designed to introduce students from all faculties to entrepreneurship as a career option. The entrepreneurial process will be explored through a mix of lectures and case studies. Topics include the identification of profitable business ideas, assessment of business opportunities, entry strategies, marshalling resources, and the start-up process. (Prerequisite: STEN-1000. Business students must also have completed MGMT-1000.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (793, N' Co-op Work Term II', N'3050', N'STEN', N'Supervised experience in an approved career-related setting with a focus on the application of theory and the development of transferable skills. The co-op work experience is designed to provide students with an enriched learning opportunity to integrate academic theory and concepts in an applied setting. (Prerequisite: Student must be enrolled in a co-operative education program. Offered on a Pass/non-Pass basis. Supervised practicum requires the successful completion of a minimum of 420 hours. Students who do not pass the course can not continue in the co-op program.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (794, N' Entrepreneurial Resource Management', N'3900', N'STEN', N'This course covers the strategic issues involved in attracting and managing resources - financial, human, and intellectual capital - in the entrepreneurial firm. The course is taught from two distinct perspectives-- from the point of the view of the resource provider (angel investor, venture capitalist, bank, corporation, talent) and from the point of view of the resource seeker (the entrepreneur). The course focuses on the strategic implications, rather than financial techniques, associated with both attracting resources and valuing new and growing ventures. The course is suitable for any student wishing to pursue an entrepreneurial career path, including a career in venture capital. Pre-requisites: FINA-2710 (Business Finance II) and MKTG-1310 (Principles of Marketing).', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (795, N' New Venture Formation', N'3910', N'STEN', N'Designed for students who choose entrepreneurship as a career option, this course is an in-depth study of the process of drawing the blueprints for a new enterprise including: developing business ideas, developing business concepts, conducting feasibility studies, choosing a legal form or business, writing business plans, identifying and approaching sources of money, raising funds, and putting together a package of resources to start an enterprise. (Prerequisites: STEN-2900.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (796, N' International Business', N'3930', N'STEN', N'This course is designed to provide students with the tools to think globally and manage internationally. This survey course covers a wide range of topics including, the global trade and investment environment, the international firm''s cultural, political, and competitive environment, and the management and operations of international firms. The focus throughout the course is on the changes that occur when a firm moves from a domestic focus to a global one. (Prerequisites: FINA-2710 and MKTG-1310.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (797, N' The Law and Business Administration', N'3970', N'STEN', N'A survey of the law pertaining to business administration. Topics include: the legal approach to business problems, contracts, sale of goods, bills of exchange, agency, bailment, real property, partnerships, corporations, and bankruptcy. (Prerequisite: MGMT-2430.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (798, N' Co-op Work Term III', N'4050', N'STEN', N'Supervised experience in an approved career-related setting with a focus on the application of theory and the development of transferable skills. The co-op work experience is designed to provide students with an enriched learning opportunity to integrate academic theory and concepts in an applied setting. (Prerequisite: Student must be enrolled in a co-operative education program. Offered on a Pass/non-Pass basis. Supervised practicum requires the successful completion of a minimum of 420 hours. Students who do not pass the course can not continue in the co-op program.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (799, N' Strategy in the Global Business Environment', N'4900', N'STEN', N'This course builds on the basic knowledge provided in STEN-3930 to provide students with an in-depth appreciation of global management issues. The course focuses on developing and implementing global strategies. This includes a detailed analysis of the international environment and the forces that determine global effectiveness, as well as consideration of different forms of entry available to firms and the specific factors that must be addressed to implement global strategies successfully. (Prerequisite: STEN-3930.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (800, N' Special Topics in Strategic Management and Entrepreneurship', N'4910', N'STEN', N'This is an advanced course designed to examine, in-depth, the strategic issues facing business decision makers. Coverage will vary to reflect the contemporary issues and concerns of today''s executives. (May be taken for credit twice if content is different.) (Prerequisite: STEN-3910, fourth year standing or consent of the instructor.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (801, N' Field Study in Strategic Management and Entrepreneurship', N'4930', N'STEN', N'In this course, teams of students study an actual firm focusing on identifying the strategic issues facing the firm, the needed strategic plans for addressing them, and the implementation of such plans. Students pursuing the entrepreneurial option can also take this course to finalize the prototype for the business they intend to start. (Prerequisite: STEN-3910, fourth year standing or consent of the intsructor.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (802, N' Independent Study in Strategic Management and Entrepreneurship', N'4950', N'STEN', N'Under faculty supervision, students undertake an individualized program of independent study to pursue, in great depth, a topic in strategic management or entrepreneurship where they can apply the knowledge gained in prior courses. (Prerequisite: consent of instructor and Area Chair.) (May be taken for credit twice if content is different.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (803, N' Enterprise Development Consulting', N'4960', N'STEN', N'Students will perform business consulting and market research for local organizations, giving them opportunities to network while applying skills and knowledge learned in the classroom to real life business situations. Semester-long projects covering different business areas are performed in small supervised teams. Weekly class time is a round-table discussion format used for collaboration of ideas and strategy with the rest of the class. Students will be evaluated on participation along with formal reports and presentations regarding the projects. Highly motivated students from a variety of business-related disciplines will make-up the consulting team. (Prerequisites: Approval of instructor.) (Open to Semester 7 and above students only).', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (804, N' Strategic Management', N'4980', N'STEN', N'Taught from the perspective of the CEO, this is the capstone course of the B.Comm. Program. It is designed to integrate the knowledge gained in all business courses and focus such knowledge on the central task of managing the firm in its entirety. (Prerequisites: All other required Business courses and Semester 7 or 8 standing for B. Comm. students, or Semester 5 or 6 standing for B. Comm. for University Graduates students.) (Not open to non-Business students.)', 3, 14, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (805, N' Teaching and Learning Part I', N'1199', N'EDUC', N'The course provides students with directed experience in a community service organization such as a service club, a youth club or group, a national park or conservation area, or a science museum. The Faculty of Education Field Experience Office will arrange the Community Service Field Placements, where applicable. Workshops and seminars will prepare students for the Community Service Field Placements and introduce students to the Professional Year Applicant Portfolio as a means of documenting and reflecting on professional learning and practice in the teaching profession. (Open only to students in the Modern Languages, French, English Language and Literature, History, Drama, Visual Art, General Science and Mathematics I/S Concurrent Education programs).', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (806, N' Community Service Education', N'2299', N'EDUC', N'This course provides students with directed field experiences in a grade 9 or 10 classroom (mathematics or science) during each of the Fall and Winter semesters. The Faculty of Education Field Experience Office will arrange the Field Placements. Workshops and seminars will provide an orientation to schools, with a focus on school culture and school community, and assist students in developing their Professional Year Applicant Portfolio as a means of documenting and reflecting on professional learning and practice in the teaching profession. (Open only to third-year students in the Modern Languages, French, English Language and Literature, History, Drama, Visual Art, General Science and Mathematics I/S Concurrent Education programs (Prerequisite: EDUC-1199).', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (807, N' Teaching and Learning Part II', N'3399', N'EDUC', N'This course provides students with directed field experiences in a grade 11 or 12 classroom (mathematics or science) during the Fall semester. The Faculty of Education Field Experience Office will arrange the Field Placements. Workshops and seminars will provide an orientation to the senior division, with a focus on school culture and school community, and assist students in completing their Professional Year Applicant Portfolio. (Open only to fourth-year students in the Modern Languages, French, English Language and Literature, History, Drama, Visual Art, General Science and Mathematics I/S Concurrent Education programs.) (Pre-requisites: EDUC-1199 and EDUC-2299).', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (808, N' Religious Education in Roman Catholic Schools', N'5200', N'EDUC', N'This course is provided for those preparing themselves for the ministry of teaching in the Roman Catholic Schools of Ontario. This course is open to all Education students. This course offers prospective teachers the opportunity: 1) to reflect, in an adult context, upon the significance of their faith and faith growth for themselves and their students; 2) to gain a theological background for an introduction to Religious Education. (1.5 Credit Weight).', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (809, N' Foundations of Practice (Part I): Philosophical Orientation to Education', N'5201', N'EDUC', N'Theories of learning and the nature of learning will be explored so that teacher candidates begin an inquiry process toward their teaching philosophy. (1.5 Credit Weight)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (810, N' Foundations of Practice (Part II): Classroom Practice', N'5202', N'EDUC', N'Theories from Part 1 will be linked to instructional aspects of teaching, discovering strategies that are related to teacher candidates’ perspective/philosophy of teaching. (1.5 Credit Weight)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (811, N' Educational Psychology', N'5203', N'EDUC', N'Psychology applied to teaching: child growth and development, the learning process, mental health, learning and adjustment problems in the environment. (3.0 Credit Weight)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (812, N' Differentiated Instruction for Students with Special Needs', N'5204', N'EDUC', N'This course provides an introduction to the field of special education in the Canadian context, focusing on background knowledge needed by teachers to address diverse learning needs in inclusive classrooms. This course provides an introduction to the field of special education in the Canadian context, focusing on background knowledge needed by teachers to address diverse learning needs in inclusive classrooms. It will examine the various methodologies used for differentiating instruction, accommodating and modifying instruction for IEP’s, tiered instruction, and using technology to assist learning. (3.0 Credit Weight)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (813, N' Educational Foundations, Law and Ethics', N'5205', N'EDUC', N'This course focuses on provincial legislation and policies and explores the significance of professional learning and ethical conduct that involves understanding a range of educational philosophies and pedagogical approaches. (Open only to Concurrent Education students.) (3.0 Credit Weight)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (814, N' Aboriginal Ways of Knowing: Cultural, Political and Linguistic Contexts', N'5206', N'EDUC', N'Emphasis will be on critical thinking around the politics of education, explore resources so educators can better support Aboriginal learners and increase awareness about First Nations, Métis and Inuit culture as well as the multicultural and inclusive classrooms. (1.5 Credit Weight)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (815, N' Service Learning Specialization', N'5207', N'EDUC', N'Students select one of the following options according to their division PJ/JI/IS (3.0 Credit Weight):', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (816, N' Assessment and Evaluation', N'5208', N'EDUC', N'This course will examine ways to incorporate “assessment as”, “assessment for” and “assessment of” learning in all classes. Emphasis will be placed on incorporating frequent, continuous assessment techniques to foster an environment of intrinsic motivation for success. Strategies for tying feedback directly to curriculum expectations will also be explored. (1.5 Credit Weight)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (817, N' Critical Analysis of Social, Global & Cultural Issues in Education', N'5209', N'EDUC', N'An introduction to critical reflection and analysis of educational issues. This course addresses the varieties of students who enter the classroom in terms of their diverse social origins, cultures, identities, and social status (lived and perceived). It engages participants in an examination of the purposes of education, education policy, and teachers’ responsibility to work productively with school colleagues and other adults to achieve equitable access, experiences, and outcomes for all students. (1.5 Credit Weight)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (818, N' Foundations of Practice (III) Law and Ethics – School Governance', N'5210', N'EDUC', N'This course focuses on provincial legislation and policies and explores the significance of professional learning and ethical conduct that involves understanding a range of educational philosophies and pedagogical approaches. (Open only to Consecutive Education students.) (1.5 Credit Weight)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (819, N' Mathematics Foundations (P/J)', N'5215', N'EDUC', N'This course is intended to give prospective teachers an in-depth preparation in the content, concepts, and principles of elementary mathematics education for students in Junior Kindergarten - Grade 6. Real-life problem solving approaches, usefulness, and power of mathematics in everyday life will be emphasized in the course. The use of manipulatives, investigations, discussions, and the application of modern technological tools in appropriate situations. (1.5 Credit Weight)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (820, N' Pedagogy of the Arts (J/I)', N'5221', N'EDUC', N'This course situates the arts in Grades 4 to 8 classes and communities and examines how they contribute to the growth of knowledge, creativity, and critical thinking in our students and in society. This will include strategies to encourage best practices in teaching and supporting learning and attainment of skills and knowledge in aesthetic and artistic practices.The course emphasises differentiated instructional practices, diversity, curriculum planning and assessment. (1.5 Credit Weight)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (821, N' Mathematics Foundations (J/I)', N'5225', N'EDUC', N'An introduction to the growth, development, and learning of children from Grades 4 to 8, with an emphasis on instructional practices and curriculum planning for teaching mathematics. (1.5 Credit Weight)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (822, N' Pedagogy of the Arts (I/S)', N'5231', N'EDUC', N'This course situates the arts in Grades 7 to 8 classes and communities and examines how they contribute to the growth of knowledge, creativity, and critical thinking in our students and in society. This will include strategies to encourage best practices in teaching and supporting learning and attainment of skills and knowledge in aesthetic and artistic practices. The course emphasises differentiated instructional practices, diversity, curriculum planning and assessment. (1.5 Credit Weight)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (823, N' Visual Arts Methodology', N'5311', N'EDUC', N'An introduction to the growth, development, and learning of children from Junior Kindergarten to grade 6 with an emphasis on instructional practices and curriculum planning for teaching visual arts. (3.0 Credit Weight)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (824, N' Digital Technology and Social Media Applications (P/J)', N'5312', N'EDUC', N'This course explores a range of digital technologies in educational settings. Focusing on teaching, learning and inquiry, students will evaluate digital educational resources, critically discuss and assess uses of new media in school-based contexts, gain hands-on experiences with various digital tools, and develop various multimedia instructional tools with the aim of building an intelligent and thoughtful disposition towards the use of learning technologies within their own classroom and school contexts. (3.0 Credit Weight)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (825, N' Health and Physical Education (P/J)', N'5313', N'EDUC', N'This course explores a constructivist approach to teaching health and physical education to children from Junior Kindergarten to Grade 6 with an emphasis on differentiated instructional practices, diversity, curriculum planning and assessment. (3.0 Credit Weight)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (826, N' Language Arts (P/J)', N'5314', N'EDUC', N'An introduction to the theories of learning and linguistics of children from Junior Kindergarten to grade 6 with an emphasis on curriculum content, expectations, ways of learning and implications of EQAO standardized testing. (3.0 Credit Weight)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (827, N' Mathematics Methodology (P/J)', N'5315', N'EDUC', N'This course is designed to introduce methods for teaching mathematics to elementary students from Junior Kindergarten - Grade 6. This course focuses on children’s thinking in mathematics and classroom practices that support and develop children’s thinking. Teacher candidates will develop frameworks for assessing children’s strategies for solving problems. They will explore the connection between arithmetical and algebraic thinking in the elementary grades. (3.0 Credit Weight)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (828, N' Music Methodology', N'5316', N'EDUC', N'An introduction to the growth, development, and learning of children from Junior Kindergarten to grade 6 with an emphasis on instructional practices and curriculum planning for teaching music. (3.0 Credit Weight)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (829, N' Science (P/J)', N'5317', N'EDUC', N'This course explores an inquiry-based approach to teaching science to children from Junior Kindergarten to Grade 6 with an emphasis on differentiated instructional practices, diversity, curriculum planning and assessment. (3.0 Credit Weight)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (830, N' Social Studies (P/J)', N'5318', N'EDUC', N'This course explores a constructivist approach to teaching social studies to children from Junior Kindergarten to Grade 6 with an emphasis on differentiated instructional practices, diversity, curriculum planning and assessment. (3.0 Credit Weight)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (831, N' Issues in Education', N'5319', N'EDUC', N'An introduction to critical reflection and analysis of social, cultural and political issues in education.', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (832, N' Visual Arts Methodology', N'5321', N'EDUC', N'An introduction to the growth, development, and learning of children from grades 4 to 8, with an emphasis on instructional practices and curriculum planning for teaching visual art. (3.0 Credit Weight)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (833, N' Digital Technology and Social Media Applications (J/I)', N'5322', N'EDUC', N'This course explores a range of digital technologies in educational settings. Focusing on teaching, learning and inquiry, students will evaluate digital educational resources, critically discuss and assess uses of new media in school-based contexts, gain hands-on experiences with various digital tools, and develop various multimedia instructional tools with the aim of building an intelligent and thoughtful disposition towards the use of learning technologies within their own classroom and school contexts. (3.0 Credit Weight)', 3, 15, NULL, NULL)
