INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (838, N' Science (J/I)', N'5327', N'EDUC', N'This course explores an inquiry-based approach to teaching science to children from Grades 4 to 8 with an emphasis on differentiated instructional practices, diversity, curriculum planning and assessment. (3.0 Credit Weight)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (839, N' Social Studies (J/I)', N'5328', N'EDUC', N'This course explores a constructivist approach to teaching social studies to children from Grades 4 to 8 with an emphasis on differentiated instructional practices, diversity, curriculum planning and assessment. (1.5 Credit Weight)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (840, N' Visual Arts Methodology', N'5331', N'EDUC', N'An introduction to the development and learning of students in grades 7 to 8 with an emphasis on instructional practices and curriculum planning for teaching visual art. (1.5 Credit Weight)', 3, 15, NULL, NULL)
