INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (843, N' Language Across the Curriculum (I/S)', N'5334', N'EDUC', N'An exploration of the development and learning of students in Grades 7 to 8 with an emphasis on instructional practices and curriculum planning for teaching language arts across the curriculum. (1.5 Credit Weight)', 3, 15, NULL, NULL)
