INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (845, N' Music Methodology', N'5336', N'EDUC', N'An introduction to the development and learning of students in grades 7 to 8 with an emphasis on instructional practices and curriculum planning for teaching music. (1.5 Credit Weight)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (846, N' Science (I/S)', N'5337', N'EDUC', N'This course explores an inquiry-based approach to teaching science to children Grades 7 to 8 with an emphasis on differentiated instructional practices, diversity, curriculum planning and assessment. (1.5 Credit Weight)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (847, N' Social Science (I/S)', N'5338', N'EDUC', N'This course explores a constructivist approach to teaching social studies to children Grades 7 to 8 with an emphasis on differentiated instructional practices, diversity, curriculum planning and assessment. (1.5 Credit Weight)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (848, N' Career and Guidance Education (I/S)', N'5339', N'EDUC', N'An exploration of the development and learning of students in grades 7 to 8 with an emphasis on career and guidance education. ((1.5 Credit Weight)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (849, N' Junior - Intermediate, Intermediate - Senior Methods', N'5352', N'EDUC', N'These courses provide a more detailed study and application of the aims and teaching procedures of specific subjects in the Junior-Intermediate and Intermediate-Senior concentrations. Intermediate-Senior candidates must select two courses from the Table of Options. Junior-Intermediate candidates must select one.', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (850, N' Curriculum Development for Technological Studies Part I', N'5386', N'EDUC', N'An introduction to the theory and practice of curriculum development for Broad-Based Technology programs in secondary schools. The course uses a constructivist approach to promote integrated learning and broad-based technology approaches through activities that lead to the development of unit plans, a course of study, a project outline, a student learning module and a course portfolio. Thematic and project-based strategies are used to address the learning expectations outlined in curriculum guidelines and policy documents and provide a variety of student learning styles, teaching approaches, and assessment and evaluation strategies. Shop safety and workplace safety are themes infused throughout the course.', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (851, N' Curriculum Development for Technological Studies Part II', N'5387', N'EDUC', N'An introduction to the theory and practice of curriculum development for Broad-Based Technology programs in secondary schools. The course uses a constructivist approach to promote integrated learning and broad-based technology approaches through activities that lead to the development of unit plans, a course of study, a project outline, a student learning module and a course portfolio. Thematic and project-based strategies are used to address the learning expectations outlined in curriculum guidelines and policy documents and provide a variety of student learning styles, teaching approaches, and assessment and evaluation strategies. Shop safety and workplace safety are themes infused throughout the course. (Prerequisite: EDUC-5386.)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (852, N' Principles and Methods of Teaching Technological Studies Part I', N'5388', N'EDUC', N'This course is an introduction to the methodology and processes of facilitating learning in the Broad-Based Technological Education classroom. The course will provide opportunities to apply pre-instructional planning, instruction and classroom management skills, ongoing and post-instructional assessment and evaluation strategies. Emphasis is on course activities that will focus on teaching and learning theories, the dynamics of team and group learning, and the development of written and oral communication skills. Course activities include lesson planning, team practice teaching, report writing, seminar presentations, and the development of video and print student learning modules.', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (853, N' Principles and Methods of Teaching Technological Studies Part II', N'5389', N'EDUC', N'This course is an introduction to the methodology and processes of facilitating learning in the Broad-Based Technological Education classroom. The course will provide opportunities to apply pre-instructional planning, instruction and classroom management skills, ongoing and post-instructional assessment and evaluation strategies. Emphasis is on course activities that will focus on teaching and learning theories, the dynamics of team and group learning, and the development of written and oral communication skills. Course activities include lesson planning, team practice teaching, report writing, seminar presentations, and the development of video and print student learning modules. (Prerequisite: EDUC-5388.)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (854, N' Drama Methodology (P/J)', N'5411', N'EDUC', N'This course situates Drama as one of the arts in Junior Kindergarten to Grade 6 classes and communities and examines how it contributes to the growth of knowledge, creativity, and critical thinking in our students and in society. This will include strategies to encourage best practices in teaching and supporting learning and attainment of skills and knowledge in aesthetic and artistic practices. The course emphasises differentiated instructional practices, diversity, curriculum planning and assessment.', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (855, N' Dance Methodology (P/J)', N'5412', N'EDUC', N'This course situates Dance as one of the arts in Junior Kindergarten to Grade 6 classes and communities and examines how it contributes to the growth of knowledge, creativity, and critical thinking in our students and in society. This will include strategies to encourage best practices in teaching and supporting learning and attainment of skills and knowledge in aesthetic and artistic practices. The course emphasises differentiated instructional practices, diversity, curriculum planning and assessment.', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (856, N' Language and Media Literacy (P/J)', N'5414', N'EDUC', N'Emphasis will be placed on instructional practices and curriculum planning for teaching language arts through differentiated and tiered instruction. The use of media in language arts will be explored through a critical lens. (1.5 Credit Weight)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (857, N' Drama Methodology (J/I)', N'5421', N'EDUC', N'This course situates Drama as one of the arts in Grades 4 to 8 classes and communities and examines how it contributes to the growth of knowledge, creativity, and critical thinking in our students and in society. This will include strategies to encourage best practices in teaching and supporting learning and attainment of skills and knowledge in aesthetic and artistic practices. The course emphasises differentiated instructional practices, diversity, curriculum planning and assessment.', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (858, N' Dance Methodology (J/I)', N'5422', N'EDUC', N'his course situates Dance as one of the arts in Grades 4 to 8 classes and communities and examines how it contributes to the growth of knowledge, creativity, and critical thinking in our students and in society. This will include strategies to encourage best practices in teaching and supporting learning and attainment of skills and knowledge in aesthetic and artistic practices. The course emphasises differentiated instructional practices, diversity, curriculum planning and assessment', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (859, N' Language and Media Literacy (J/I)', N'5424', N'EDUC', N'An introduction to the growth, development, and learning of children from Grades 4 to 8, with an emphasis on instructional practices and curriculum planning for teaching language arts. (1.5 Credit Weight)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (860, N' Drama Methodology (I/S)', N'5431', N'EDUC', N'This course situates Drama as one of the arts in Grades 7 to 8 classes and communities and examines how it contributes to the growth of knowledge, creativity, and critical thinking in our students and in society. This will include strategies to encourage best practices in teaching and supporting learning and attainment of skills and knowledge in aesthetic and artistic practices. The course emphasises differentiated instructional practices, diversity, curriculum planning and assessment.', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (861, N' Practice Teaching', N'5491', N'EDUC', N'Directed observation and practice teaching is provided through a series of field experiences. Primary-Junior candidates will normally be placed in Grades K to Six inclusive; Junior-Intermediate candidates will normally be placed in Grades Four to Ten inclusive; Intermediate-Senior candidates will be placed in Grades Seven to Twelve inclusive.', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (862, N' Internship', N'5497', N'EDUC', N'The Internship consists of 90 hours of skills upgrading with 36 hours dedicated to an action research community service project. This community service project gives the students an opportunity to complete a needs assessment related to their trade in education, followed by an inquiry-based research project and evaluation. The remaining hours will be dedicated to the integration of technological skills, theory, and pedagogical practice and to deepening and broadening their skills and knowledge in their selected area of technological education. A technology skills profile will be used to track the range and level of skill development of each candidate. The Program Coordinator for the Technological Education program will work with Board-based Technological Program Consultants across the province to select teacher advisors who will support teacher candidates during their Internship period. The Technological Program Consultants will be responsible for evaluating teacher candidates during their Internship.(7.5 Credit Weight).', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (863, N' Practicum', N'5498', N'EDUC', N'The practicum consists of two main components: orientation to schools, with a focus on school culture and school community; and classroom practice related to the candidate''s specific discipline. Teacher candidates are assigned, during Fall and Winter practicum sessions, to schools or other settings approved by the Ontario College of Teachers, for a minimum of sixty days of combined observation and practice related to these components. As well, candidates will do 100 hours of pedagogical workshops, courses or seminars, offered by the Board. The Technological Program Consultant will sign off on this training. Candidates would be required to visit schools that offer their discipline. The Technological Program Coordinator will set up the school visits and the Board will pay for teacher coverage during their absence from regular teaching. (Open only to student in the BEd/Diploma in Technological Education program.)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (864, N' Practicum', N'5499', N'EDUC', N'The practicum consists of two main components: orientation to schools, with a focus on school culture and school community; and classroom practice related to the candidates’ specific discipline. Teacher candidates are assigned, during Fall and Winter practicum sessions, to schools or other settings approved by the Ontario College of Teachers, for a minimum of 80 days of combined observation and practice related to these components. (16 Credit Weight)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (865, N' Diversity and Inclusion in the Learning Organization', N'4000', N'EDUC', N'This course will examine the evolution of the concepts of diversity and inclusion in social organizations, key management practices for improving performance, and current diversity and inclusion challenges in organizations. Diversity and inclusion are important aspects of learning organizations for the purpose of developing strategic options for improvement in many different ways. (Prerequisite: Semester 3 or above standing)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (866, N' Instructional Technologies', N'4050', N'EDUC', N'This course has been designed to provide students with an introduction to theoretical and practical issues pertaining to the use of informational and instructional technologies in learning organizations. Students will examine and critique the context of the field of instructional technologies and learn to apply current instructional technologies and media to instructional design and practice and the enhancement of learning opportunities. Basic concepts in educational technology, major developments, the present status of informational and instructional technologies, key principles of educational technology as an approach and tool for teaching and learning, and the development of appropriate educational technologies in terms of a learning organization’s goals will also be examined. Technological literacy will be emphasized throughout while exploring computer applications, the utilization of converging digital technologies, and the use of the internet and web resources.(Prerequisite: Semester 3 or above standing)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (867, N' Learning-Centred Teaching: Planning, Delivery, Assessment, and Evaluation', N'4100', N'EDUC', N'Students will learn about principles and theories of learning-centred practices. Specifically, students will critically examine and synthesize the findings of current research and scholarly texts on teaching and learning to develop a critical personal understanding of learning-centred practices that are applicable to a wide range of diverse workplace contexts. Through assigned readings and texts, students will acquire, integrate, and apply knowledge pertaining to planning, instructional delivery, and the assessment and evaluation of learning. Self-, peer-, and teacher-evaluated assignments will provide students with opportunities to integrate research and practice and to facilitate the development of particular skills, notably, interpersonal communication skills, planning, facilitation and organization of learning, critical thinking, inquiry learning, and reflection. (Prerequisite: Semester 3 or above standing)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (868, N' Learning Organizations: Management and Leadership', N'4150', N'EDUC', N'Students will learn about current management theories and practices in contemporary learning organizations where learning is a primary or significant characteristic or quality of the organization. Specifically, from a leadership perspective, this course will examine the nature of leading and managing in learning organizations, the role of learning, and the complex legal, ethical, and social issues that give shape to the organization and its leaders. Through the use of a variety of resources and approaches, students will explore and question theories, models, tools, and best practices for managing and leading in learning organizations, prompting and providing critical perspectives and practical tools that may be applied in different contexts.(Prerequisite: Semester 3 or above standing)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (869, N' Theories of Individual and Collective Learning', N'4200', N'EDUC', N'Students will examine current theories pertaining to learning and learners in diverse organizational contexts. Particular themes will be examined, including the nature of learning, patterns of growth and development, the dynamics and complexities of learning in diverse educational contexts, and current educational realities in society. Specifically, students will examine a number of important issues, such as: learning and cognitive processes; personal, social and moral development; individual and group differences; social-cognitive views (e.g., racial discrimination, bullying, harassment, abuse, gender bias, xenophobia, homophobia, stereotyping); motivation and cognition relevant to individual and collective learning; knowledge construction and higher-order thinking. In this course, students will develop a critical awareness of learning theories and related issues and will critique, analyze, and reflect on the underlying assumptions associated with matters and the implications for individual and collective learning in learning organizations.(Prerequisite: Semester 3 or above standing)', 3, 15, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (870, N' Materials in Civil and Environmental Engineering', N'2190', N'CIVL', N'Fundamental materials in civil and environmental engineering. Concrete: Portland cement, hydration, mixture design, admixtures, mixing, placing and curing. Masonry: masonry units, mortar, grout, and plaster, types of bond and joints. Timber: Structure and types of wood. Iron and steel: cast iron, wrought iron, steel products, structural steel, reinforcing steel, welded wire fabrics. Wastewater and biosolids, Coagulant and adsorbents. Particulate air pollutants. Solid waste and hazardous waste.', 3, 16, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (871, N' Civil Engineering Information Systems', N'2200', N'CIVL', N'A course in information systems in civil engineering including Surveying, GPS, GIS and Graphic communication. Surveying: Distance measurements; Leveling theory of differential leveling, curvature and refraction, types of surveying levels, leveling rods, benchmark leveling, profile and cross-section leveling; Angles and Theodolites: reference directions for vertical angles, meridians, horizontal angles, Total Station: reference directions for vertical angles, meridians, horizontal angles; Transverses Survey and Computations: balancing field angles, meridians, bearings, azimuths, latitude departures; Curves: circular curves, vertical curves, spiral curves. Introduction to GPS and GIS; applications of GIS and GPS in surveying. Graphic communication using AutoCAD. Required field trip*. (one week; immediately following the final examination period.) *Note: There will be no additional fees for the field camp course.', 3, 16, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (872, N' Fluid Mechanics', N'3510', N'CIVL', N'Continuity, energy, momentum concepts. Boundary layers. Pipe flow including network installations. Rotodynamic pumps, system curves. Irrotational flow, flownets. Introduction to open channel flow: specific energy, flow regimes; uniform, (Prerequisite: GENG-1200 and MATH-2790 (3 lecture, 2 laboratory hours a week.)', 3, 16, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (873, N' Stress Analysis', N'3520', N'CIVL', N'Distribution of internal loading in structural members; Deflection of beams; Moment areas theorems; Virtual work; Castigliano''s theorems; Maxwell-Betti reciprocal theorem; Buckling of columns; and Application of Energy methods in simple determinate structures. (Prerequisite: GENG-2180) (3 lecture and 2 laboratory/tutorial hours a week.)', 3, 16, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (874, N' Structural Analysis', N'3530', N'CIVL', N'Stability and determinacy of trusses and frames; analysis of statistically determinate trusses and frames;Influence lines and moving loads. Statically indeterminate structures; force method; displacement method. (Prerequisite: GENG-2180.) (3 lecture, 2 laboratory hours a week.).', 3, 16, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (875, N' Concrete Design', N'3540', N'CIVL', N'Mechanics and behaviour of reinforced concrete components. Analysis and ultimate strength design of reinforced concrete beams and one-way slabs. Design for serviceability. Design for columns. Laboratory work includes design and testing of a concrete beam. (Prerequisite: GENG-2180) (3 lecture, 2 laboratory hours a week.)', 3, 16, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (876, N' Geotechnical Engineering I', N'3550', N'CIVL', N'Index properties of soils. Soil structure and classification of soils. Soil compaction and stabilization. Hydraulic principles of flow through soils, flow nets. Frost action in soils. Effective stresses. Compressibility, consolidation, and settlement analysis. Shear strength of soil. Kinematics and Stress distribution in soil. Stress analysis and stability of slopes (Prerequisite: GENG-2180.) (3 lecture, 3 laboratory hours a week.)', 3, 16, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (877, N' Masonry and Concrete Design', N'3610', N'CIVL', N'Analysis and design of columns, two-way slabs, and footings. Design of RC bearing walls and retaining walls. Design of masonry units for axial, flexure, and combined loads; Design of masonry beams, flexural and load bearing walls; columns, and pilasters; (Prerequisite: CIVL-3540) (3 lecture, 2 laboratory hours a week.)', 3, 16, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (878, N' Finite Element for Analysis and Design', N'3620', N'CIVL', N'Limit state design concepts; load factors and combinations; load specifications for structural design calculations.Displacement method for frames; Introduction to the finite element method; computer-aided analysis and design of structures; (Prerequisite: CIVL-3530) (3 lecture, 2 tutorial hours a week.)', 3, 16, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (879, N' Geotechnical Engineering II', N'3630', N'CIVL', N'Earth pressure and design of retaining walls. Sheet-pile walls, braced and tie back excavations. Combined pressures. Soil bearing capacity. Soil exploration. Load induced pressures and settlements. Footings and eccentrically loaded foundations. Raft and pile foundations. Piles and pile driving, cofferdams and caissons. (Prerequisites: GENG-2180 and CIVL-3550) (3 lecture, 3 laboratory hours a week).', 3, 16, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (880, N' Structural Steel Design', N'3640', N'CIVL', N'Design of structural steel components subjected to axial tension and compression forces, shear force, bending moment, and combined bending and compression; Design of Composite beams -; Design of simple bolted and welded connections; Computer-aided design of steel structures. (Prerequisite: GENG-2180 and CIVL-3520.) (3 lecture, 2 tutorial hours a week.)', 3, 16, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (881, N' Transportation and Traffic Engineering', N'3650', N'CIVL', N'Characteristics of transportation systems; rail, highway, airway, waterway, and pipeline, urban transportation planning, analysis and prediction, traffic impacts studies, highway and intersection capacity, characteristics of traffic flow, traffic control principles, queuing theory. (3 lecture, 2 laboratory hours a week.)', 3, 16, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (882, N' Capstone Design Project', N'4000', N'CIVL', N'A significant design experience which is based on knowledge and skills acquired in earlier course work. Engineering design integrates mathematics, basic science, engineering sciences and complementary studies in developing elements, systems and processes to meet specific needs while considering economic, health, safety, environmental, social or other pertinent factors. It involves a creative, iterative, and open-ended process Written and oral reports are required. (Prerequisite: consent of the supervisor and Department Head.) (2 hours lecture and 4 hours lab/week) (2 semester course)', 3, 16, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (883, N' Hydrology', N'4710', N'CIVL', N'Hydrologic Cycle, Weather. Precipitation: intensity, frequency, duration; Point and area estimates of precipitation; rational methods. Hydrologic abstractions. Runoff: storms, conceptual models, unit hydrograph principles, inflow design hydrograph. Streamflow: gauging, stage-discharge. Channel and Reservoir flood routing. Snowmelt. basics of hydrologic modeling; Probability applications and frequency analysis of precipitation and floods. Groundwater flow and water wells. Hydrologic design of urban and highway structures (Prerequisites: GENG-2220 and CIVL-3510 or consent of the instructor/ Department Head.) (3 lecture, 2 laboratory hours a week.)', 3, 16, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (884, N' Hydraulics', N'4720', N'CIVL', N'Review of basic concepts. Gradually and rapidly varied flows; surface profiles. Design of open channels with non-erodible and erodible beds; steady gradually varied flow computations in prismatic and non-prismatic channels; computer methods including HEC2. Design of hydraulic structures including gravity and arch dams, spillways, and outlet structures. (Prerequisite: CIVL-3510.) (3 lecture, 2 laboratory hours a week.)', 3, 16, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (885, N' Highway Design and Construction', N'4810', N'CIVL', N'Geometric design of highways and at-grade intersections; horizontal and vertical alignments, Cross--section elements, drainage; highway soil engineering including soil stabilization; bituminous materials; rigid and flexible pavement design; construction of pavements. (Prerequisite: GENG-2180 or CIVL-3520.) ( 3 lecture, 2 laboratory hours a week.)', 3, 16, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (886, N' Planning and Construction Management', N'4820', N'CIVL', N'The planning portion of this course will cover the elements of proper urban planning, the Planning Act, official plans, zoning by-laws, and subdivision design guidelines. The construction management portion will cover construction industry characteristics; types of business ownerships; organizational structures; drawings and specifications; estimating and bidding; types of construction contracts; insurance, bonding and claims; financial considerations; project cost controls and scheduling; project planning and administration; computer applications in construction industry, quality assurance, and construction safety. (3 lecture, 2 tutorial hours a week.)', 3, 16, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (887, N' Foundation Engineering', N'4910', N'CIVL', N'Footings and eccentrically loaded foundations. Raft and pile foundations. Piles and pile driving, cofferdams and caissons. Soil-structure iteraction. Computer-aided analysis and design of foundations (Prerequisite: CIVL-3550.) ( 3 lecture hours, 2 laboratory/tutorial hour a week.)', 3, 16, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (888, N' Advanced Topics in Structural Design', N'4920', N'CIVL', N'Design of: beam-columns; plate girders; composite structures; and connections. Introduction to Prestressed concrete design. (Prerequisite: CIVL-3550 and CIVL-3640.) ( 3 lecture, 2 tutorial hours a week.)', 3, 16, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (889, N' Transportation Systems Analysis', N'4940', N'CIVL', N'Socio-economic impacts on transportation, four-stage demand modeling system, use of models in transportation planning and forecasting, data and space issues, regression and discrete choice models; choice of modes, destinations and routes, passenger and freight travel, introduction to land use modeling. (Prerequisite: CIVL-3650) (3 lecture, 2 laboratory hours a week.)', 3, 16, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (890, N' Thermodynamics', N'3510', N'ENVE', N'Real gas behaviour and equations of state. The First and Second Laws of Thermodynamics and their applications. (3 lecture hours, 2 tutorial hours a week.)', 3, 16, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (891, N' Environmental Chemical Analysis', N'3520', N'ENVE', N'Environmental site assessment, statistical analysis of environmental data. Important characteristics of air, water, wastewater, and solid wastes. Basic concepts in quantitative analysis of physical, chemical, and biological parameters. Environmental site assessment, statistical analysis of environmental data. Instrumental methods of analysis for organic and inorganic contaminants in air, water, and soil. (Prerequisite: CHEM-1103.) (3 lecture, 2 tutorial/laboratory hours a week.)', 3, 16, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (892, N' Transport Phenomena', N'3610', N'ENVE', N'Introduction to the unifying theory of transport phenomena. Estimation of viscosities, thermal conductivities, and diffusivities for gases and liquids. Shell balances and some case studies of momentum, energy, and mass-transport. Mass transfer applications involving examples relevant to the environment. (3 lecture, 2 tutorial hours a week).', 3, 16, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (893, N' Air Pollution Control', N'3620', N'ENVE', N'Regulations and methods of source testing and monitoring. Nomenclature of organic compounds. Dispersion modelling. Air pollution control methods, designs, and their relative effectiveness. (Prerequisite: GENG-2500) (3 lecture, 2 lab/tutorial hours per week).', 3, 16, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (894, N' Water and Wastewater Treatment', N'3630', N'ENVE', N'Water and wastewater quality, guidelines and standards, flow fluctuation and design capacity. Design of different unit operations and processes in water and wastewater treatment. (Prerequisites: GENG-2500 and a course in fluid mechanics or hydraulics.) (3 lecture, 3 laboratory hours a week.)', 3, 16, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (895, N' Materials Recovery and Waste Management', N'3640', N'ENVE', N'The study, characterization, and analysis of municipal and industrial solid waste systems. Waste reduction, reuse, and recycling. Managing, collecting and transporting solid wastes. Waste reclamation and disposal methods, including landfiling, incineration, and composting. Waste management policies, regulations and facility siting issues. Various assignments will be integrated into a comprehensive project. (Prerequisite: GENG-2500.) (3 lecture hours).', 3, 16, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (896, N' Capstone Design Project', N'4000', N'ENVE', N'A significant design experience which is based on knowledge and skills acquired in earlier course work. Engineering design integrates mathematics, basic science, engineering sciences and complementary studies in developing elements, systems and processes to meet specific needs while considering economic, health, safety, environmental, social or other pertinent factors. Written and oral reports are required. (Prerequisite: consent of the supervisor and Department Head.) (2 lecture, 4 lab/tutorial hours per week (2 terms).', 3, 16, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (897, N' Water Distribution and Wastewater Collection Systems', N'4710', N'ENVE', N'Quantities of water and wastewater; development of surface and groundwater sources; design, construction, and maintenance of water distribution systems; design, construction, and maintenance of wastewater collection systems. (Prerequisite: CIVL-3510 Fluid Mechanics or equivalent.) (3 lecture, 2 tutorial/laboratory hours a week).', 3, 16, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (898, N' Chemical Reaction Engineering', N'4720', N'ENVE', N'Classification of chemical reactions and reactors, the rate equation, ideal reactor concept. Design equations for batch and flow (CSTR and PFR) reactors. Multiple reaction and reactor systems. Reactor design calculations under isothermal and non-isothermal conditions. (3 lecture, 2 tutorial hours a week).', 3, 16, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (899, N' Environmental Engineering Microbiology', N'4730', N'ENVE', N'Nature of inorganic and organic pollutants; biological approaches to environmental pollution problems; microorganisms; nutritional requirements and growth; metabolic pathways; energy generation and utilization in biological systems; response to changes in environment; pathogenic microorganisms and disinfection. (3 lecture hours a week, 1 tutorial/laboratory hour a week).', 3, 16, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (900, N' Sustainability in Engineering', N'4810', N'ENVE', N'Environmental impact assessment. Biophysical and socioeconomic impacts from engineering activities, processes, and projects. Human health and environmental risk concepts. Introduction to life cycle analysis, corporate/industrial environmental management, and environmental management systems. Students will undertake various project related and problem-based assignments. (3 lecture, 2 tutorial hours a week.) (Credit may only be obtained for one of MECH-4228, or ENVE-4810).', 3, 16, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (901, N' Computer-Aided Analysis', N'2110', N'ELEC', N'Object oriented programming in C++ covering most of the basic concepts. Development of Classes for matrix operations, complex numbers, etc. The rest of the course covers class development for a set of numerical schemes that include: Gauss-Jordan Method for solving Linear Simultaneous Algebraic Equations; Matrix inversion; Root finding using the Newton-Raphson and the half-interval methods; Lin-Bairstow method for Roots of Polynomials; Least-squares fitting; Numerical Integration using the Trapezoidal and Simpson’s 1/3 rule; Solution of Ordinary Differential Equations of any order using Euler, Improved Euler and the fourth-order Runge-Kutta methods. (Corequisites: MATH-2780 and MATH-2790) (3 lecture, 2 laboratory/tutorial hours or equivalent a week.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (902, N' Circuit Analysis*', N'2140', N'ELEC', N'Current, voltage, power and energy; simple resistive circuits; Kirchhoff’s laws; Wye-delta transformations; techniques of circuit analysis, nodal and mesh analysis; network theorems, superposition, Thevenin’s and Norton’s theorems; source transformations; operational amplifiers and Op-amp circuit analysis, inductors and capacitors, natural response of first-order RL and RC circuits; natural response of RLC circuits; sinusoids and phasers; ac power analysis; balance three-phase circuits; network simulations using SPICE and MATLAB.(Prerequisite: PHYS-1410) (3 lecture, 2 laboratory/tutorial hours or equivalent a week.) (Credit cannot be obtained for both GENG-2340 and ELEC-2140.) (Corequisites:MATH-2780 and MATH-2790).*This course will be discontinued Fall 2019.', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (903, N' Digital Logic Design I', N'2170', N'ELEC', N'Boolean algebra and logic gates; simplification of Boolean functions; arithmetic operations; analysis and design of combinatorial logic circuits with SSI, MSI, and LSI; sequential logic components; registers; counters and memory units; analysis and synthesis of sequential synchronous and asynchronous networks. (Co-requisites: MATH-2780 and MATH-2790) (3 lecture, 2 Laboratory/tutorial hours or equivalent a week.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (904, N' Circuit Analysis', N'2200', N'ELEC', N'Sinusoidal steady-state analysis; complex power in single and three-phase systems; magnetically coupled circuits; circuit analysis in the s-domain; frequency response; two-port networks; and computer-aided analysis and design. (3 lecture, 3.0 laboratory/tutorial hours a week.) (Prerequisite: GENG-2340.) (Credit cannot be obtained for ELEC-2200 and ELEC-2140.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (905, N' Signals and Systems', N'2240', N'ELEC', N'Discrete and Continuous-Time Signals and Systems, Discrete and Continuous-Time Linear Time-Invariant Systems, System Analysis in Time Domain, System Analysis in Frequency Domain, Convolution, Differential Equation Models, Fourier series, the Fourier Transform, the Laplace Transform and it''s Applications, Sampling of Systems. (Prerequisites: MATH-2780 and MATH-2790) (3 lecture, 1.5 laboratory hours and 1.5 tutorial hours a week.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (906, N' Physical Electronics', N'2250', N'ELEC', N'This course covers crystal structures. properties of semiconductor materials. Schrodinger wave equation. energy band theory. intrinsic and extrinsic semiconductors. charge carriers in semiconductors. thermal equilibrium carrier concentrations. carrier transport mechanisms. non-equilibrium excess carriers in semiconductors, Metal-Oxide-Semiconductor Field-Effect Transistors (MOSFET), and Bipolar Junction Transistors (BJT). (Prerequisites: MATH-2780 and MATH-2790) (3 lecture, 2 laboratory/tutorial hours or equivalent a week.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (907, N' Electronics I', N'2260', N'ELEC', N'Classification of signals; introduction to diodes; rectifier circuits, Zener diode, limiting and clamping circuits; Op amp amplifier configurations, Op amp distortion, non ideal op amp performance; active filters, Tow-Thomas Biquad; Introduction to data converters; oscillators; super-diodes; pulse generation. (Prerequisites: MATH-2780 and MATH-2790) (3 lecture, 1.5 laboratory hours and 1.5 tutorial hours a week.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (908, N' Electromagnetic Fields', N'2280', N'ELEC', N'Static electric fields; Coulomb’s law, Gauss’s law and its applications; electric potential; dielectrics; boundary conditions; capacitance; resistance; steady electric currents, current density, boundary condition for current density, equation of continuity and Kirchhoff’s law; power dissipation; static magnetic fields; Biot-Savart’s law, Ampere’s law; vector magnetic potential; magnetic dipole; magnetic circuits; boundary conditions for magnetic fields; magnetic forces and torque; induction current. (Prerequisites: MATH-2780 and MATH-2790) (3 lecture, 2 laboratory/tutorial hours or equivalent a week.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (909, N' Electromechanical Systems', N'3130', N'ELEC', N'Machinery principles; transformers; AC machinery fundamentals; synchronous generators; synchronous and induction motors; DC machinery fundamentals; DC motors; electromechanical energy conversion; three-phase concepts; special-purpose motors. (Prerequisites: MATH-2780, MATH-2790, ELEC-2250.) (3 lecture, 2 laboratory hours or equivalent a week.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (910, N' Electronics II', N'3160', N'ELEC', N'Analog amplification; small-signal modeling of analog circuits; differential-amplifier topology; BJT, MOSFET and JFET differential amplifiers; frequency response and time-dependent circuit behavior; feedback and stability; multistage and power amplifiers; active filters and oscillators; use of CAD in modern transistor circuit design. (Prerequisites: MATH-2780, MATH-2790 and ELEC-2260.) (3 lecture, 1.5 laboratory hours and 1 hour tutorial.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (911, N' Control Systems I', N'3240', N'ELEC', N'Transfer function and state-space model for linear time-invariant systems; linearization of nonlinear systems; controllability and observability; transient performance; stability; tracking performance; Proportional-Integral-Derivative (PID) control design; frequency response and root locus (Prerequisites: MATH-2780, MATH-2790, ELEC-3130.) (3 lecture, 1.5 laboratory hours and 1.5 tutorial hours a week.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (912, N' Microprocessors', N'3270', N'ELEC', N'Microprocessor systems (8 and 16 bit) and architecture; data representations, arithmetic units; memory structures; complex instruction set; accumulator, index, and memory reference instructions; addressing modes; stacks, subroutines, and other instructions; interrupts and timing; interfacing I/O devices and data converters; software development systems and assemblers; code implementation on microcontrollers. (Prerequisites: MATH-2780, MATH-2790, ELEC-2170, ELEC-3160 and ELEC-3300.) (3 lecture, 3 laboratory/tutorial hours or equivalent a week.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (913, N' Analog Communications', N'3290', N'ELEC', N'Analog communication systems; information measure; signals and noise; Fourier transform and spectra; bandwidth of signals; analog modulation and demodulation systems; AM, FM, TV transmitters and receivers, detector circuits. (Prerequisites: MATH-2780, MATH-2790 and ELEC-3160.) (3 lecture, 1.5 laboratory hours and 1.5 tutorial hours a week.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (914, N' Digital Logic Design II', N'3300', N'ELEC', N'Contemporary digital system design; programmable logic; device architectures; reconfigurable computing; design entry methods; VHDL (Hardware Description Language); Electronic Design Automation (EDA) tools; combinational and sequential logic design, implementation using programmable logic devices. (Prerequisites: MATH-2780, MATH-2790 and ELEC-2170.) (3 lecture, 3 laboratory/tutorial hours or equivalent a week.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (915, N' Capstone Design Project', N'4000', N'ELEC', N'Team based design project satisfying the "CAPSTONE DESIGN PROJECT REQUIREMENTS", available from the Department of Electrical and Computer Engineering. Gives the student significant design experience and builds on the knowledge and skills acquired in earlier course work. Provides an exposure to teamwork so as to emulate a typical professional design environment. Computers are to be used both in the execution of the design methodology and the management of the design project. (Prerequisites: completion of all Electrical Engineering courses from 1st year, 2nd year and 3rd year.) (6 laboratory hours per week; that must be completed over two consecutive winter and summer terms.) 2 semester course.', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (916, N' Directed Study I', N'4100', N'ELEC', N'The objective of this course is to provide an opportunity for the exceptional fourth-year student with a demonstrated record of scholarship to work in close accord with a faculty member on a project of mutual interest. A written report and oral presentation are required for evaluation by the Department. A Directed Study course may be taken by an eligible student in place of a fourth-year unspecified course. (Prerequisite: an 11.0 GPA or better in the third year and permission of the Department Head.) (For the purposes of assigning grades and determining averages, 3 lecture hours per week have been allocated to the course.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (917, N' Digital Communications', N'4190', N'ELEC', N'Digital communication systems; discrete Fourier transform; sampling theory; A/D converters; digital modulation; time-division multiplexing; packet transmission; random processes and spectral analysis for digital systems; error probabilities; noise; introduction to information theory. (Prerequisites: completion of all Electrical Engineering courses from 1st year, 2nd year and 3rd year.) (3 lecture, 2 laboratory/tutorial hours or equivalent a week.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (918, N' Directed Study II', N'4200', N'ELEC', N'The objective of this course is to provide an opportunity for the exceptional fourth-year student with a demonstrated record of scholarship to work in close accord with a faculty member on a project of mutual interest. A written report and oral presentation are required for evaluation. A Directed Study course may be taken by an eligible student in place of a fourth-year general option course. (Prerequisite: an 11.0 GPA or better in the third year and permission of the Department Head.) (For the purposes of assigning grades and determining averages, 3 lecture hours per week have been allocated to the course.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (919, N' Control Systems II', N'4310', N'ELEC', N'Stability and performance analysis in frequency domain; lead-lag control design in frequency domain; elementary observer and control design in state space; z- transformation and z-plane analysis; direct and indirect discrete-time control design; implementation of digital control. (Prerequisites: completion of all Electrical Engineering courses from 1st year, 2nd year and 3rd year.) (3 lecture, 1.5 laboratory hours and 1.5 tutorial hours a week.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (920, N' EM Waves and Radiating Systems', N'4320', N'ELEC', N'Maxwell equations; time varying potentials; time harmonic fields; electromagnetic wave propagation; wave polarization; power and Poynting vector; transmission lines; Smith chart; rectangular waveguides; waveguide current and mode excitation; dipole antenna; small loop antennas; antenna characteristics; antenna arrays. (Prerequisites: completion of all Electrical Engineering courses from 1st year, 2nd year and 3rd year.) (3 lecture, 2 tutorial hours a week.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (921, N' Digital Integrated Circuit Design', N'4330', N'ELEC', N'Physics and modelling of MOSFETs; fabrication and layout of CMOS integrated circuits; the CMOS inverter: analysis and design; switching properties of MOSFETs; static logic gates; transmission gate logic circuits; dynamic logic circuit concepts; CMOS dynamic logic families; CMOS differential logic families; design methodologies and CAD tools; deep-submicron implementations. (Prerequisites: completion of all Electrical Engineering courses from 1st year, 2nd year and 3rd year.) (3 lecture, 2 laboratory/tutorial hours or equivalent a week.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (922, N' Automotive Electronics', N'4340', N'ELEC', N'Proportional-Integral-Derivative (PID) controllers and limit cycle controllers; fundamentals of digital control of Spark-Ignition (SI) engine; MPC555 Motorola Power PC/dSPACE based SI engine control system; Motronic engine management system; automotive sensors and actuators; vehicle motion control including Antilock Braking System (ABS); Controller Area Network (CAN); Time-Triggered CAN (TTCAN); FlexRay. (Prerequisites: completion of all Electrical Engineering courses from 1st year, 2nd year and 3rd year.) (3 lecture, 2 laboratory/tutorial hours or equivalent a week.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (923, N' Microelectromechanical Systems', N'4350', N'ELEC', N'MicroElectroMechanical System (MEMS) technology overview and design process; microfabrication and process integration; lumped element modeling; 3-D finite element modeling; energy conserving transducers (electrostatics); linear and nonlinear system dynamics; elasticity, stress, strain, material properties; structure analysis, beams, plates; MEMS sensing and actuation; material case studies; MEMS design methodology; device modeling. (Prerequisites: completion of all Electrical Engineering courses from 1st year, 2nd year and 3rd year.) (3 lecture, 2 laboratory/tutorial hours or equivalent a week.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (924, N' Computer Communications', N'4360', N'ELEC', N'Protocols and architecture; data transmission; data encoding; interfacing; data link control; multiplexing, ISO reference model; wide-area networks; circuit switching; packet switching; ATM and frame relay; LAN technology and systems; internet protocols; inter-network operation; transport protocols; network security. (Prerequisites: completion of all Electrical Engineering courses from 1st year, 2nd year and 3rd year.) (3 lecture, 2 laboratory/tutorial hours or equivalent a week.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (925, N' Intelligent Computing', N'4370', N'ELEC', N'Computing models of the human mind. Neural computing models and learning algorithms. Fuzzy set theory and fuzzy systems. Evolutionary computing. Applications of intelligent computing. (Prerequisites:completion of all Electrical Engineering courses from 1st year, 2nd year and 3rd year in an Engineering program or fourth year standing in a Computer Science program.) (3 lecture, 2 laboratory/tutorial hours or equivalent a week.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (926, N' Coding and Information Theory', N'4380', N'ELEC', N'Abstract algebra, number theory and complexity theory; simple cryptosystems; Shannon''s theory; entropy and information theory; data encryption standard, RSA system and factoring; public-key cryptosystems; signature schemes; hash functions; key distribution and key agreement; identification schemes; authentication codes; access structures and general secret sharing; pseudo-random number generation; zero-knowledge proofs (Prerequisites: completion of all Electrical Engineering courses from 1st year, 2nd year and 3rd year) (3 lecture, 2 laboratory/tutorial hours or equivalent a week.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (927, N' Multimedia Systems', N'4390', N'ELEC', N'Multimedia signals: Audio fundamentals; the Human visual system and perception; multimedia data acquisition. Multimedia signal compression: Transforms and subband decomposition; text representation; digital text, audio, image, and video compression. Multimedia signal processing: Digital audio, image, and video processing. Multimedia systems. (Prerequisites: completion of all Electrical Engineering courses from 1st year, 2nd year and 3rd year) (3 lecture, 2 laboratory/tutorial hours or equivalent a week.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (928, N' Wireless Communications', N'4400', N'ELEC', N'Introduction to wireless communications; cellular system design fundamentals; propagation path loss; fading and multi-path propagation; modulation techniques; diversity; coding and equalization; speech coding for wireless communications; multiple access networking, wireless communications protocols; satellite communication systems. (Prerequisites: completion of all Electrical Engineering courses from 1st year, 2nd year and 3rd year.) (3 lecture, 2 laboratory/tutorial hours or equivalent a week.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (929, N' Embedded System Design', N'4430', N'ELEC', N'Embedded hardware and software systems; introduction to embedded systems; custom single-purpose processors, hardware design; general-purpose processors, software, design flow environment and tools, testing and debugging; standard single-purpose processors, peripherals, memory system design; interfacing issues, serial and parallel communication, bus standards, protocols and arbitration; exercises on real world applications; Laboratory implementation on modern Field Programmable Gate Arrays (FPGAs) and microcontrollers using associated Electronic Design Automation (EDA) tools. (Prerequisites: completion of all Electrical Engineering courses from 1st year, 2nd year and 3rd year.) (3 lecture, 3 laboratory hours a week.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (930, N' Analog Integrated Circuit Design', N'4440', N'ELEC', N'Bipolar and Metal-Oxide-Semiconductor Field-Effect Transistors (MOSFET) technology; device characterization; analog circuit modelling; current sinks, sources, and mirrors; differential pairs; current and voltage amplifiers; differential amplifiers; comparators; operational amplifiers; A/D and D/A converters; Integrated Circuit (IC) implementation with Electronic Design Automation (EDA) tools. (Prerequisites: completion of all Electrical Engineering courses from 1st year, 2nd year and 3rd year.) (3 lecture, 3 laboratory/tutorial hours or equivalent a week.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (931, N' Power Electronics', N'4450', N'ELEC', N'Power diodes; thyristors; power Metal-Oxide-Semiconductor Field-Effect Transistors (MOSFET); Insulated-Gate Bipolar Transistors (IGBT); controlled rectifiers; DC-DC converters; inverters; AC-AC converters; gate drive circuits; motor drives; r computer simulation of power electronics and motor drives. (Prerequisites: completion of all Electrical Engineering courses from 1st year, 2nd year and 3rd year.) (3 lecture, 2 laboratory/tutorial hours or equivalent a week.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (932, N' Computer Networks Security', N'4470', N'ELEC', N'Introduction to computer networks security; cryptography; public-key and secret key encryption; encryption algorithms; network security mechanisms and techniques; security protocols; authentication and network security services; traditional and emerging Information Technology (IT) security; cyber-security. (Prerequisites: completion of all Electrical Engineering courses from 1st year, 2nd year and 3rd year.) (3 lecture, 2 laboratory/tutorial hours or equivalent a week.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (933, N' Digital Computer Architecture', N'4480', N'ELEC', N'Computer Organization and architecture (32 bit); computer abstraction; reduced instruction set; high level to assembler level language translation; pipelinable instruction set architectures; speculation and branch prediction; instruction level parallelism; memory hierarchies, and virtual memory; secondary storage and I/O; multithreading, multicore, multiple CPU, and clustering; Graphics Processing Unit (GPU). (Prerequisites: completion of all Electrical Engineering courses from 1st year, 2nd year and 3rd year.) (3 lecture, 3 laboratory/tutorial hours a week.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (934, N' Sensor and Vision Systems', N'4490', N'ELEC', N'Basics of sensors and transducers; sensor characteristics and applications; fundamentals of pressure, temperature, displacement and position sensors; accelerometer physics, strain gauges, and torque sensors; machine vision; image processing, image enhancement, edge and corner detectors; image segmentation techniques; image feature extraction and matching; colour models and processing; object recognition and classification; discussion on camera parameters and calibration; stereo vision, 3D range imaging techniques. (Prerequisites: completion of all Electrical Engineering courses from 1st year, 2nd year and 3rd year.) (3 lecture, 2 laboratory/tutorial hours a week.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (935, N' Power Systems I', N'4500', N'ELEC', N'Principles of operation, modeling and analysis of electric power systems; complex power, phasors and per-unit system; three-phase circuits; power transformer and generator modeling; transmission line parameters; steady-state operation of transmission lines; network matrices and power flow analysis; introduction to alternative energy sources. (Prerequisites: completion of all Electrical Engineering courses from 1st year, 2nd year and 3rd year) (3 lecture, 2 laboratory/tutorial hours or equivalent a week.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (936, N' Fundamentals of Digital Signal Processing', N'4570', N'ELEC', N'Discrete time signals and systems models and analysis; Z-transform; discrete Fourier transform (DFT); FFT algorithms; FIR filter design; IIR filter design; stability; realization; hardware and software implementations; digital signal processing applications. (Prerequisites: completion of all Electrical Engineering courses from 1st year, 2nd year and 3rd year.) (3 lecture, 2 laboratory/tutorial hours or equivalent a week.)', 3, 17, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (937, N' Dynamics', N'2210', N'MECH', N'Review of kinetics and kinematics of particles; work-energy and impulse-momentum methods; moments of inertia of areas and masses; kinematics of rigid bodies; plane motion; forces and accelerations for rigid bodies, energy and momentum methods for rigid bodies in plane motion. (Prerequisite: GENG-1110 or PHYS-1400) (3 lecture, 2 tutorial hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (938, N' Stress Analysis', N'3211', N'MECH', N'Analysis of stresses and strains in simple mechanical structures subjected to combinations of axial, torsion and flexural loads; two-dimensional transformations of stress and strain components; yield and fracture criteria; deflection of statically determinate and indeterminate beams; buckling of columns with various end conditions; introduction to energy methods. (Prerequisite: GENG-2180.) (3 lecture, 2 laboratory/tutorial hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (939, N' Applied Thermodynamics', N'3217', N'MECH', N'Ideal gas mixtures and psychrometrics. Reacting mixtures and combustion. Power cycles, refrigeration and heat pump cycles. (Prerequisite: MECH-3212.) (3 lecture, 2 laboratory/tutorial hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (940, N' Fluid Mechanics II', N'3220', N'MECH', N'Navier-Stokes equations and some exact solutions, external flows boundary layer over a flat plate, drag forces; turbulent flows in pipes and mixing length theory, flow measurement, compressible flows and introduction to potential flows. (Prerequisite: MECH-3233.) (3 lecture, 2 laboratory/tutorial hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (941, N' Control Theory', N'3221', N'MECH', N'Control system concepts, linear modelling and analysis of response and stability of physical systems, complex variables and Laplace transforms, frequency, and transient response analysis and performance specifications. (Prerequisites: MATH-2780 and MATH-2790.) (3 lecture hours, 1 tutorial hour a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (942, N' Machine Dynamics', N'3223', N'MECH', N'Linkages of flexible connectors, cams, toothed gearing, intermittent motion mechanisms, trains of mechanisms, static and dynamic analysis of mechanical flywheels, balancing of rotating and reciprocating masses. (Prerequisite: MECH-2210.) (3 lecture, 2 tutorial hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (943, N' Engineering Measurements', N'3224', N'MECH', N'Basic concepts in instrumentation; error analysis; instrumentation and measurement systems including sensors, transducer, signal conditioning and display; microcomputer-based data acquisition and analysis. (Prerequisite: GENG-2220 or STAT-2910.) (3 lecture, 1.5 laboratory/tutorial hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (944, N' Heat Transfer', N'3228', N'MECH', N'Introduction to the three heat transfer modes: conduction, convection, and radiation. Application of heat exchange equipment.(Prerequisite: MATH-2790 and semester 6 or higher standing.) (3 lecture, 2 laboratory hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (945, N' Capstone Design', N'4200', N'MECH', N'Student design teams, operating within a "company" environment, utilize the broad range of their undergraduate experience in interdisciplinary projects selected to promote interaction between the mechanical, automotive, and materials programs. Design methodologies and team interaction simulate future professional practice. Project milestones include: a design proposal with cost analysis and scheduling, construction and commissioning of the designed appartus, and a final report and presentation having both global and detail completeness. (Prerequisite: Semester 7 or higher standing.) (2 semester course.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (946, N' Design for Failure Prevention', N'4211', N'MECH', N'Philosophy of machine design. Design factor/reliability relationships. Contemporary fatigue analysis, including low- and high-cycle, triaxial state of non-reversed stress and fatigue damage, with applications of selected mechanical elements. (Prerequisites: MECH-3211, MECH-3223, and Semester 7 or higher standing.) (3 lecture, 3 laboratory hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (947, N' Thermofluid Systems Design', N'4218', N'MECH', N'Evaluation of major thermofluid systems: HVACandR, power generation. Factors affecting design and selection of thermofluid devices: boilers, pumps and compressors, valves, piping systems, heat exchangers, evaporators, and turbines. Effect of device characteristics on process efficiency. Application of optimization techniques to thermofluid systems. (Prerequisites: MECH-3212, MECH-3233, MECH-3228; and either MECH-3217 or MECH-3220.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (948, N' Machine Design', N'4221', N'MECH', N'Gearing and gear trains: spur, helical, worm, and bevel gears. Clutches, brakes, couplings, flywheels. Chain and belt drives. Design of shafting. Student-developed software to support mechanical design. (Prerequisite: MECH-3223 and MECH-3211.) (3 lecture, 3 laboratory hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (949, N' Computer Aided Engineering - CAE', N'4259', N'MECH', N'Three-dimensional graphics; fundamentals of finite element methods for problem solving in heat transfer, solids, and trusses using finite element computer programs. (Prerequisite: MECH-3211.) (2 lecture, 3 laboratory/tutorial hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (950, N' Mechanical Vibrations', N'3215', N'MECH', N'Free, damped, and forced vibration of single and multi-degree of freedom systems with discrete masses. Exact and approximate methods of solution. Vibration isolation, vibration transducers, use of computers in vibration analysis. (Prerequisite: MECH-2210 and Semester 6 or higher standing.) (3 lecture, 2 tutorial hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (951, N' Mechatronics', N'4212', N'MECH', N'Review of electromechanical components. Practical application of microcontrollers in electromechanical systems. Use of infrared sensors, photoresistors, operational amplifiers, timers, servomotors, and analog/digital converters in mechatronics systems. A hands-on, laboratory-based course.(Prerequisite: Semester 7 or 8 standing for Mechanical Engineering students; other students require instructor approval.) (2 lecture, 3 laboratory/tutorial hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (952, N' Sustainability in Engineering', N'4228', N'MECH', N'Environmental impact assessment. Biophysical and socioeconomic impacts from engineering activities, processes, and projects. Human health and environmental risk concepts. Introduction to life cycle analysis, corporate/industrial environmental management, and environmental management systems. Students will undertake various project related and problem-based assignments. (Cross-listed with ENVE-4810.) (Prerequisite: GENG-2500 and Semester 6 or higher standing.) (3 lecture, 2 tutorial hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (953, N' Topics in Mechanical Engineering', N'4240', N'MECH', N'Selected topics of current interest in Mechanical Engineering. (Prerequisite: 4th-year Semester 7 or higher standing or permission of instructor.) (3 lecture, 1 laboratory hour a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (954, N' Directed Studies in Mechanical Engineering', N'4241', N'MECH', N'A special course of studies in Mechanical Engineering with content and direction approved by the Department Head. (Prerequisite: Semester 7 or higher standing with a 70% average or better.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (955, N' Gas Dynamics', N'4250', N'MECH', N'Basic concepts and one-dimensional flow equations of gas dynamics. Emphasis on isentropic flows in variable area ducts as well as Fanno, Rayleigh and Isothermal flows in constant area ducts. Normal shock waves, their appearance in various flow types, their application in nozzles and diffusers. Oblique shock and Prandtl Meyer expansion waves. Considerations in compressible flow measurements. (Prerequisite: MECH-3220.) (3 lecture, 1 laboratory/tutorial hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (956, N' Turbomachines', N'4251', N'MECH', N'Dimensional analysis and similitude; definitions of efficiency, two dimensional analysis of axial flow turbines and compressors, three dimensional flow, centrifugal pumps and compressors. (Prerequisite: MECH-3220.) (3 lecture, 1 laboratory/tutorial hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (957, N' Heating, Ventilation, and Air Conditioning', N'4253', N'MECH', N'Principles of environmental air quality and occupant comfort control. Psychrometric analysis of buildings as applied to common air distribution system designs. Current solar radiation estimation techniques and other energy transfer mechanisms; their application to cooling and heating load calculations. Analytical and numerical calculations. Computational tools. (Prerequisite: MECH-3217.) (3 lecture, 1 laboratory/tutorial hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (958, N' Environmental Effects and Control of Noise', N'4255', N'MECH', N'Physical properties of sound and noise, measurement of noise, noise control, hearing characteristics and environmental effects of noise. (Prerequisite: Semester 7 or higher standing.) (3 lecture, 1 tutorial/laboratory hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (959, N' Aerospace Engineering Fundamentals', N'3670', N'MECH', N'History of flight and aircraft evolution. Aircraft operating principles. Airfoil and wing aerodynamics. Aerospace propulsion systems (turbojets, turbofans, turboprops, and rockets). Lab on performance estimation and measurement for a turbojet engine. Aircraft design. Weight estimation. Aircraft systems. Aircraft materials and structures. Governance of aviation in North America. Design studies of aircraft or spacecraft and/or components thereof. (Prerequisites: MATH-2780, MATH-2790, semester 6 or higher standing; and Aerospace option students or permission of instructor.) (Co-requisites: MECH-3217, MECH-3220.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (960, N' Aerospace Materials and Manufacturing', N'3671', N'MECH', N'Properties and selection of metals, ceramics, polymers, and composite materials for aerospace applications. Structural and gas-turbine alloys. Machining, casting, forming, heat treating, and joining processes for original manufacture and repair. Manufacture and application of composites. In-service materials degradation. (Pre-requisites: MECH-3670.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (961, N' Aerospace Propulsion', N'4670', N'MECH', N'Application of gas dynamics and thermodynamics to aerospace engines. Analysis of engine cycles. Theory and design of propellers; turboprop engine analysis, Internal combustion and gas turbine engines. Component design for compressors, combustors, afterburners, exhaust nozzles. (Pre-requisites: MECH-3217, MECH-3220, MECH-3670, and semester 7 or higher standing.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (962, N' Aerodynamics and Performance', N'4671', N'MECH', N'Analysis of aircraft configurations. Viscous and compressibility effects. Manoeuvering loads and load factors; implications of manoeuverability on thrust requirements. Aircraft stability and control. (Pre-requisites: MECH-3220, MECH-3670, and Semester 7 or higher standing.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (963, N' Flight Dynamics and Control of Unmanned Aerial Vehicles', N'4672', N'MECH', N'Flight dynamics modelling for fixed-wing aircraft and rotorcraft. Low-Reynolds number considerations applicable to unmanned aerial vehicles (UAVs). Control theory and state-space control schemes. State-space controller design for UAVs. Lab(s) involving control of virtual and/or physical UAV models. (Pre-requisites: MECH-3670 and MECH-4671)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (964, N' Automotive Engineering Fundamentals', N'3430', N'MECH', N'Overview of primary automotive systems. Engine types and configurations, combustion, emission control, vehicle performance. Powertrain, suspension, frame and chassis. Materials and fabrication issues. Engine and vehicle dissection laboratory. Identification of industry issues and trends. (Prerequisite: Automotive Option students only and Semester 6 or higher standing.) (2 lecture, 3 laboratory hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (965, N' Topics in Automotive Engineering', N'4440', N'MECH', N'Selected topics of current interest in Automotive Engineering. (Prerequisite: Semester 7 or higher standing or permission of instructor.) (3 lecture, 1 laboratory hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (966, N' Directed Studies in Automotive Engineering', N'4441', N'MECH', N'A special course of studies in Automotive Engineering with content and direction approved by the Department Head. (Prerequisite: Semester 7 or higher standing with a 70% average or better.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (967, N' Vehicle Dynamics', N'4463', N'MECH', N'Classification and analysis of suspension types and geometry, powertrain layout, and ride quality. Tire modeling, stability, and numerical simulation of vehicle dynamics, including longitudinal and lateral vehicle response to driver inputs. Selected topics from industry experts. (Prerequisite: Semester 7 or higher standing.) (Co-requisite: MECH-3215 or MECH-3221.) (3 lecture, 1 tutorial hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (968, N' Internal Combustion Engines', N'4465', N'MECH', N'Mechanical design of vehicular internal combustion engines for different applications. Covers basic engine types and their operation from an energy conversion systems viewpoint, where the system needs to satisfy a number of requirements. These performance and operational requirements are derived from basic thermodynamics, operation of heat engine cycles, ignition and combustion processes, fuel system design, heat transfer, emissions formulation, available instrumentation and testing procedures. Environmental impact of vehicular designs on global pollution and government standards. Recent developments in energy-efficient and alternate fuel engines. (Prerequisites: MECH-3217, MECH-3220.) (3 lecture, 1 tutorial hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (969, N' Vehicle Thermal Management', N'4467', N'MECH', N'A study of controlled passenger compartment environment, and automotive thermal management hardware: radiator, heater core, air-conditioning components. Topics include the thermal comfort model of occupants in a vehicle, determination of heating and cooling loads, the practical application of refrigeration in automotive air-conditioning followed by design of equipment and HVAC system, description and design of engine cooling system. (Prerequisites: MECH-3217, MECH-3228, MECH-3233.) (3 lecture, 1 laboratory hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (970, N' Diesel Engine Fundamentals', N'4469', N'MECH', N'Theory and practice of modern diesel engines. Diesel combustion cycle. Engine design aspects including fuel injection, turbocharging, and intercooling. Measurement and control of engine emissions. Engine performance testing. Future and advanced technologies including exhaust aftertreatment. (Prerequisites: MECH-3217 and Semester 6 or higher standing.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (971, N' Materials and Their Properties', N'3830', N'MECH', N'The relationship of the engineering properties of materials to their atomic structure, bonding, crystal structure, imperfections and microstructure. The processing of materials to produce required structure and properties. Includes consideration of crystal structure determination, phase diagrams, diffusion, phase transformations, solidification, heat treatment and deformation. The laboratory is a term-long project designed to familiarize students with the use of materials-related equipment commonly found in industrial and research laboratories. (Prerequisite: GENG-2190 and Semester 6 or higher standing.) (3 lecture, 2 laboratory hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (972, N' Thermodynamics and Kinetics of Materials', N'3831', N'MECH', N'Thermodynamics: review of First and Second Laws, gas laws, humidity, thermochemistry, entropy, reversible and irreversible processes, equilibrium criteria, Gibbs free energy, activity and activity coefficient, solution thermodynamics, Raoult''s and Henry''s Laws, Gibbs-Duhem equation, alloy phase equilibria, free energy-composition diagrams, Ellingham diagrams. Kinetics: empirical treatment for homogeneous reaction rates, reaction order and specific rate constant, activation energy, Arrhenius'' Law, energy distribution in reacting systems, heterogeneous reactions. Selected problems in materials processing to illustrate theory. (Prerequisites: MECH-3212 and Semester 7 or higher standing.) (3 lecture, 2 laboratory hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (973, N' Ceramic Materials', N'4820', N'MECH', N'Uses of traditional and advanced ceramics. Monolithic and composite ceramics. Comparison of ceramics with metals and alloys. Processing: raw material preparation, forming techniques, theory and practice of sintering, quality control. Properties: modulus of rupture, creep, corrosion, erosion, and electrical, magnetic and optical properties. (Prerequisite: Semester 7 or higher standing.) (3 lecture hours, 1 laboratory hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (974, N' Deformation and Fracture', N'4821', N'MECH', N'Introduction to basic plasticity theory and its application to common metal forming and metal cutting processes. Fracture mechanics and its applications in brittle and ductile fracture, creep and fatigue, for purposes of design and of analysis. (Prerequisite: MECH-3211 and Semester 7 or higher standing.) (3 lecture, 2 laboratory hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (975, N' Modern Steels', N'4832', N'MECH', N'Traditional and advanced high strength steels. Automotive sheet steels. Stainless and tool steels. Cast irons. Steel industry in Canada. Mechanical and microstructural characterization laboratories. (Prerequisite: Semester 7 or higher standing.) (3 lecture hours, 1 laboratory hour a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (976, N' Polymers', N'4834', N'MECH', N'The structure, properties, and processing of polymers (plastics) with emphasis on polymer forming processes, including extrusion, injection molding, blowmolding, and thermoforming, including tours of local industry. Fabrication and properties of composites with a polymer base. (Prerequisite: Semester 7 or higher standing.) (3 lecture hours, 1 laboratory hour a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (977, N' Topics in Materials Engineering', N'4840', N'MECH', N'Selected topics of current interest in Materials Engineering. (Prerequisites: Semester 7 or higher standing or permission of instructor.) (3 lecture, 1 laboratory hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (978, N' Directed Studies in Materials Engineering', N'4841', N'MECH', N'A special course of studies in Materials Engineering with content and direction approved by the Department Head. (Prerequisites: Semester 7 or higher standing with a 70% average or better.) (3 lecture hours, 1 laboratory hour a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (979, N' Welding Engineering', N'4850', N'MECH', N'Design and qualification of arc welding procedures to met the requirements of the ASME Boiler and Pressure Vessel Code. Arc welding processes, weld discontinuities, mechanical and non-destructive testing. Welding metallurgy, base and filler metal classification. Control of hydrogen-assisted cracking, preheat and postweld heat treatment. Fabrication issues. Canadian and international welding codes. (Prerequisite: Semester 7 or higher standing.) (3 lecture, 1 laboratory hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (980, N' Engineering Management and Globalization', N'2010', N'INDE', N'As globalization leads to an internationally integrated production and consumption of goods, cultural products, and services, local and national identities are challenged. Globalization creates both challenges and opportunities for companies providing goods or services. This course discusses the impact of globalization on the industrial and systems engineering discipline, in a multidisciplinary and multi national context. For engineers to competently operate in a globalized environment, they must understand the context, methodologies content and outcomes. Critical thinking, systems thinking, integration of technical and, professional and business acumen is necessary. Stability requires knowledge about understanding the complexity involved and learning to manage it. The course will also deal with: impact on industrial, production, and national systems. It should help prepare students and giving them skills for solving complex systems, and life-long learning and continuous improvement.(Prerequisites: GENG-1190 and GENG-1330) (3 lecture, 2 laboratory hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (981, N' Health, Safety and Human Factors', N'3020', N'INDE', N'Fundamentals of manufacturing safety and health are studied to provide manufacturing engineers with the knowledge to effectively incorporate design solutions for health and safety considerations in the workplace. Human capabilities and limitations in the industrial workplace are also assessed and taken into account when implementing design solutions. Topics will include: machine guards, confined space protocol, accident losses, prevention, liabilities and the Workplace Safety and Insurance Board by-laws, the Ontario Occupational Safety and Health Act, and related standards and codes. Also addressed are ergonomic issues such as the design of the workplace and environment, design of display and control systems and human factors in expanding technology. (Pre-requisite: GENG-2190) (3 lecture, 2 laboratory hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (982, N' Computer-Aided Design and Computer Aided Manufacturing', N'3110', N'INDE', N'This courses focuses on CAD/CAM from theory to practice. Basic and generic design principles and tools are introduced and the course material is complemented with significant hands on practice and engineering applications. Students will learn modelling strategies, and advanced computer aided engineering design, analysis, manufacturing and measurement tools. Topics include: Solid modelling, GD & T, tolerance stack ups, assembly modelling and mechanism analysis, process planning, CNC code generation, tool path optimization and principles of measurement.(3 lecture, 2 laboratory hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (983, N' Operations Research I', N'3120', N'INDE', N'Deterministic O. R. models. Linear programming-graphical and simplex methods, duality theory. Transportation, assignment and network models. Sensitivity analysis. Integer programming, branch-and-bound and cutting plane methods, mixed IP algorithms, 0/1 programming. Use of LP and IP computer software programs. Dynamic programming-principle of optimality, stagecoach problems, recursive relationship. (Prerequisite: MATH-1270.) (3 lecture, 2 laboratory hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (984, N' Product and Process Design', N'3150', N'INDE', N'Engineering design and work measurement principals are studied and applied to quantify and reduce the base engineered assembly content of automotive product designs. Non traditional methods for designing and building products for profit are studied with a goal of minimizing total assembly costs, manual labour and associated ergonomic injuries. Recent advances in manufacturing driven product designs in the automotive industry are presented to educate students on the contributions of product designs to the minimization of assembly costs, assembly labour content and the risk of injuries. (3 lecture and 2 lab hrs per week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (985, N' Systems Analysis and Design', N'3170', N'INDE', N'Fundamental concepts, philosophies, and trends that provide the context of systems analysis and design methods. Information systems in terms of common building blocks: Data, Processes, and Interfaces. Basic concept of systems and systems engineering; system representation; system life cycle; system design process; and system design methods. Formulation of decision problems in engineering and management. Decision criteria. Strategies. Utility theory and decision functions. Information requirements of decision-making systems. Methods in systems analysis and design are applied to a wide variety of problem domains. (3 lecture hours and 2 laboratory/tutorial hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (986, N' Manufacturing Process Design', N'3210', N'INDE', N'This curriculum provides students with the basic science and engineering science background required to lead the design and manufacturing of products for profit in a globally competitive marketplace. Successful students will understand manufacturing in the broadest sense and emphasis is placed on the basic science of converting select raw materials into quality products anywhere on earth. Successful graduates will know and be able to compare the difference between the mechanical behaviors of materials, select materials for manufacturing based on desirable physical properties and lead the design of the most important manufacturing processes used in industry. The manufacturing processes studied include the engineering science of: metal casting, rolling of metals, forging, extrusions and drawing of metals, sheet metal forming, and the forming and shaping of plastics parts. This course includes the laboratory analysis of the materials and the study of manufacturing processes used to produce automotive components. (3 lecture hours and 2 laboratory/tutorial hours a week.) (Prerequisite: GENG-2190, in addition to the program prerequisites.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (987, N' Product Quality and Reliability', N'3270', N'INDE', N'Impact of quality on manufacturing processes and product design. Methods and theories of statistical process control. Control charts for attributes and for variables. Process capability analysis and six-sigma method. Acceptance sampling and sampling standards. Reliability engineering and various failure models. Failure modes and effects analysis (FMEA). Taguchi method. Product design and quality function deployment (QFD). ISO 9000/ QS 9000 standards. Total Quality Management (TQM) method. (Prerequisite: GENG-2220.) (3 lecture, 2 tutorial hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (988, N' Supply Chain Engineering', N'3910', N'INDE', N'This course explores the basic concepts of managing the flow of materials in a typical enterprise supply chain. This includes the design and operation of manufacturing and warehousing facilities. Students will examine a complete overview of material and information flow, from internal and external suppliers, to and from the enterprise. Topics covered include: basic elements of the supply chain; planning and managing inventories in supply chains; just-in-time; enterprise resource planning; demand and aggregate planning; the analysis of logistics capabilities and transportation issues; and interrelationships among customer service. The impact of e-commerce on supply chain management is also included. The students have the opportunity to explore and use SAP and other software packages. (Pre-requisite: INDE-3120) (3 lecture, 2 laboratory hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (989, N' Capstone Industrial Design Projects', N'4000', N'INDE', N'Students working in teams, and supervised by Faculty, will undertake an industrial design project, eight hours per week. This design course integrates mathematics, basic sciences, engineering sciences and complementary studies in developing elements, systems and processes to meet specific needs of the industrial sponsor. It is a creative, iterative and often open-ended process subject to constraints which may be governed by corporate standards or applicable legislation to varying degrees depending upon the project. These constraints may relate to economic, health, safety, environmental, social or other pertinent interdisciplinary factors. (The Faculty advisor and industrial preceptor will advise the students and evaluate the progress and results of the design project. Students participate in faculty instruction and group meetings. An Oral exam and written engineering design reports (interim and final) are required.) (1 lecture hour and 8 industry laboratory hours a week. ) (2 semester course)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (990, N' Operations Research II', N'4120', N'INDE', N'Probabilistic O.R. models. Markov chains and their properties; continuous-time Markov chains. Queuing theory; the role of Exponential and Poisson distributions. Applications of queuing theory in production systems. Markovian decision processes. Reliability. Renewal Theory. Use of computer software programs to solve optimization problems in queues and Markov Processes. (Prerequisite: GENG-2220.) (3 lecture, 2 laboratory hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (991, N' Production Analysis', N'4130', N'INDE', N'Analysis and control of production systems. Demand forecasting. Deterministic and stochastic inventory systems. Aggregate planning and master scheduling. Material requirement planning. Operations sequencing and balancing. Job shop scheduling and control systems. Introduction to group technology and flexible manufacturing systems. (Prerequisite: INDE-3120.) (3 lecture and 2 laboratory hours per week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (992, N' Simulation of Industrial Systems', N'4220', N'INDE', N'Introduction to Simulation-Random number and variate generation. Applications to queues, inventories and related models. Special purpose simulation languages-SIMAN/ARENA. Input data analysis and model validation. Simulation output analysis, design of experiments. Use of computer software. (Prerequisite: INDE-3270.) (3 lecture, 2 laboratory hours a week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (993, N' Facilities Design and Logistics', N'4280', N'INDE', N'Approaches to establishing location and layout of space, equipment and services for industrial facilities. Criteria and data for generating & comparing alternatives. Computerized layout planning models, storage systems, AS/RS, Material handling, scope, definitions, and principles, unit load design, types of equipment, flow of material and line balancing. Environmental, human and cost considerations. Electrical and lighting systems and atmospheric systems. (Prerequisite: INDE-3150.) (3 lecture and 2 laboratory hours per week.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (994, N' Directed Study', N'4300', N'INDE', N'The student will undertake a literature survey and/or a laboratory project in consultation with the Department Head. A written report is mandatory and participation in the Industrial Engineering Program seminars may be part of the requirement. (Prerequisite: fourth-year standing with at least an 8.0 average.)', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (995, N' Flexible Manufacturing Systems', N'4310', N'INDE', N'Production Systems, Flexible Automation, Computer-Integrated Manufacturing, Group Technology And Cellular Manufacturing, Flexible Manufacturing Systems, Assembly Systems, Materials and tools handling, Robotics In Manufacturing, Principles Of Design For Manufacture, Process Planning And Concurrent Engineering, New Trends-Lean, Agile And Re-Configurable Manufacturing Systems. (Corequisite or prerequisite: INDE-4130 or equivalent.) (3 lecture, 2 tutorial/laboratory hours a week.', 3, 18, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (996, N' Health and Wellness', N'1000', N'KINE', N'This introductory course will examine health and wellness from both a local and global perspective. Personal health and wellness will be evaluated from a physical, mental, spiritual and social perspective. Behavioural change and motivational techniques will be explored to aid in achieving a healthier lifestyle. This course will introduce various topics that impact the health and wellness of an individual including physical activity, nutrition, obesity, stress, disease prevention, high risk behaviour, health care systems, alternative medicine, violence in society and the environment. Current health and wellness issues within the community and media will also be presented. (Open to Kinesiology majors only.)', 3, 19, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (997, N'Movement Science Perspectives', N'1030', N'KINE', N'This course will present an overview of the biophysical sub-disciplines that comprise Kinesiology. Treatment of each sub-discipline will highlight the history of the area, the current state of research and the practical application of principles in Kinesiology to sport, the workplace and activities of daily living. (Open only to non-Kinesiology majors.)', 3, 19, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (998, N' Introduction to Kinesiology: Sport Management and Sociocultural Perspectives', N'1040', N'KINE', N'This course will present an overview of the sport management and sociocultural sub-disciplines that comprise Kinesiology. Treatment of each sub-discipline will highlight the history of the area, the current state of research and the practical application of principles in Kinesiology to sport, the workplace and activities of daily living. (Open only to non-Kinesiology majors.)', 3, 19, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (999, N' Introductory Exercise Physiology', N'1050', N'KINE', N'The focus of this 1st year course in exercise physiology is to introduce students to the various physiological systems of the human body and how they respond to acute and chronic bouts of physical activity. The course will highlight how the human body responds to accommodate the exercise stimulus and the benefits of exercise. Designed to stimulate interest in the Movement Science area of our program. (Open to Kinesiology majors only.)', 3, 19, NULL, NULL)
INSERT INTO [dbo].[Course] ([CourseId], [CourseName], [CourseNumber], [DepartmentCourseCode], [CourseDescription], [Credits], [DepartmentId], [Cart_CartId], [PreRequisite_PreRequisiteId]) VALUES (1000, N'Principles of Mental Skills Training', N'1110', N'KINE', N'This course surveys the psychological principles underlying cognitive techniques that can be used to improve performance and enjoyment in physical activity environments such as sport and exercise. Among the topics to be explored will be goal setting, anxiety control, and attentional focus.', 3, 19, NULL, NULL)
